            super._transfer(sender, _addrFees, amtFeeOps );


            uint256 amtT = amount.sub(amtDestroy).sub(amtFeeOpb).sub(amtFeeOps);
            super._transfer(sender,recipient, amtT );

        } else {      // transfer
            super._transfer(sender, recipient, amount);

        } 
    }

