contract ERC1155Impl is ERC1155Pausable, ERC1155Supply, ERC1155Enumerable, ERC1155Burnable, ERC1155URIStorage, Ownable {
    uint256 public constant maxTokenId0Amount = 9999;
    uint256 public constant minStakeAmount = 5*10**4*10**18;
    uint256 public maxCanGet = 3;
    uint256 public caseA = 0;
    ILianGoPay public constant c_lgp = ILianGoPay(0x97719d4d8e4C5fc2e12B19dC4f5895D446b3E6F6);
    IERC20 public constant c_erc20dividend = IERC20(0xb0fd95723534aF7a08021813bc9f792a083947B2);
    mapping (address => bool) public isDividendAddress;
    uint256 public totalDividend;

    struct User {
        uint256 tokenIdNum;
        uint256 dividends;
    }
    mapping(address => User) public users;

    struct DividendInfo {
        uint128 timestamp;
        uint128 amount;
    }
    mapping(address => DividendInfo[]) public dividendInfos;

    constructor(string memory _uri) ERC1155(_uri) {
    }

    function symbol() external pure returns (string memory) {
        return "ShoppingFi NFT";
    }

    function name() external pure returns (string memory) {
        return "ShoppingFi NFT";
    }

    function setURI(string memory _newURI) external onlyOwner {
        _setURI(_newURI);
    }

    function setTokenURI(uint256 tokenId, string memory tokenURI) external onlyOwner {
        _setURI(tokenId, tokenURI);
    }

    function setBaseURI(string memory baseURI) external onlyOwner {
        _setBaseURI(baseURI);
    }

    function totalSupply() public view returns (uint256) {
        return totalSupply(0);
    }

    function balanceOf(address account) public view returns (uint256) {
        return balanceOf(account, 0);
    }

    function mint0() external {
        uint256 withdrawableAmount = withdrawable(msg.sender);
        if (withdrawableAmount == 0) {
            return;
        }
        users[msg.sender].tokenIdNum += withdrawableAmount;
        _mint(msg.sender, 0, withdrawableAmount, "");
    }

    function withdrawable(address addr) public view returns(uint256) {
        ( , , uint128 totalStakeAmount, uint128 adaptAmount, , , , ) = c_lgp.users(addr);
        uint256 youcanget = totalStakeAmount;
        if (caseA == 1) {
            youcanget += adaptAmount;
        }
        if (youcanget < minStakeAmount) {
            return 0;
        }
        youcanget = youcanget/minStakeAmount;
        if(youcanget <= users[addr].tokenIdNum) {
            return 0;
        }
        if(youcanget > maxCanGet) {
            youcanget = maxCanGet;
        }

        youcanget -= users[addr].tokenIdNum;
        uint256 t = totalSupply(0);
        if (youcanget + t <= maxTokenId0Amount) {
            return youcanget;
        }else {
            return maxTokenId0Amount - t;
        }
    }

    function setCase(uint256 newC) external onlyOwner {
        caseA = newC;
    }

    function setMaxGet(uint256 m) external onlyOwner {
        maxCanGet = m;
    }

    function setPause() external onlyOwner {
        _pause();
    }

    function setUnpause() external onlyOwner {
        _unpause();
    }

    function setDividendAddress(address addr, bool b) external onlyOwner {
        isDividendAddress[addr] = b;
    }

    function getERC20(IERC20 c_erc2, uint256 amount) external onlyOwner {
        c_erc2.transfer(msg.sender, amount);
    }

    function _beforeTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal virtual override(ERC1155, ERC1155Pausable, ERC1155Supply) {
        super._beforeTokenTransfer(operator, from, to, ids, amounts, data);
    }

    function _afterTokenTransfer(
        address operator,
        address from,
        address to,
        uint256[] memory ids,
        uint256[] memory amounts,
        bytes memory data
    ) internal virtual override(ERC1155, ERC1155Enumerable) {
        super._afterTokenTransfer(operator, from, to, ids, amounts, data);
    }

    function uri(uint256 tokenId) public view override(ERC1155, ERC1155URIStorage) returns (string memory) {
        return super.uri(tokenId);
    }

    function distributeByTokenId(uint256 amount, bool r1, bool r2) external {
        uint256 id = 0;
        require(isDividendAddress[msg.sender], "not addr");
        uint256 len = holderLength(id);
        if (len == 0) return;
        uint256 idTotalSupply = totalSupply(id);
        if (idTotalSupply == 0) return;

        IERC20 c = c_erc20dividend;
        c.transferFrom(msg.sender, address(this), amount);
        totalDividend += amount;
        amount = amount/idTotalSupply; 

        for(uint256 i; i < len; ++i) {
            address addr = at(id, i);
            uint256 b = balanceOf(addr, id)*amount;
            c.transfer(addr, b);

            if(r1) {
                users[addr].dividends += b;
            }
            
            if(r2) {
                dividendInfos[addr].push(DividendInfo(uint128(block.timestamp), uint128(b)));
            }
        }
    }

    function distributeByTokenIdStartEnd(uint256 start, uint256 end, uint256 oneAmount) external {
        uint256 id = 0;
        uint256 len = holderLength(id);
        if (len == 0) return;
        require(start <= end && end < len, "invalid param");
        require(isDividendAddress[msg.sender], "not addr");

        uint256 sum;
        IERC20 c = c_erc20dividend;
        for(uint256 i = start; i <= end; ++i) {
            address addr = at(id, i);
            uint256 b = balanceOf(addr, id)*oneAmount;
            c.transfer(addr, b);
            users[addr].dividends += b;
            sum += b;
        }
        totalDividend += sum;
    }

    function contractInfo() external view returns(uint256, uint256, uint256) {
        return (totalSupply(0), holderLength(0), totalDividend);
    }

    function userInfo(address addr) external view returns(uint256, uint256, uint256, uint256) {
        return (balanceOf(addr, 0), withdrawable(addr), users[addr].tokenIdNum, users[addr].dividends);
    }

    function getRecordsByPage(address addr, uint256 pageNum, uint256 pageSize) external view returns (DividendInfo[] memory o, uint256 total) {
        total = dividendInfos[addr].length;
        uint256 from = pageNum*pageSize;
        if (total <= from) {
            return (new DividendInfo[](0), total);
        }

        uint256 minNum = Math.min(total - from, pageSize);
        from = total - from - 1;
        o = new DividendInfo[](minNum);
        
        for (uint256 i; i < minNum; ++i) {
            o[i] = dividendInfos[addr][from];
            if(from > 0) {
                from--;
            }
        }
    }
}"},"ERC1155Pausable.sol":{"content":"// SPDX-License-Identifier: MIT
