/// @title Dividend-Paying Token Interface
/// @author Roger Wu (https://github.com/roger-wu)
/// @dev An interface for a dividend-paying token contract.
interface DividendPayingTokenInterface {
  /// @notice View the amount of dividend in wei that an address can withdraw.
  /// @param _owner The address of a token holder.
  /// @return The amount of dividend in wei that `_owner` can withdraw.
  function dividendOf(address _owner) external view returns(uint256);


  /// @notice Withdraws the ether distributed to the sender.
  /// @dev SHOULD transfer `dividendOf(msg.sender)` wei to `msg.sender`, and `dividendOf(msg.sender)` SHOULD be 0 after the transfer.
  ///  MUST emit a `DividendWithdrawn` event if the amount of ether transferred is greater than 0.
  function withdrawDividend() external;

  /// @dev This event MUST emit when ether is distributed to token holders.
  /// @param from The address which sends ether to this contract.
  /// @param weiAmount The amount of distributed ether in wei.
  event DividendsDistributed(
    address indexed from,
    uint256 weiAmount
  );

  /// @dev This event MUST emit when an address withdraws their dividend.
  /// @param to The address which withdraws ether from this contract.
  /// @param weiAmount The amount of withdrawn ether in wei.
  event DividendWithdrawn(
    address indexed to,
    uint256 weiAmount
  );
}

/// @title Dividend-Paying Token Optional Interface
/// @author Roger Wu (https://github.com/roger-wu)
/// @dev OPTIONAL functions for a dividend-paying token contract.
interface DividendPayingTokenOptionalInterface {
  /// @notice View the amount of dividend in wei that an address can withdraw.
  /// @param _owner The address of a token holder.
  /// @return The amount of dividend in wei that `_owner` can withdraw.
  function withdrawableDividendOf(address _owner) external view returns(uint256);

  /// @notice View the amount of dividend in wei that an address has withdrawn.
  /// @param _owner The address of a token holder.
  /// @return The amount of dividend in wei that `_owner` has withdrawn.
  function withdrawnDividendOf(address _owner) external view returns(uint256);

  /// @notice View the amount of dividend in wei that an address has earned in total.
  /// @dev accumulativeDividendOf(_owner) = withdrawableDividendOf(_owner) + withdrawnDividendOf(_owner)
  /// @param _owner The address of a token holder.
  /// @return The amount of dividend in wei that `_owner` has earned in total.
  function accumulativeDividendOf(address _owner) external view returns(uint256);
}

/// @title Dividend-Paying Token
/// @author Roger Wu (https://github.com/roger-wu)
/// @dev A mintable ERC20 token that allows anyone to pay and distribute ether
///  to token holders as dividends and allows token holders to withdraw their dividends.
///  Reference: the source code of PoWH3D: https://etherscan.io/address/0xB3775fB83F7D12A36E0475aBdD1FCA35c091efBe#code
contract DividendPayingToken is ERC20, Ownable, DividendPayingTokenInterface, DividendPayingTokenOptionalInterface {
  using SafeMath for uint256;
  using SafeMathUint for uint256;
  using SafeMathInt for int256;

  address public immutable rewardToken = address(0xe64972C311840cFaf2267DCfD365571F9D9544d9);


  // With `magnitude`, we can properly distribute dividends even if the amount of received ether is small.
  // For more discussion about choosing the value of `magnitude`,
  //  see https://github.com/ethereum/EIPs/issues/1726#issuecomment-472352728
  uint256 constant internal magnitude = 2**128;

  uint256 internal magnifiedDividendPerShare;

  // About dividendCorrection:
  // If the token balance of a `_user` is never changed, the dividend of `_user` can be computed with:
  //   `dividendOf(_user) = dividendPerShare * balanceOf(_user)`.
  // When `balanceOf(_user)` is changed (via minting/burning/transferring tokens),
  //   `dividendOf(_user)` should not be changed,
  //   but the computed value of `dividendPerShare * balanceOf(_user)` is changed.
  // To keep the `dividendOf(_user)` unchanged, we add a correction term:
  //   `dividendOf(_user) = dividendPerShare * balanceOf(_user) + dividendCorrectionOf(_user)`,
  //   where `dividendCorrectionOf(_user)` is updated whenever `balanceOf(_user)` is changed:
  //   `dividendCorrectionOf(_user) = dividendPerShare * (old balanceOf(_user)) - (new balanceOf(_user))`.
  // So now `dividendOf(_user)` returns the same value before and after `balanceOf(_user)` is changed.
  mapping(address => int256) internal magnifiedDividendCorrections;
  mapping(address => uint256) internal withdrawnDividends;

  uint256 public totalDividendsDistributed;

  constructor(string memory _name, string memory _symbol) public ERC20(_name, _symbol) {

  }


  function distributeDividends(uint256 amount) public onlyOwner{
    require(totalSupply() > 0);

    if (amount > 0) {
      magnifiedDividendPerShare = magnifiedDividendPerShare.add(
        (amount).mul(magnitude) / totalSupply()
      );
      emit DividendsDistributed(msg.sender, amount);

      totalDividendsDistributed = totalDividendsDistributed.add(amount);
    }
  }

  /// @notice Withdraws the ether distributed to the sender.
  /// @dev It emits a `DividendWithdrawn` event if the amount of withdrawn ether is greater than 0.
  function withdrawDividend() public virtual override {
    _withdrawDividendOfUser(msg.sender);
  }

  /// @notice Withdraws the ether distributed to the sender.
  /// @dev It emits a `DividendWithdrawn` event if the amount of withdrawn ether is greater than 0.
 function _withdrawDividendOfUser(address payable user) internal returns (uint256) {
    uint256 _withdrawableDividend = withdrawableDividendOf(user);
    if (_withdrawableDividend > 0) {
      withdrawnDividends[user] = withdrawnDividends[user].add(_withdrawableDividend);
      emit DividendWithdrawn(user, _withdrawableDividend);
      bool success = IERC20(rewardToken).transfer(user, _withdrawableDividend);

      if(!success) {
        withdrawnDividends[user] = withdrawnDividends[user].sub(_withdrawableDividend);
        return 0;
      }

      return _withdrawableDividend;
    }

    return 0;
  }


  /// @notice View the amount of dividend in wei that an address can withdraw.
  /// @param _owner The address of a token holder.
  /// @return The amount of dividend in wei that `_owner` can withdraw.
  function dividendOf(address _owner) public view override returns(uint256) {
    return withdrawableDividendOf(_owner);
  }

  /// @notice View the amount of dividend in wei that an address can withdraw.
  /// @param _owner The address of a token holder.
  /// @return The amount of dividend in wei that `_owner` can withdraw.
  function withdrawableDividendOf(address _owner) public view override returns(uint256) {
    return accumulativeDividendOf(_owner).sub(withdrawnDividends[_owner]);
  }

  /// @notice View the amount of dividend in wei that an address has withdrawn.
  /// @param _owner The address of a token holder.
  /// @return The amount of dividend in wei that `_owner` has withdrawn.
  function withdrawnDividendOf(address _owner) public view override returns(uint256) {
    return withdrawnDividends[_owner];
  }


  /// @notice View the amount of dividend in wei that an address has earned in total.
  /// @dev accumulativeDividendOf(_owner) = withdrawableDividendOf(_owner) + withdrawnDividendOf(_owner)
  /// = (magnifiedDividendPerShare * balanceOf(_owner) + magnifiedDividendCorrections[_owner]) / magnitude
  /// @param _owner The address of a token holder.
  /// @return The amount of dividend in wei that `_owner` has earned in total.
  function accumulativeDividendOf(address _owner) public view override returns(uint256) {
    return magnifiedDividendPerShare.mul(balanceOf(_owner)).toInt256Safe()
      .add(magnifiedDividendCorrections[_owner]).toUint256Safe() / magnitude;
  }

  function _withdrawTokens(address token, address recipient) internal {
        require(token != address(0), 'Invalid Token!');
        require(recipient != address(0), 'Invalid Recipient!');

        uint256 balance = IERC20(token).balanceOf(address(this));
        if (balance > 0) {
            require(IERC20(token).transfer(recipient, balance), "Transfer Failed");
        }
    }

  /// @dev Internal function that transfer tokens from one address to another.
  /// Update magnifiedDividendCorrections to keep dividends unchanged.
  /// @param from The address to transfer from.
  /// @param to The address to transfer to.
  /// @param value The amount to be transferred.
  function _transfer(address from, address to, uint256 value) internal virtual override {
    require(false);

    int256 _magCorrection = magnifiedDividendPerShare.mul(value).toInt256Safe();
    magnifiedDividendCorrections[from] = magnifiedDividendCorrections[from].add(_magCorrection);
    magnifiedDividendCorrections[to] = magnifiedDividendCorrections[to].sub(_magCorrection);
  }

  /// @dev Internal function that mints tokens to an account.
  /// Update magnifiedDividendCorrections to keep dividends unchanged.
  /// @param account The account that will receive the created tokens.
  /// @param value The amount that will be created.
  function _mint(address account, uint256 value) internal override {
    super._mint(account, value);

    magnifiedDividendCorrections[account] = magnifiedDividendCorrections[account]
      .sub( (magnifiedDividendPerShare.mul(value)).toInt256Safe() );
  }

  /// @dev Internal function that burns an amount of the token of a given account.
  /// Update magnifiedDividendCorrections to keep dividends unchanged.
  /// @param account The account whose tokens will be burnt.
  /// @param value The amount that will be burnt.
  function _burn(address account, uint256 value) internal override {
    super._burn(account, value);

    magnifiedDividendCorrections[account] = magnifiedDividendCorrections[account]
      .add( (magnifiedDividendPerShare.mul(value)).toInt256Safe() );
  }

  function _setBalance(address account, uint256 newBalance) internal {
    uint256 currentBalance = balanceOf(account);

    if(newBalance > currentBalance) {
      uint256 mintAmount = newBalance.sub(currentBalance);
      _mint(account, mintAmount);
    } else if(newBalance < currentBalance) {
      uint256 burnAmount = currentBalance.sub(newBalance);
      _burn(account, burnAmount);
    }
  }
}

contract PharaohToken is ERC20, Ownable {
    using SafeMath for uint256;

    IUniswapV2Router02 public uniswapV2Router;
    address public  uniswapV2Pair;

    bool public isRewardOn = true;

    bool private swapping;
    bool public swapAndLiquifyEnabled = true;

    RewardDividendTracker public dividendTracker;

    address public deadWallet = 0x000000000000000000000000000000000000dEaD;

    address public immutable rewardToken = address(0xe64972C311840cFaf2267DCfD365571F9D9544d9);

    address payable public _marketingWalletAddress = payable(0x8C40594DD5955073509606Ba2c057f3034339C21);
    address payable public _giveawayWalletAddress = payable(0x99E88Dccc9eBeFD9735E0Fc2A40FAecEA50adf9A);
    address payable public _rewardWalletAddress = payable(0x4887ca75E534e3afB611EeB364a2e20B5B7C43EE);

    uint256 public swapTokensAtAmount = 250_000 * (10**18);
    uint256 public _maxTxAmount = 1_000_000 * (10**18);

    uint256 public marketingFeeOnBuy = 5;
    uint256 public rewardsFeeOnBuy = 4;
    uint256 public giveawayFeeOnBuy = 3;
    uint256 public liquidityFeeOnBuy = 1;
    uint256 public burnFeeOnBuy = 1;
    uint256 public totalFeesOnBuy = marketingFeeOnBuy.add(rewardsFeeOnBuy).add(giveawayFeeOnBuy).add(liquidityFeeOnBuy).add(burnFeeOnBuy);

    uint256 public marketingFeeOnSell = 5;
    uint256 public rewardsFeeOnSell = 5;
    uint256 public giveawayFeeOnSell = 3;
    uint256 public liquidityFeeOnSell = 1;
    uint256 public burnFeeOnSell = 1;
    uint256 public buybackFeeOnSell = 1;
    uint256 public totalFeesOnSell = marketingFeeOnSell.add(rewardsFeeOnSell).add(giveawayFeeOnSell).add(liquidityFeeOnSell).add(burnFeeOnSell).add(buybackFeeOnSell);

    // use by default 300,000 gas to process auto-claiming dividends
    uint256 public gasForProcessing = 300000;

     // exlcude from fees and max transaction amount
    mapping (address => bool) private _isExcludedFromFees;

    mapping(address => bool) public _isBlacklisted;

    bool public startTrade = true;
    uint256 public launchedAt;

    address public manager;
    modifier onlyManager() {
		require(msg.sender == manager, 'Permission Denied');
		_;
	}

    ////////////    --> EVENTS <--    ///////////////    
    event SwapAndLiquifyEnabledUpdated(bool enabled);
    event SendDividends(uint256 ethAmount, uint256 amount);
    event ExcludeFromFees(address indexed account, bool isExcluded);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);
    event GasForProcessingUpdated(uint256 indexed newValue, uint256 indexed oldValue);
    event UpdateDividendTracker(address indexed newAddress, address indexed oldAddress);
    event UpdateUniswapV2Router(address indexed newAddress, address indexed oldAddress);
    event SwapAndLiquify(uint256 tokensSwapped, uint256 ethReceived, uint256 tokensIntoLiqudity);
    event LiquidityWalletUpdated(address indexed newLiquidityWallet, address indexed oldLiquidityWallet);
    event ProcessedDividendTracker(uint256 iterations, uint256 claims, uint256 lastProcessedIndex, bool indexed automatic, uint256 gas, address indexed processor);

    constructor() public ERC20("Pharaoh Token", "PHO") {
    	dividendTracker = new RewardDividendTracker();
        manager = owner();

    	IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        // Create a uniswap pair for this new token
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(address(this), _uniswapV2Router.WETH());

        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;

        // exclude from receiving dividends
        dividendTracker.excludeFromDividends(address(dividendTracker));
        dividendTracker.excludeFromDividends(address(this));
        dividendTracker.excludeFromDividends(owner());
        dividendTracker.excludeFromDividends(deadWallet);
        dividendTracker.excludeFromDividends(uniswapV2Pair);
        dividendTracker.excludeFromDividends(address(_uniswapV2Router));
        
        // exclude from paying fees or having max transaction amount
        _isExcludedFromFees[owner()]                 = true;
        _isExcludedFromFees[address(this)]           = true;
        _isExcludedFromFees[_rewardWalletAddress]    = true;
        _isExcludedFromFees[_giveawayWalletAddress]  = true;
        _isExcludedFromFees[_marketingWalletAddress] = true;

        _mint(owner(), 1_000_000_000 * (10**18));
        _approve(owner(), address(uniswapV2Router), 1_000_000_000 * (10**18));
    }

    receive() external payable {}

    // called by the masterchef;
	function mint(address to, uint256 amount) public onlyOwner {
		_mint(to, amount);
	}

    function updateDividendTracker(address newAddress) public onlyManager {
        require(newAddress != address(dividendTracker), "Error: The dividend tracker already has that address");
        RewardDividendTracker newDividendTracker = RewardDividendTracker(payable(newAddress));
        require(newDividendTracker.owner() == address(this), "Error: The new dividend tracker must be owned by the token contract");
        newDividendTracker.excludeFromDividends(address(newDividendTracker));
        newDividendTracker.excludeFromDividends(address(this));
        newDividendTracker.excludeFromDividends(owner());
        newDividendTracker.excludeFromDividends(address(uniswapV2Router));
        emit UpdateDividendTracker(newAddress, address(dividendTracker));
        dividendTracker = newDividendTracker;
    }

    function updateUniswapV2Router(address newRouter) public onlyManager {
        require(newRouter != address(uniswapV2Router), "Error: The router already has that address");
        IUniswapV2Router02 _newRouter = IUniswapV2Router02(newRouter);
        address _pair = IUniswapV2Factory(_newRouter.factory()).getPair(address(this), _newRouter.WETH());
        if (_pair == address(0)) {
            uniswapV2Pair = IUniswapV2Factory(_newRouter.factory()).createPair(address(this), _newRouter.WETH());
        }
        else {
            uniswapV2Pair = _pair;
        }
        dividendTracker.excludeFromDividends(uniswapV2Pair);
        emit UpdateUniswapV2Router(newRouter, address(uniswapV2Router));
        uniswapV2Router = _newRouter;
    }

    function updateSwapTokensAtAmount(uint256 _swapTokensAtAmount) public onlyManager {
        swapTokensAtAmount = _swapTokensAtAmount * (10**18);
    }

    function excludeFromFees(address account, bool excluded) public onlyManager {
        require(_isExcludedFromFees[account] != excluded, "Error: Account is already the value of 'excluded'");
        _isExcludedFromFees[account] = excluded;
        emit ExcludeFromFees(account, excluded);
    }

    function excludeMultipleAccountsFromFees(address[] calldata accounts, bool excluded) public onlyManager {
        for(uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFees[accounts[i]] = excluded;
        }
        emit ExcludeMultipleAccountsFromFees(accounts, excluded);
    }

    function setMarketingWallet(address payable wallet) external onlyManager {
        _marketingWalletAddress = wallet;
    }

    function setGiveawayWallet(address payable wallet) external onlyManager {
        _giveawayWalletAddress = wallet;
    }

    function setRewardWallet(address payable wallet) external onlyManager {
        _rewardWalletAddress = wallet;
    }

    function setSwapAndLiquifyEnabled(bool _enabled) public onlyManager {
        swapAndLiquifyEnabled = _enabled;
        emit SwapAndLiquifyEnabledUpdated(_enabled);
    }

    function setIsRewardOn(bool enabled) public onlyManager {
        isRewardOn = enabled;
    }

    function setStartTrade(bool enabled) public onlyManager {
        startTrade = enabled;
    }

    function setManager(address _manager) public onlyManager {
        manager = _manager;
    }

    function setMaxTxPercent(uint256 maxTxPercent) external onlyManager {
        _maxTxAmount = totalSupply().mul(maxTxPercent).div(10**2);
    }

    function setFeesOnBuy(uint256 _burnFeeOnBuy, uint256 _rewardsFeeOnBuy, uint256 _giveawayFeeOnBuy, uint256 _marketingFeeOnBuy, uint256 _liquidityFeeOnBuy) external onlyManager{
        burnFeeOnBuy      = _burnFeeOnBuy;
        rewardsFeeOnBuy   = _rewardsFeeOnBuy;
        giveawayFeeOnBuy  = _giveawayFeeOnBuy;
        marketingFeeOnBuy = _marketingFeeOnBuy;
        liquidityFeeOnBuy = _liquidityFeeOnBuy;
        totalFeesOnBuy    = marketingFeeOnBuy.add(rewardsFeeOnBuy).add(giveawayFeeOnBuy).add(liquidityFeeOnBuy).add(burnFeeOnBuy);
    }

    function setFeesOnSell(uint256 _burnFeeOnSell, uint256 _rewardsFeeOnSell, uint256 _buybackFeeOnSell, uint256 _giveawayFeeOnSell, uint256 _marketingFeeOnSell, uint256 _liquidityFeeOnSell) external onlyManager{
        burnFeeOnSell      = _burnFeeOnSell;
        rewardsFeeOnSell   = _rewardsFeeOnSell;
        buybackFeeOnSell   = _buybackFeeOnSell;
        giveawayFeeOnSell  = _giveawayFeeOnSell;
        marketingFeeOnSell = _marketingFeeOnSell;
        liquidityFeeOnSell = _liquidityFeeOnSell;
        totalFeesOnSell    = marketingFeeOnSell.add(rewardsFeeOnSell).add(giveawayFeeOnSell).add(liquidityFeeOnSell).add(burnFeeOnSell).add(buybackFeeOnSell);
    }
    
    function blacklistAddress(address account, bool value) external onlyManager {
        _isBlacklisted[account] = value;
    }

    function updateGasForProcessing(uint256 newValue) public onlyManager {
        require(newValue >= 200000 && newValue <= 500000, "Error: gasForProcessing must be between 200,000 and 500,000");
        require(newValue != gasForProcessing, "Error: Cannot update gasForProcessing to same value");
        emit GasForProcessingUpdated(newValue, gasForProcessing);
        gasForProcessing = newValue;
    }

    function updateClaimWait(uint256 claimWait) external onlyManager {
        dividendTracker.updateClaimWait(claimWait);
    }

    function updateMinimumTokensBalanceForDividends(uint256 minimumTokenBalanceForDividends) external onlyManager {
        dividendTracker.updateMinimumTokensBalanceForDividends(minimumTokenBalanceForDividends);
    }

    function excludeFromDividends(address account) external onlyManager {
	    dividendTracker.excludeFromDividends(account);
	}

    function getClaimWait() external view returns(uint256) {
        return dividendTracker.claimWait();
    }

    function getTotalDividendsDistributed() external view returns (uint256) {
        return dividendTracker.totalDividendsDistributed();
    }

    function isExcludedFromFees(address account) public view returns(bool) {
        return _isExcludedFromFees[account];
    }

    function withdrawableDividendOf(address account) public view returns(uint256) {
    	return dividendTracker.withdrawableDividendOf(account);
  	}

	function dividendTokenBalanceOf(address account) public view returns (uint256) {
		return dividendTracker.balanceOf(account);
	}

    function getAccountDividendsInfo(address account) external view returns (address, int256, int256, uint256, uint256, uint256, uint256, uint256) {
        return dividendTracker.getAccount(account);
    }

	function getAccountDividendsInfoAtIndex(uint256 index) external view returns (address, int256, int256, uint256, uint256, uint256, uint256, uint256) {
    	return dividendTracker.getAccountAtIndex(index);
    }

	function processDividendTracker(uint256 gas) external {
		(uint256 iterations, uint256 claims, uint256 lastProcessedIndex) = dividendTracker.process(gas);
		emit ProcessedDividendTracker(iterations, claims, lastProcessedIndex, false, gas, tx.origin);
    }

    function claim() external {
		dividendTracker.processAccount(msg.sender, false);
    }

    function withdrawTokensFromDividendTracker(address token, address recipient) external onlyManager {
        dividendTracker.withdrawTokens(token, recipient);
    }

    function getLastProcessedIndex() external view returns(uint256) {
    	return dividendTracker.getLastProcessedIndex();
    }

    function getNumberOfDividendTokenHolders() external view returns(uint256) {
        return dividendTracker.getNumberOfTokenHolders();
    }

    function _transfer(address from, address to, uint256 amount) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(!_isBlacklisted[from] && !_isBlacklisted[to], 'Blacklisted address');

        if (!startTrade) {
            revert('Trading is not active!');
        }

        if(amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        if (launchedAt == 0 && to == uniswapV2Pair) {
            launchedAt = block.number;
        }

        if(from != owner() && to != owner() && !_isExcludedFromFees[from] && !_isExcludedFromFees[to]) {
            require(amount <= _maxTxAmount, "Transfer amount exceeds the maxTxAmount");
        }

		uint256 contractTokenBalance = balanceOf(address(this));
        bool canSwap = contractTokenBalance >= swapTokensAtAmount;
        if(canSwap && !swapping && to == uniswapV2Pair && swapAndLiquifyEnabled && from != owner() && to != owner()) {
            contractTokenBalance = swapTokensAtAmount;
            swapping = true;

            uint256 tokensToBurn = (contractTokenBalance.mul(burnFeeOnSell)).div(totalFeesOnSell);
            uint256 tokenBalanceLessBurn = contractTokenBalance.sub(tokensToBurn);
            uint256 tokensLiquifyHalf = ((tokenBalanceLessBurn.mul(liquidityFeeOnSell)).div(totalFeesOnSell)).div(2);
            uint256 tokensToSwapForEth = tokenBalanceLessBurn.sub(tokensLiquifyHalf);

            // Swap fee into bnb first
            swapTokensForEth(tokensToSwapForEth);

            uint256 ethBalance = address(this).balance;
            uint256 ethForLiquidity = ((ethBalance.mul(liquidityFeeOnSell)).div(totalFeesOnSell)).div(2);

            // Add to liquidity
            liquify(tokensLiquifyHalf, ethForLiquidity);

            // Send bnb to marketing
            _marketingWalletAddress.transfer((ethBalance.mul(marketingFeeOnSell)).div(totalFeesOnSell));
            // Send to giveaway
            _giveawayWalletAddress.transfer((ethBalance.mul(giveawayFeeOnSell)).div(totalFeesOnSell));

            // Buy Back
            swapETHForTokens((ethBalance.mul(buybackFeeOnSell)).div(totalFeesOnSell));

            // Handle rewards
            if (isRewardOn) {
                swapAndSendDividends(address(this).balance);
            } else {
                _rewardWalletAddress.transfer(address(this).balance);
            }

            // Burn tokens
            _burn(address(this), tokensToBurn);

            swapping = false;
        }

        bool takeFee = true;
        if(_isExcludedFromFees[from] || _isExcludedFromFees[to] || from != uniswapV2Pair && to != uniswapV2Pair) {
            takeFee = false;
        }

        if(takeFee) {
        	uint256 fees;
        	if(to == uniswapV2Pair){
                fees = amount.mul(totalFeesOnSell).div(100);
        	} 
            
            if (from == uniswapV2Pair) {
                if (block.number - launchedAt <= 7) {
                    uint256 afterBalance = balanceOf(to) + amount;
                    require(afterBalance <= 2_000_000 * (10**18), 'Owned amount exceeds the max');
                    fees = amount.mul(99).div(100);
                } else if (block.number - launchedAt.add(7) <= 3) {
                    uint256 afterBalance = balanceOf(to) + amount;
                    require(afterBalance <= 2_000_000 * (10**18), 'Owned amount exceeds the max');
                    fees = amount.mul(totalFeesOnBuy).div(100);
                } else {
                    fees = amount.mul(totalFeesOnBuy).div(100);
                }
            }
        	amount = amount.sub(fees);

            super._transfer(from, address(this), fees);
        }

        super._transfer(from, to, amount);

        if (isRewardOn) {
            try dividendTracker.setBalance(payable(from), balanceOf(from)) {} catch {}
            try dividendTracker.setBalance(payable(to), balanceOf(to)) {} catch {}

            if(!swapping) {
                uint256 gas = gasForProcessing;
                try dividendTracker.process(gas) returns (uint256 iterations, uint256 claims, uint256 lastProcessedIndex) {
                    emit ProcessedDividendTracker(iterations, claims, lastProcessedIndex, true, gas, tx.origin);
                }
                catch {

                }
            }
        }
    }

    function liquify(uint256 tokenAmount, uint256 ethAmount) private {
        // add liquidity to uniswap
        addLiquidity(tokenAmount, ethAmount);
        emit SwapAndLiquify(tokenAmount, ethAmount, tokenAmount);
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            owner(),
            block.timestamp
        );
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function swapETHForTokens(uint256 ethAmount) private {
        // generate the uniswap pair path of weth -> token
        address[] memory path = new address[](2);
        path[0] = uniswapV2Router.WETH();
        path[1] = address(this);

        // make the swap
        uniswapV2Router.swapExactETHForTokensSupportingFeeOnTransferTokens{value: ethAmount}(
            0, // accept any amount of Tokens
            path,
            deadWallet, // Burn address
            block.timestamp
        );
    }

    function swapETHForRewardToken(uint256 ethAmount) private {
        // generate the uniswap pair path of weth -> token
        address[] memory path = new address[](2);
        path[0] = uniswapV2Router.WETH();
        path[1] = rewardToken;

        // make the swap
        uniswapV2Router.swapExactETHForTokensSupportingFeeOnTransferTokens{value: ethAmount}(
            0, // accept any amount of Tokens
            path,
            address(dividendTracker),
            block.timestamp
        );
    }

    function swapAndSendDividends(uint256 ethAmount) private {
        uint256 initialDividendBalance = IERC20(rewardToken).balanceOf(address(dividendTracker));
        swapETHForRewardToken(ethAmount);
        uint256 newDividendBalance = IERC20(rewardToken).balanceOf(address(dividendTracker));
        uint256 dividendAdded = newDividendBalance.sub(initialDividendBalance);

        if (dividendAdded > 0) {
            dividendTracker.distributeDividends(dividendAdded);
            emit SendDividends(ethAmount, dividendAdded);
        }
    }
}

contract RewardDividendTracker is Ownable, DividendPayingToken {
    using SafeMath for uint256;
    using SafeMathInt for int256;
    using IterableMapping for IterableMapping.Map;

    IterableMapping.Map private tokenHoldersMap;
    uint256 public lastProcessedIndex;

    mapping (address => bool) public excludedFromDividends;

    mapping (address => uint256) public lastClaimTimes;

    uint256 public claimWait;
    uint256 public minimumTokenBalanceForDividends;

    event ExcludeFromDividends(address indexed account);
    event ClaimWaitUpdated(uint256 indexed newValue, uint256 indexed oldValue);
    event UpdateMinimumTokensBalanceForDividends(uint256 indexed newValue, uint256 indexed oldValue);
    event Claim(address indexed account, uint256 amount, bool indexed automatic);

    constructor() public DividendPayingToken("Dividen_Tracker", "Dividend_Tracker") {
    	claimWait = 3600;
        minimumTokenBalanceForDividends = 10_000 * (10**18); // must hold at least 10,000 tokens
    }

    function _transfer(address, address, uint256) internal override {
        require(false, "Dividend_Tracker: No transfers allowed");
    }

    function withdrawDividend() public override {
        require(false, "Dividend_Tracker: withdrawDividend disabled. Use the 'claim' function on the main contract.");
    }

    function excludeFromDividends(address account) external onlyOwner {
    	require(!excludedFromDividends[account]);
    	excludedFromDividends[account] = true;
    	_setBalance(account, 0);
    	tokenHoldersMap.remove(account);
    	emit ExcludeFromDividends(account);
    }

    function updateClaimWait(uint256 newClaimWait) external onlyOwner {
        require(newClaimWait >= 3600 && newClaimWait <= 86400, "Dividend_Tracker: claimWait must be updated to between 1 and 24 hours");
        require(newClaimWait != claimWait, "Dividend_Tracker: Cannot update claimWait to same value");
        emit ClaimWaitUpdated(newClaimWait, claimWait);
        claimWait = newClaimWait;
    }

    function updateMinimumTokensBalanceForDividends(uint256 _minimumTokenBalanceForDividends) external onlyOwner {
        require(_minimumTokenBalanceForDividends != minimumTokenBalanceForDividends, "Dividend_Tracker: Cannot update minimumTokenBalanceForDividends to same value");
        emit UpdateMinimumTokensBalanceForDividends(_minimumTokenBalanceForDividends, minimumTokenBalanceForDividends);
        minimumTokenBalanceForDividends = _minimumTokenBalanceForDividends;
    }

    function getLastProcessedIndex() external view returns(uint256) {
    	return lastProcessedIndex;
    }

    function getNumberOfTokenHolders() external view returns(uint256) {
        return tokenHoldersMap.keys.length;
    }

    function getAccount(address _account) public view returns (address account, int256 index, int256 iterationsUntilProcessed, uint256 withdrawableDividends, uint256 totalDividends, uint256 lastClaimTime, uint256 nextClaimTime, uint256 secondsUntilAutoClaimAvailable) {
        account = _account;

        index = tokenHoldersMap.getIndexOfKey(account);

        iterationsUntilProcessed = -1;

        if(index >= 0) {
            if(uint256(index) > lastProcessedIndex) {
                iterationsUntilProcessed = index.sub(int256(lastProcessedIndex));
            }
            else {
                uint256 processesUntilEndOfArray = tokenHoldersMap.keys.length > lastProcessedIndex ? tokenHoldersMap.keys.length.sub(lastProcessedIndex) : 0;
                iterationsUntilProcessed = index.add(int256(processesUntilEndOfArray));
            }
        }

        withdrawableDividends = withdrawableDividendOf(account);
        totalDividends = accumulativeDividendOf(account);

        lastClaimTime = lastClaimTimes[account];

        nextClaimTime = lastClaimTime > 0 ? lastClaimTime.add(claimWait) : 0;

        secondsUntilAutoClaimAvailable = nextClaimTime > block.timestamp ? nextClaimTime.sub(block.timestamp) : 0;
    }

    function getAccountAtIndex(uint256 index) public view returns (address, int256, int256, uint256, uint256, uint256, uint256, uint256) {
    	if(index >= tokenHoldersMap.size()) {
            return (0x0000000000000000000000000000000000000000, -1, -1, 0, 0, 0, 0, 0);
        }

        address account = tokenHoldersMap.getKeyAtIndex(index);

        return getAccount(account);
    }

    function canAutoClaim(uint256 lastClaimTime) private view returns (bool) {
    	if(lastClaimTime > block.timestamp)  {
    		return false;
    	}
    	return block.timestamp.sub(lastClaimTime) >= claimWait;
    }

    function setBalance(address payable account, uint256 newBalance) external onlyOwner {
    	if(excludedFromDividends[account]) {
    		return;
    	}

    	if(newBalance >= minimumTokenBalanceForDividends) {
            _setBalance(account, newBalance);
    		tokenHoldersMap.set(account, newBalance);
    	}
    	else {
            _setBalance(account, 0);
    		tokenHoldersMap.remove(account);
    	}

    	processAccount(account, true);
    }

    function withdrawTokens(address token, address recipient) external onlyOwner {
        _withdrawTokens(token, recipient);
    }

    function process(uint256 gas) public returns (uint256, uint256, uint256) {
    	uint256 numberOfTokenHolders = tokenHoldersMap.keys.length;

    	if(numberOfTokenHolders == 0) {return (0, 0, lastProcessedIndex);}

    	uint256 _lastProcessedIndex = lastProcessedIndex;
    	uint256 gasUsed = 0;
    	uint256 gasLeft = gasleft();
    	uint256 iterations = 0;
    	uint256 claims = 0;

    	while(gasUsed < gas && iterations < numberOfTokenHolders) {
    		_lastProcessedIndex++;

    		if(_lastProcessedIndex >= tokenHoldersMap.keys.length) {
    			_lastProcessedIndex = 0;
    		}

    		address account = tokenHoldersMap.keys[_lastProcessedIndex];

    		if(canAutoClaim(lastClaimTimes[account])) {
    			if(processAccount(payable(account), true)) {
    				claims++;
    			}
    		}

    		iterations++;
    		uint256 newGasLeft = gasleft();

    		if(gasLeft > newGasLeft) {
    			gasUsed = gasUsed.add(gasLeft.sub(newGasLeft));
    		}

    		gasLeft = newGasLeft;
    	}

    	lastProcessedIndex = _lastProcessedIndex;

    	return (iterations, claims, lastProcessedIndex);
    }

    function processAccount(address payable account, bool automatic) public onlyOwner returns (bool) {
        uint256 amount = _withdrawDividendOfUser(account);

    	if(amount > 0) {
    		lastClaimTimes[account] = block.timestamp;
            emit Claim(account, amount, automatic);
    		return true;
    	}

    	return false;
    }
}
"
    }
  },
