import "@openzeppelin/contracts/access/Ownable.sol";

contract Multicall3 is Ownable {
    struct Call {
        address target;
        bytes callData;
    }

    struct Call3 {
        address target;
        bool allowFailure;
        bytes callData;
    }

    struct Call3Value {
        address target;
        bool allowFailure;
        uint256 value;
        bytes callData;
    }

    struct Result {
        bool success;
        bytes returnData;
    }

    // Add a mapping to track whitelisted addresses
    mapping(address => bool) public whitelist;

    event WhitelistAdded(address account);
    event WhitelistRemoved(address account);
    error ErrorCallFailure(uint callIndex, bytes returnData);
    error ErrorCallerNotWhitelisted();
    error ErrorMismatchValue(uint256 value, uint256 accumulateVault);

    // Modifier to restrict function calls to only whitelisted addresses
    modifier onlyWhitelisted() {
        if (!whitelist[msg.sender]) {
            revert ErrorCallerNotWhitelisted();
        }
        _;
    }

    constructor(address initialOwner) Ownable(initialOwner) {}

