    function tryAggregate(
        bool requireSuccess,
        Call[] calldata calls
    ) public payable onlyWhitelisted returns (Result[] memory returnData) {
        uint256 length = calls.length;
        returnData = new Result[](length);
        Call calldata call;
        for (uint256 i = 0; i < length; ) {
