    event Mint(address indexed sender, uint amount0, uint amount1);
    event Burn(address indexed sender, uint amount0, uint amount1, address indexed to);
    event Swap(
        address indexed sender,
        uint amount0In,
        uint amount1In,
        uint amount0Out,
        uint amount1Out,
        address indexed to
    );
    event Sync(uint112 reserve0, uint112 reserve1);

    function MINIMUM_LIQUIDITY() external pure returns (uint);
    function factory() external view returns (address);
    function token0() external view returns (address);
    function token1() external view returns (address);
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
    function price0CumulativeLast() external view returns (uint);
    function price1CumulativeLast() external view returns (uint);
    function kLast() external view returns (uint);

    function mint(address to) external returns (uint liquidity);
    function burn(address to) external returns (uint amount0, uint amount1);
    function swap(uint amount0Out, uint amount1Out, address to, bytes calldata data) external;
    function skim(address to) external;
    function sync() external;

    function initialize(address, address) external;
}

interface ISwapRouter01 {
    function factory() external pure returns (address);
    function WETH() external pure returns (address);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint amountADesired,
        uint amountBDesired,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB, uint liquidity);
    function addLiquidityETH(
        address token,
        uint amountTokenDesired,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external payable returns (uint amountToken, uint amountETH, uint liquidity);
    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline
    ) external returns (uint amountA, uint amountB);
    function removeLiquidityETH(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external returns (uint amountToken, uint amountETH);
    function removeLiquidityWithPermit(
        address tokenA,
        address tokenB,
        uint liquidity,
        uint amountAMin,
        uint amountBMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountA, uint amountB);
    function removeLiquidityETHWithPermit(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountToken, uint amountETH);
    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapTokensForExactTokens(
        uint amountOut,
        uint amountInMax,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts);
    function swapExactETHForTokens(uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        payable
        returns (uint[] memory amounts);
    function swapTokensForExactETH(uint amountOut, uint amountInMax, address[] calldata path, address to, uint deadline)
        external
        returns (uint[] memory amounts);
    function swapExactTokensForETH(uint amountIn, uint amountOutMin, address[] calldata path, address to, uint deadline)
        external
        returns (uint[] memory amounts);
    function swapETHForExactTokens(uint amountOut, address[] calldata path, address to, uint deadline)
        external
        payable
        returns (uint[] memory amounts);

    function quote(uint amountA, uint reserveA, uint reserveB) external pure returns (uint amountB);
    function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) external pure returns (uint amountOut);
    function getAmountIn(uint amountOut, uint reserveIn, uint reserveOut) external pure returns (uint amountIn);
    function getAmountsOut(uint amountIn, address[] calldata path) external view returns (uint[] memory amounts);
    function getAmountsIn(uint amountOut, address[] calldata path) external view returns (uint[] memory amounts);
}

interface ISwapRouter02 is ISwapRouter01 {
    function removeLiquidityETHSupportingFeeOnTransferTokens(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline
    ) external returns (uint amountETH);
    function removeLiquidityETHWithPermitSupportingFeeOnTransferTokens(
        address token,
        uint liquidity,
        uint amountTokenMin,
        uint amountETHMin,
        address to,
        uint deadline,
        bool approveMax, uint8 v, bytes32 r, bytes32 s
    ) external returns (uint amountETH);

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external payable;
    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
}

/**
 * Stores constant shared between the token and presale contracts
 * Prevents accidental mismatches between contracts
 * bsc main PCS v2 router 0x10ED43C718714eb63d5aA57B78B54704E256024E
 * Uniswap v2 router 0x7a250d5630B4cF539739dF2C5dAcb4c659F2488D
 * testnet PCS router 0x9Ac64Cc6e4415144C455BD8E4837Fea55603e5c3
 */
contract OrbitShared {
    address constant public ROUTER = 0x10ED43C718714eb63d5aA57B78B54704E256024E; 
    // CHANGEME - This needs changing to the PCS Router address: 0x10ED43C718714eb63d5aA57B78B54704E256024E
}

contract Orbit is ERC20, Ownable, OrbitShared {
    using Address for address;
    using Address for address payable;

    ISwapRouter02 public uniswapV2Router;
    address public  uniswapV2Pair;

    bool private swapping;

    address public constant BURN_ADDRESS = 0x000000000000000000000000000000000000dEaD;
    
    uint256 public swapTokensAtAmount = 100_000 * (10**18);
    uint256 public blacklistTimeout = 30 minutes;
    
    mapping(address => uint256) public isBlacklistedUntil;
    mapping(address => uint256) public lastSellTime;
    mapping(address => uint256) public lastBuyTime;
    mapping(address => uint256) public lastSellTimeBalance;
    
    uint8 public maxSellPercentage = 100;
    uint256 public sellDelay = 0 hours;
    uint256 public buyDelay = 0 minutes;
    uint256 public totalHoldingsToExempt = 400 * 10**17; // 40BNB

    uint256 public liquidityFee = 2;
    uint256 public marketingFee = 5;
    uint256 public feeDenominator = 100;

    uint256 public maxTxAmount;
    uint256 private launchedAt;
    bool public tradingOpened;
    
    address public marketingWalletAddress = 0x05AAe3B9016deC38D73d6170e182A4e807fc5D81;

     // exlcude from fees and max transaction amount
    mapping (address => bool) private isExcludedFromFees;


    // store addresses that a automatic market maker pairs. Any transfer *to* these addresses
    // could be subject to a maximum transfer amount
    mapping (address => bool) public automatedMarketMakerPairs;

    event UpdateUniswapV2Router(address indexed newAddress, address indexed oldAddress);
    event ExcludeFromFees(address indexed account, bool isExcluded);
    event ExcludeMultipleAccountsFromFees(address[] accounts, bool isExcluded);
    event SetAutomatedMarketMakerPair(address indexed pair, bool indexed value);

    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiquidity
    );

    constructor() ERC20("Orbit", "ORBIT") {
        ISwapRouter02 _uniswapV2Router = ISwapRouter02(ROUTER);
         // Create a uniswap pair for this new token
        address _uniswapV2Pair = ISwapFactory(_uniswapV2Router.factory()).createPair(address(this), _uniswapV2Router.WETH());

        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;

        _setAutomatedMarketMakerPair(_uniswapV2Pair, true);

        // exclude from paying fees or having max transaction amount
        excludeFromFees(owner(), true);
        excludeFromFees(marketingWalletAddress, true);
        excludeFromFees(BURN_ADDRESS, true);
        excludeFromFees(address(this), true);

        /*
            _mint is an internal function in ERC20.sol that is only called here,
            and CANNOT be called ever again
        */
        _mint(owner(), 10_000_000_000 * 10**18);
         maxTxAmount = totalSupply() / 500; // 0.2%
    }

    receive() external payable {}
    

    function airdropDifferentNumberOfTokens (address airdropWallet, address[] calldata airdropRecipients, uint256[] calldata airdropAmounts) external onlyOwner {
        if (!isExcludedFromFees[airdropWallet])
        excludeFromFees(airdropWallet, true);
        require (airdropRecipients.length == airdropAmounts.length, "Length of recipient and amount arrays must be the same");
        
        // airdropWallet needs to have approved the contract address to spend at least the sum of airdropAmounts
        for (uint256 i = 0; i < airdropRecipients.length; i++)
            _transfer (airdropWallet, airdropRecipients[i], airdropAmounts[i]);
    }

    
    function airdropSameNumberOfTokens (address airdropWallet, address[] calldata airdropRecipients, uint256 airdropAmount) external onlyOwner {
        if (!isExcludedFromFees[airdropWallet])
        excludeFromFees(airdropWallet, true);
        // airdropWallet needs to have approved the contract address to spend at least airdropAmount * number of recipients
        for (uint256 i = 0; i < airdropRecipients.length; i++)
            _transfer (airdropWallet, airdropRecipients[i], airdropAmount);
    }

    function updateUniswapV2Router(address newAddress) public onlyOwner {
        require(newAddress != address(uniswapV2Router), "Orbit: The router already has that address");
        emit UpdateUniswapV2Router(newAddress, address(uniswapV2Router));
        uniswapV2Router = ISwapRouter02(newAddress);
        address _uniswapV2Pair = ISwapFactory(uniswapV2Router.factory()).createPair(address(this), uniswapV2Router.WETH());
        uniswapV2Pair = _uniswapV2Pair;
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        require(isExcludedFromFees[account] != excluded, "Orbit: Account is already the value of 'excluded'");
        isExcludedFromFees[account] = excluded;

        emit ExcludeFromFees(account, excluded);
    }

    function excludeMultipleAccountsFromFees(address[] calldata accounts, bool excluded) public onlyOwner {
        for(uint256 i = 0; i < accounts.length; i++) {
            isExcludedFromFees[accounts[i]] = excluded;
        }

        emit ExcludeMultipleAccountsFromFees(accounts, excluded);
    }

    function setMarketingWallet(address payable wallet) external onlyOwner{
        require(marketingWalletAddress != address(0), "Orbit: Can't set marketing wallet to the zero address");
        marketingWalletAddress = wallet;
    }
    
    function setMaxSellPercentage (uint8 value) external onlyOwner {
        require (value >= 10, "Orbit: Can't set sell percentage < 10%");
        maxSellPercentage = value;
    }

    function setSellDelay (uint256 valueInSeconds) external onlyOwner {
        require (valueInSeconds <= 6 hours, "Orbit: Can't set sell delay > 6 hours");
        sellDelay = valueInSeconds;
    }

    function setBuyDelay (uint256 valueInSeconds) external onlyOwner {
        require (valueInSeconds <= 6 hours, "Orbit: Can't set buy delay > 6 hours");
        buyDelay = valueInSeconds;
    }

    function setTotalHoldingsToExempt (uint256 valueInWei) external onlyOwner {
        totalHoldingsToExempt = valueInWei;
    }

    function setLiquidityFee(uint256 value) external onlyOwner {
        liquidityFee = value;
    }

    function setMarketingFee(uint256 value) external onlyOwner {
        marketingFee = value;
    }
    
    function setBlacklistTimeout(uint256 value) external onlyOwner{
        blacklistTimeout = value;
    }
    
    function setMaxTxPermille(uint256 maxTxPermille) external onlyOwner {
        require (maxTxPermille > 0, "Orbit: Can't set max Tx to 0");
        maxTxAmount = totalSupply() * maxTxPermille / 1000;
    }

    function setAutomatedMarketMakerPair(address pair, bool value) public onlyOwner {
        require(pair != uniswapV2Pair, "Orbit: The PancakeSwap pair cannot be removed from automatedMarketMakerPairs");

        _setAutomatedMarketMakerPair(pair, value);
    }
    
    function blacklistAddress(address account, bool value) external onlyOwner {
        require (block.timestamp < launchedAt + 30 minutes, "Orbit: Can't blacklist, out of time");
        isBlacklistedUntil[account] = block.timestamp + (value ? blacklistTimeout : 0);
    }
    
    function launch() external onlyOwner {
        launchedAt = block.timestamp;
        tradingOpened = true;
    }
    
    function toggleTrading (bool _tradingOpened) external onlyOwner {
        // require (launchedAt + 1 days < block.timestamp && !_tradingOpened, "Orbit: Cannot pause trading"); //CHANGEME Comment this line out to be able to pause forever, else the ability to pause is limited to first 24 hours only
        tradingOpened = _tradingOpened;
    }


    function _setAutomatedMarketMakerPair(address pair, bool value) private {
        require(automatedMarketMakerPairs[pair] != value, "Orbit: Automated market maker pair is already set to that value");
        automatedMarketMakerPairs[pair] = value;

        emit SetAutomatedMarketMakerPair(pair, value);
    }

    function excludedFromFees (address account) public view onlyOwner returns (bool) {
        return isExcludedFromFees[account];
    }
    
    function isExemptFromSellPercentage (address account) private view returns (bool) {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        uint256[] memory valueOfHoldingsInBnb = uniswapV2Router.getAmountsOut (balanceOf (account), path);
        return valueOfHoldingsInBnb[1] >= totalHoldingsToExempt ? false : true;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require (tradingOpened || isExcludedFromFees[from], "Orbit: Trading paused");
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(isBlacklistedUntil[from] < block.timestamp && isBlacklistedUntil[to] < block.timestamp, "Orbit: Blacklisted address");

        if(amount == 0) {
            super._transfer(from, to, 0);
            return;
        }
        
        if (automatedMarketMakerPairs[to]) { //Selling
            if (!isExcludedFromFees[from] && (!isExemptFromSellPercentage (from) || block.timestamp - lastSellTime[from] <= sellDelay)) {
                uint256 maxAllowedToSell = balanceOf(from) * maxSellPercentage / 100;
                require (amount <= maxAllowedToSell, "Orbit: Sell amount too high"); // Catches sells > maxSellPercentage
                
                if (block.timestamp - lastSellTime[from] <= sellDelay) {
                    maxAllowedToSell = lastSellTimeBalance[from] * maxSellPercentage / 100;
                    require (balanceOf(from) - amount >= lastSellTimeBalance[from] - maxAllowedToSell, "Orbit: Wait until sell delay is up to sell again"); // Catches multiple sells > maxSellPercentage within sellDelay
                } else {
                    lastSellTime[from] = block.timestamp;
                    lastSellTimeBalance[from] = balanceOf(from);
                }
            }
        } else if (automatedMarketMakerPairs[from]) {
            if (!isExcludedFromFees[to]) {
                require (block.timestamp - lastBuyTime[to] >= buyDelay, "Orbit: Wait until buy delay is up to buy again");
                lastBuyTime[to] = block.timestamp;
            }
        }

		uint256 contractTokenBalance = balanceOf(address(this));
		uint256 totalFees = liquidityFee + marketingFee;

        bool canSwap = contractTokenBalance >= swapTokensAtAmount && launchedAt + 10 < block.timestamp && tradingOpened;

        if( canSwap &&
            !swapping &&
            !automatedMarketMakerPairs[from] &&
            from != owner() &&
            to != owner()
        ) {
            swapping = true;

            uint256 swapTokens = contractTokenBalance * liquidityFee / totalFees;
            swapAndLiquify(swapTokens);

            uint256 marketingTokens = balanceOf(address(this));
            swapAndSendToFee(marketingTokens, marketingWalletAddress);

            swapping = false;
        }


        bool takeFee = !swapping;

        // if any account belongs to _isExcludedFromFee account then remove the fee
        if (isExcludedFromFees[from] || isExcludedFromFees[to])
            takeFee = false;

        if (takeFee) {
        	uint256 fees = amount * (launchedAt + 10 < block.timestamp ? totalFees : feeDenominator - 1) / feeDenominator;
        	
        	if (automatedMarketMakerPairs[to])
        	    fees += amount / 100;
        	
        	amount = amount - fees;
            super._transfer (from, address(this), fees);
        }

        super._transfer (from, to, amount);
    }

    function swapAndSendToFee(uint256 tokens, address feeAddress) private  {
        uint256 initialBalance = address(this).balance;
        swapTokensForEth(tokens);
        uint256 newBalance = address(this).balance - initialBalance;
        (bool success, ) = feeAddress.call{ value: newBalance }("");
        require (success, "Orbit: Payment to marketing wallet failed");
    }

    function swapAndLiquify(uint256 tokens) private {
       // split the contract balance into halves
        uint256 half = tokens / 2;
        uint256 otherHalf = tokens - half;

        // swap tokens for ETH
        uint256 newBalance = swapTokensForEth(half); 

        // add liquidity to uniswap
        addLiquidity(otherHalf, newBalance);

        emit SwapAndLiquify(half, newBalance, otherHalf);
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {

        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        (,uint256 ethFromLiquidity,) = uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            address(0),
            block.timestamp
        );
        if (ethAmount - ethFromLiquidity > 0)
            payable(marketingWalletAddress).sendValue(ethAmount - ethFromLiquidity);
    }

    function swapTokensForEth(uint256 tokenAmount) private returns (uint256) {
        uint256 initialBalance = address(this).balance;
        
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
        
        return (address(this).balance - initialBalance);
    }
}
