      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.7.6;

import "./Common/Uniswap.sol";
import "./Common/ERC20.sol";
import "./Common/SafeMath.sol";
import "./Common/Ownable.sol";
import "./IManager.sol";

abstract contract BPContract {
    function protect( address sender, address receiver, uint256 amount ) external virtual;
}

contract FootballGo is Ownable, ERC20 {
    using SafeMath for uint256;
    IManager public manager;
    uint256 public maxSupply = 2000 * 10**6 * 10**18;
    uint256 public p2eAmount = 1000 * 10**6 * 10**18;
    uint256 public minMarkettingAmount = 2 * 10**5 * 10**18;

    uint256 public p2eReward;
    address public markettingAddress;
    uint256 public buyRate = 3;
    uint256 public sellRate = 6;

    mapping (address => bool) private _isExcludedFromFees;
    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;

    bool inSwapAndLiquify;
    bool public swapAndLiquifyEnabled = false;

    uint256 public _startTimeForSwap;
    uint256 public _intervalMinutesForSwap = 1 * 1 minutes;

    BPContract public BP;
    bool public bpEnabled;
    bool public BPDisabledForever = false;

    event ExcludeFromFees(address indexed account, bool isExcluded);
    event SwapAndLiquifyEnabledUpdated(bool enabled);

    constructor() ERC20("FootballGo", "FGSPORT") {
        excludeFromFees(owner(), true);

        _mint(_msgSender(), maxSupply.sub(p2eAmount));
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(
            0x10ED43C718714eb63d5aA57B78B54704E256024E
        );

        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
        .createPair(address(this), _uniswapV2Router.WETH());

        uniswapV2Router = _uniswapV2Router;
        _approve(address(this), address(uniswapV2Router), ~uint256(0));

        _startTimeForSwap = block.timestamp;
    }

    function approveRouter(address _router, uint256 _amount) public onlyOwner() returns (bool) {
        _approve(address(this), _router, _amount);
    }

    function setBPAddrss(address _bp) external onlyOwner {
        require(address(BP)== address(0), "Can only be initialized once");
        BP = BPContract(_bp);
    }

    function setBpEnabled(bool _enabled) external onlyOwner {
        bpEnabled = _enabled;
    }

    function setBotProtectionDisableForever() external onlyOwner{
        require(BPDisabledForever == false);
        BPDisabledForever = true;
    }

    function burn(uint256 amount) public {
        _burn(_msgSender(), amount);
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        require(_isExcludedFromFees[account] != excluded, "Account is already 'excluded'");
        _isExcludedFromFees[account] = excluded;

        emit ExcludeFromFees(account, excluded);
    }

    function isExcludedFromFees(address account) public view returns(bool) {
        return _isExcludedFromFees[account];
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual override {
        if (bpEnabled && !BPDisabledForever){
            BP.protect(sender, recipient, amount);
        }
        uint256 contractTokenBalance = IERC20(address(this)).balanceOf(address(this));
        bool overMinimumTokenBalance = contractTokenBalance >= minMarkettingAmount;    
        // Sell tokens for ETH
        if (!inSwapAndLiquify && swapAndLiquifyEnabled && IERC20(address(this)).balanceOf(uniswapV2Pair) > 0 && recipient == uniswapV2Pair) {
          if (overMinimumTokenBalance && _startTimeForSwap + _intervalMinutesForSwap <= block.timestamp) {
              _startTimeForSwap = block.timestamp;
              contractTokenBalance = minMarkettingAmount;
              swapTokensForEth(contractTokenBalance);
          }  
        }

        uint256 transferFeeRate = recipient == uniswapV2Pair
            ? sellRate
            : (sender == uniswapV2Pair ? buyRate : 0);

        if (
            transferFeeRate > 0 &&
            sender != address(this) &&
            recipient != address(this) &&
            !_isExcludedFromFees[sender]
        ) {
            uint256 _fee = amount.mul(transferFeeRate).div(100);
            super._transfer(sender, address(this), _fee);
            amount = amount.sub(_fee);
        }

        super._transfer(sender, recipient, amount);
    }

    function swapToken() public onlyOwner {
        uint256 contractTokenBalance = balanceOf(address(this));
        if (contractTokenBalance >= minMarkettingAmount && _startTimeForSwap + _intervalMinutesForSwap <= block.timestamp) {
            _startTimeForSwap = block.timestamp;
            swapTokensForEth(minMarkettingAmount);
        }
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        // make the swap
        try uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            markettingAddress,
            block.timestamp
        ) {} catch {}
    }

    receive() external payable {}

    function setMarkettingAddress(address _markettingAddress) external onlyOwner {
        require(_markettingAddress != address(0), "invalid address");
        markettingAddress = _markettingAddress;
    }

    modifier onlyPlayer() {
        require(manager.isPlayer(_msgSender()), "must be player");
        _;
    }

    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    function setController(address _controller) public onlyOwner {
        manager = IManager(_controller);
    }

    function setFeeRate(uint256 _sellRate, uint256 _buyRate) public onlyOwner {
        require(_sellRate < 20, "Sell fee alway below 20%, so this contract never can lock sell");
        require(_buyRate < 20, "Buy fee alway below 20%");
        sellRate = _sellRate;
        buyRate = _buyRate;
    }

    function setMinTokensBeforeSwap(uint256 _minMarkettingAmount) public onlyOwner {
        require(_minMarkettingAmount < 20 * 10**6 * 10**18);
        minMarkettingAmount = _minMarkettingAmount;
    }

    function reward(address winner, uint256 reward) external onlyPlayer {
        require(p2eReward != p2eAmount, "Over cap");
        require(winner != address(0), "Invalid address");
        require(reward > 0, "Invalid reward");

        p2eReward = p2eReward.add(reward);
        if (p2eReward <= p2eAmount) _mint(winner, reward);
        else {
            uint256 availableReward = p2eAmount.sub(p2eReward.sub(reward));
            _mint(winner, availableReward);
            p2eReward = p2eAmount;
        }
    }

    function setSwapAndLiquifyEnabled(bool _enabled) public onlyOwner {
        swapAndLiquifyEnabled = _enabled;
        emit SwapAndLiquifyEnabledUpdated(_enabled);
    }

    function GetSwapMinutes() public view returns(uint256) {
        return _intervalMinutesForSwap / 60;
    }

    function SetSwapMinutes(uint256 newMinutes) external onlyOwner {
        _intervalMinutesForSwap = newMinutes * 1 minutes;
    }

    function getBalance() public view returns(uint256) {
        return IERC20(address(this)).balanceOf(address(this));
    }

    function withdrawBalance() external onlyOwner {
        IERC20(address(this)).transfer(owner(), getBalance());
    }

}"
