pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/proxy/Proxy.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract SAHero is ERC721, ReentrancyGuard, Ownable,Proxy {
    /**
     * @dev  store info sale campaign
     * @member startTime  start time of campaign
     * @member endTime  : end time of campaign
     * @member basicPrice : price one  hero
     * @member totalSell : Total heros sell
     * @member currentSell : update how many hero sold
     * @member curremaxPerPurchase :  max Per purchase;
     * @member onlyWhiteList: if is true only sold to whitelist addresses
     * @member desc  : description info of sale campaign
     */

    struct Config {
        uint256 startTime;
        uint256 endTime;
        uint256 basicPrice;
        uint256 totalSell;
        uint256 currentSell;
        uint256 maxPurchase;
        bool onlyWhiteList;
        string desc;
    }

    /**
     * @dev ClaimRequest store info when user claim hero
     * @member amount : number heros claim
     * @member claimer : address claim
     */
    struct ClaimRequest {
        uint256 amount;
        address claimer;
    }

    /**
     * @member star of hero
     * @member form of hero
     * @member attackPart of hero
     * @member criticalRatePart of hero
     * @member defensePart of hero
     * @member hpPart of hero
     * @member summonPoint of hero
     * @member heroTypeId of hero
     * @member heroId of hero
      */

    struct Hero {
        uint8 star;
        uint8 form;
        uint8 attackPart;
        uint8 criticalRatePart;
        uint8 defensePart;
        uint8 hpPart;
        uint8 summonPoint;
        uint32 heroTypeId;
        uint256 heroId;
    }

     /**
     * @dev Emitted when Hero created.
     */
    event HeroCreated(uint256 indexed heroId);


    /**
     * @dev Emitted when Hero star up.
     */
    event HeroStarUp(uint256 indexed heroId, uint8 newStar);

     /**
     * @dev Emitted when Hero subtract summonpoint.
     */
    event HeroSummonPointDown(uint256 indexed heroId, uint8 newSummonPoint);

    /**
     * @dev Emitted when generate `requestId` claim heros.
     */
    event ClaimHeroCreated(uint256 indexed requestId, uint256 amount);


    /**
     * @dev Emitted when new operator added.
     */
    event AddedOperator(address indexed operator);

    /**
     * @dev Emitted when  operator removed.
     */
    event RemovedOperator(address indexed operator);

    /**
     * @dev Emitted when change receiveFundAddress.
     */
    event ChangeReceiveFundAddress(address indexed newReceiveFundAddress);

    /**
     * @dev Emitted when change implAddress.
     */
    event ChangeImpAddress(address indexed newImplAddress) ;

    /**
     * @dev Emitted when add address to whitelist.
     */
    event AddWhiteList(address indexed addAddress);

    /**
     * @dev Emitted when remove address from whitelist.
     */
    event RemovedWhiteList(address indexed removeAddress);

    /**
     * @dev Emitted when add new Config.
     */
    event AddNewConfig(uint256 startTime, uint256 endTime);




    /*
    variables
    */

    using Counters for Counters.Counter;

    //array config sales campaign
    Config[] public configs;

    //acccount privileges operator
    mapping(address => bool) public operator;

    //base uri of token
    string private _baseHeroURI;

    //generate new heroId
    Counters.Counter private _heroIdGenerate;

    //generate new requestID
    Counters.Counter private _claimIdGenerate;

    //maping from owner to  index  heroIds
    mapping(address => mapping(uint256 => uint256)) private _ownedHeroes;

    // Mapping from heroId to index of in  _ownedHeros
    mapping(uint256 => uint256) private _ownedHeroesIndex;

    // Array all heroses
    Hero[] public _heroes;

    // Mapping from hero id to indexof  array _heros
    mapping(uint256 => uint256) private  _heroesIndex;

    //mapping from request id to claimRequest struct
    mapping(uint256 => ClaimRequest) private claimRequests;

    //mapping whitelist account
    mapping(address => bool) public whiteList;

    //receive fund address
    address public receiveFun;

    //impl logic code address
    address public implAddress;

    //mapping number of hero purchased of address in the n the sale

    mapping(address => mapping(uint256 => uint256)) public amountBought;

    /**
     * @dev Initializes the contract by setting a `name` and a `symbol` to the token collection.     
     * setting baseHeroURI
     */
    constructor(
        string memory _name,
        string memory _symbol,
        string memory __baseHeroURI
    ) ERC721(_name, _symbol) {
        operator[msg.sender] = true;       
        _baseHeroURI = __baseHeroURI;
        _heroIdGenerate.increment();
        _claimIdGenerate.increment();
        receiveFun = msg.sender;
    }

    /*
    **
    modifier function
    **
    */

    /**
     * @dev Throws if called by any account other than the operator.
     */
    modifier onlyOperator() {
        _onlyOperator();
        _;
    }

    /*
        external functions
    */

    
    /**
     * @dev add or remove address of whitelist
     */

    function changeWhiteList(address _address) external onlyOwner{
        if(whiteList[_address]){
            whiteList[_address] = false;
            emit RemovedWhiteList((_address));
        }else{
            whiteList[_address] = true;
            emit AddWhiteList(_address);
        }
        
    }

    /**
     * @dev set receive fund address 
     */
    
    function setReceiveFunAddress(address _receiveFun) external onlyOwner{
        receiveFun = _receiveFun;
        emit ChangeReceiveFundAddress(_receiveFun);
    }

    /**
     * @dev set implementation address contract 
     */

    function setImplAddress(address _implAddress)external onlyOwner{
        implAddress = _implAddress;
        emit ChangeImpAddress(_implAddress);
    }
   


    /**
     * @dev Returns the total amount of heros stored by the contract.
     */

    function totalSupply() external view returns (uint256) {
        return _heroes.length;
    }

   
     /**
     * @dev Returns the latest index of config.
     */
     function getLatestConfig() external view returns(uint256){
        return configs.length -1;
    }
   

    /**
     * @dev Returns info  a hero owned by `owner` at a given `index` of its hero list.
     * Use along with {balanceOf} to enumerate all of ``owner``'s heros.
     * @param _owner address owner
     * @param _index of hero list
     * @return star of hero
     * @return form of hero
     * @return attackPart of hero
     * @return criticalRatePart of hero
     * @return defensePart of hero
     * @return hpPart of hero
     * @return summonPoint of hero
     * @return heroTypeId of hero
     * @return heroId of hero
     */

    function getHeroOfOwnerByIndex(address _owner, uint256 _index)
        external
        view
        returns (
            uint8,
            uint8,
            uint8,
            uint8,
            uint8,
            uint8,
            uint8,
            uint32,
            uint256
        )
    {
        require(_index < ERC721.balanceOf(_owner), "SA:out of bounds");
        //get heroId at index of owner
        uint256 heroId = _ownedHeroes[_owner][_index];

        //get hero info at _heroesIndex[heroId]
        Hero memory hero = _heroes[_heroesIndex[heroId]];
        return (
            hero.star,
            hero.form,
            hero.attackPart,
            hero.criticalRatePart,
            hero.defensePart,
            hero.hpPart,
            hero.summonPoint,
            hero.heroTypeId,
            heroId
        );
    }

    /**
     * @dev Returns info  a hero by its id.
     * Use along with {balanceOf} to enumerate all of ``owner``'s heros.
     * @param _heroId address owner
     * @return star of hero
     * @return form of hero
     * @return attackPart of hero
     * @return criticalRatePart of hero
     * @return defensePart of hero
     * @return hpPart of hero
     * @return summonPoint of hero
     * @return heroTypeId of hero
     * @return address of owner
     
     */
    function getHeroById(uint256 _heroId)
        external
        view
        returns (
            uint8,
            uint8,
            uint8,
            uint8,
            uint8,
            uint8,
            uint8,
            uint32,
            address
        )
    {
        Hero memory hero = _heroes[_heroesIndex[_heroId]];
        address ownerOfHero = ownerOf(_heroId);
        return (
            hero.star,
            hero.form,
            hero.attackPart,
            hero.criticalRatePart,
            hero.defensePart,
            hero.hpPart,
            hero.summonPoint,
            hero.heroTypeId,
            ownerOfHero
        );
    }

    function _implementation()  internal view override returns (address){
        return  implAddress;
    }
    

    /**
     * @dev summon (mint) new hero to address _to and sub summon point of list hero in _heroesId
     * only operator can call this function
     * @param _to address own new hero
     * @param _star of new hero
     * @param _form of new hero
     * @param _attackPart of new hero
     * @param _criticalRatePart of new hero
     * @param _defensePart of new hero
     * @param _hpPart of new hero
     * @param _summonPoint of new hero
     * @param _heroTypeId of new hero
     * @param _heroesId list hero which sub summon point
     */

    function summonHero(
        address _to,
        uint8 _star,
        uint8 _form,
        uint8 _attackPart,
        uint8 _criticalRatePart,
        uint8 _defensePart,
        uint8 _hpPart,
        uint8 _summonPoint,
        uint32 _heroTypeId,
        uint256[] memory _heroesId
    ) external onlyOperator {
        (bool success, ) =  _implementation().delegatecall(
            abi.encodeWithSignature(
                "summonHero(address,uint8,uint8,uint8,uint8,uint8,uint8,uint8,uint32,uint256[])",
                _to,
                _star,
                _form,
                _attackPart,
                _criticalRatePart,
                _defensePart,
                _hpPart,
                _summonPoint,
                _heroTypeId,
                _heroesId
            )
        );
        require(success,"SA: summon error");
    }

    /**
     * @dev  star up for hero
     * To star up the _heroId we have to burn list food hero _foodHeroesId
     * @param _heroId id of hero will be increased by 1 star
     * @param _foodHeroesId list of heros to be burnedl
     */
    function upStar(uint256 _heroId, uint256[] memory _foodHeroesId)
        external
        onlyOperator
    {
       (bool success, ) =  _implementation().delegatecall(
            abi.encodeWithSignature(
                "upStar(uint256,uint256[])",
                _heroId,
                _foodHeroesId)
        );
        require(success,"SA: upstar error");
    }

    /**
     * @dev user call this function when claim amount hero
     * users can only buy during the period  > start time and < end time ( setup in config struct)
     * user pay amount * basic price ( setup in config struct)
     * @param _amount hero user claim
     */

    function claimHeroes(uint256 _amount) external payable nonReentrant {
        (bool success, ) =   _implementation().delegatecall(
            abi.encodeWithSignature(
                "claimHeroes(uint256)",
                _amount)
        );
        require(success,"SA: claim heroes error");
    }

    /**
     * @dev Create list hero by request id (which generate in claimHeroes function)
     * Only operator can call this function
     * @param _star array star of list heroes generate
     * @param _form array form of list heroes generate
     * @param _attackPart array attackPart of list heroes generate
     * @param _criticalRatePart array riticalRatePart of list heroes generate
     * @param _defensePart array defensePart of list heroes generate
     * @param _hpPart array hpPart of list heroes generate
     * @param _summonPoint array summonPoint of list heroes generate
     * @param _heroTypeId array heroTypeId of list heroes generate
     */
    function callBackClaimHeroes(
        uint256 _claimRequestId,
        uint8[] memory _star,
        uint8[] memory _form,
        uint8[] memory _attackPart,
        uint8[] memory _criticalRatePart,
        uint8[] memory _defensePart,
        uint8[] memory _hpPart,
        uint8[] memory _summonPoint,
        uint32[] memory _heroTypeId
    ) external onlyOperator {
         (bool success, ) =   _implementation().delegatecall(
            abi.encodeWithSignature(
                "callBackClaimHeroes(uint256,uint8[],uint8[],uint8[],uint8[],uint8[],uint8[],uint8[],uint32[])",
                _claimRequestId,_star,_form,_attackPart,_criticalRatePart,_defensePart,_hpPart,_summonPoint,_heroTypeId)
        );
         require(success,"SA: claim  error");
    }

    /**
     * @dev set base uri of hero
     */

    function setBaseTokenUri(string memory __baseHeroURI) external onlyOwner {
        _baseHeroURI = __baseHeroURI;
    }

    /**
     * @dev add or remove address to mapping operator
     * if address is operator then address can call summon, upstar and callback claim function
     */

    function setOperator(address __operator, bool _value) external onlyOwner {
        if (_value) {
            //add operator
            require(!operator[__operator], "SA: operator exist");
            operator[__operator] = _value;
            emit AddedOperator(__operator);
        } else {
            //remove operator
            require(operator[__operator], "SA: operator not exist");
            operator[__operator] = _value;
            emit RemovedOperator(__operator);
        }
    }

    /**
     * @dev up new config to array configs
     */

    function pushNewConfig(
        uint256 _startTime,
        uint256 _endTime,
        uint256 _basicPrice,
        uint256 _totalSell,
        uint256 _maxPurchase,
        bool _onlyWhiteList,
        string memory _desc
    ) external onlyOwner {
        configs.push(
            Config(
                _startTime,
                _endTime,
                _basicPrice,
                _totalSell,
                0,
                _maxPurchase,
                _onlyWhiteList,
                _desc
            )
        );
        emit AddNewConfig(_startTime, _endTime);
    }


    /*
        internal  functions
    */

    /**
     * @dev return base uri of heroes
     */
    function _baseURI() internal view override returns (string memory) {
        return _baseHeroURI;
    }
    
    /*
        private  functions
    */

    /**
     * @dev validate caller  Throws if called by any account other than the operator.
     */
    function _onlyOperator() private view {
        require(operator[msg.sender]);
    }

    

   
}
