        if (rewardsWithToken[_token]) {
            uint256 contractBalance = IERC20(_token).balanceOf(address(this));

            (uint256 totalClaimed, uint256 start, uint256 end) = _getRewardsAmountWithToken(_token, _user);

            if (totalClaimed > 0) {
                if (totalClaimed > contractBalance) {
                    totalClaimed = contractBalance;
                }

                _deliverTokens(_token, _user, totalClaimed);

                startClaimedRewardIndex[_user][_token] = end + 1;

                emit ClaimDistributionWithToken(_token, start, end, totalClaimed, _user);
            }
        }
    }

    function _afterUpdateShares(address _user) internal virtual override {
        super._afterUpdateShares(_user);

        for (uint256 j = 0; j < rewards.length; j++) {
            if (rewardsWithToken[rewards[j]]) {
                address token = rewards[j];

                if ((balanceOf(_user) == 0) && (startClaimedRewardIndex[_user][token] == 0) && (leaveStakingIndex[_user] == 0)) {
                    startClaimedRewardIndex[_user][token] = lastDistributionIndex + 1;
                }
            }
        }
    }
}
