// File: oxyswap.sol
abstract contract Context {
    address private _owner;

    constructor() {
        _setOwner(_msgSender());
    }

    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }

    function owner() public view virtual returns (address) {
        return _owner;
    }

    function _setOwner(address newOwner) private {
        _owner = newOwner;
    }

    modifier onlyOwner() {
        require(owner() == _msgSender(), "Ownable: caller is not the owner");
        _;
    }
}



interface OtherContract {
    function whitelist(address _address) external view returns (bool);
     function whitelistTimestamps(address _address) external view returns (uint256);
}
pragma solidity ^0.8.19;

contract TokenSwap is Context {
    using SafeERC20 for IERC20;
    using Address for address payable;
    mapping(address => bool) public whitelist;
    mapping(address => uint256) public conversionCount;
        mapping(address => uint256) public conversionTimestamp;
    mapping(address => uint256) public whitelistTimestamps;
    uint256 public whitelistPeriod = 48 hours;
    address public oldToken; // Address of the old token (Ox2)
    address public newToken; // Address of the new token
    uint256 public dailyConversionLimit; // Daily conversion limit per wallet
    uint256 public whitelistConversionLimit; // Conversion limit for whitelisting
     uint256 public perDayLimit;
       uint256 public totalCount;
       
          OtherContract public otherContract;
            OtherContract public otherContractB;
       uint256 public lastResetTimestamp =block.timestamp;
    uint256 public SwapCharge = 1; 
    constructor(
        address _oldToken,
        address _newToken,
        uint256 _dailyConversionLimit,
        uint256 _whitelistConversionLimit,
         uint256 _perDayLimit,
         address _oldaddress,
          address _oldaddressB
    ) {
        whitelist[msg.sender] = true;
        whitelistTimestamps[msg.sender]=block.timestamp;
        oldToken = _oldToken;
        newToken = _newToken;
        dailyConversionLimit = _dailyConversionLimit;
        whitelistConversionLimit = _whitelistConversionLimit;
        perDayLimit = _perDayLimit;
        otherContract=OtherContract(_oldaddress);
          otherContractB=OtherContract(_oldaddressB);
        // Whitelist the contract deployer
    }

    function Reclaimed(uint256 amount) external {
        require(amount > 0, "Amount must be greater than 0");
        require(whitelist[msg.sender] || otherContract.whitelist(msg.sender) ||  otherContractB.whitelist(msg.sender), "Address is not whitelisted");
        require(totalCount+amount <= perDayLimit || block.timestamp >= lastResetTimestamp + 1 days , "Daily transaction limit exceeded");
        require(whitelistTimestamps[msg.sender] == 0 || block.timestamp >= whitelistTimestamps[msg.sender] + whitelistPeriod, "Whitelist period not expired");
       
        // Check conversion limit for the day  
        require(
            conversionCount[msg.sender] + amount <= dailyConversionLimit || block.timestamp >= conversionTimestamp[msg.sender],
            "Daily conversion limit exceeded"
        );

        // Perform token swap
        uint256 newAmount = calculateSwapAmount(amount);
        IERC20(oldToken).safeTransferFrom(msg.sender, address(this), amount);
       
        IERC20(newToken).safeTransfer(msg.sender, newAmount);   
        if(conversionCount[msg.sender] + amount >= dailyConversionLimit){
          conversionTimestamp[msg.sender]=block.timestamp+1 days;
          conversionCount[msg.sender] =amount;
        }
        else{
        conversionCount[msg.sender] = conversionCount[msg.sender] + amount;
        }
        if(block.timestamp >= lastResetTimestamp +  1 days){
              totalCount =amount;
              lastResetTimestamp = block.timestamp;
        }
        else{
            totalCount +=amount;
        }

        emit Swap(msg.sender, oldToken, newToken, amount, amount);
    }


 
   function transferBNBToContract() external payable {
        // Accept BNB payment to the contract
    }
       
    function withdrawToken(address contractAddress,uint256 amount) external onlyOwner {
        IERC20 token = IERC20(contractAddress);
        token.safeTransfer(msg.sender, amount); 
    }
  
    function withdrawBNB(uint256 amount) external onlyOwner {
        require(amount > 0, "Amount must be greater than 0");
        require(address(this).balance >= amount, "Insufficient BNB balance");
        payable(msg.sender).sendValue(amount);
    }
    function balanceOf(address account) external view onlyOwner returns (uint256) {
      return IERC20(newToken).balanceOf(account);
    }
      function checkWhitlist(address account) public view  returns (bool) {
     bool status=whitelist[account] || otherContract.whitelist(account) || otherContractB.whitelist(account);
     return status;
    }
    event Swap(
        address indexed sender,
        address oldToken,
        address newToken,
        uint256 amountA,
        uint256 amountB
    );

    event whietlist(address indexed sender, address receiver, uint256 amount);

    function addToWhitelist() external payable {
        require(!whitelist[msg.sender], "Address is already whitelisted");

        // Transfer 25 old tokens from the sender to this contract for whitelisting
        require(
            IERC20(oldToken).transferFrom(msg.sender, address(this), whitelistConversionLimit),
            "Transfer of old tokens for whitelisting failed"
        );
        emit whietlist(msg.sender, address(this), whitelistConversionLimit);

        whitelist[msg.sender] = true;
        whitelistTimestamps[msg.sender] = block.timestamp;
    }

 

    function removeFromWhitelist(address wallet) external onlyOwner {
        whitelist[wallet] = false;
    }

  
    function setSwapCharge(uint256 per) external onlyOwner {
        SwapCharge = per;
    }

    function setOldToken(address _oldToken) external onlyOwner {
        oldToken = _oldToken;
    }
