      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.6.8;
import "./ERC20.sol";
import "./Ownable.sol";
import "./SafeMath.sol";


contract UNC is ERC20, Ownable  {
    uint256 public constant MAX = ~uint256(0);

    uint256 private constant maxSupply = 130 * 10000 * 1e18;     // the total supply


    using SafeMath for uint256;

    uint256 public _destroyFee = 25;
    uint256 public _nftFee = 50;
    uint256 public _auctionFee = 25; 

    bool isAddliquidity = false;
    bool isNotOpen = true;

    address public _swapAddress = 0xD432Bff3f322F37601486F1F513fE9c46690CA25;
    address public _deadAddress = 0x000000000000000000000000000000000000dEaD;
    address public _nftAddress = 0x6f5732EaC78F09431af3b0B8a3974F33CecfB4A9;
    address public _auctionAddress = 0x5950887f2d0546866C609Ff5282bc894EefC16Aa ;

    address public usdt;

    uint256 public currentPrice;

    IUniswapV2Router02 public immutable uniswapV2Router;
    address public uniswapV2Pair;


    constructor(address _router, address _usdt) ERC20("Unicorn Token", "UNC") public {
        usdt = _usdt;
        _mint(msg.sender, maxSupply);

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(_router);
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(address(this), address(_usdt));

        uniswapV2Router = _uniswapV2Router;
    }


    uint256[4] priceStage = [0, 300 * 1e18, 1000 * 1e18, 3000 * 1e18 ];
    uint256[4] buyFees = [8, 6, 4, 2 ];
    uint256[4] sellFees = [10, 8, 6, 4 ];
    uint256[4] transferFees = [8, 6, 4, 2 ];

    uint256 buyFee = buyFees[0];
    uint256 sellFee = sellFees[0];
    uint256 transferFee = transferFees[0];
    uint256 extraBuyFee = 0;
    uint256 extraSellFee = 0;


    uint256 public INTERVAL = 20 * 60 * 24;     // blocks per day
    bool public isProtection = false;
    uint256 public protectionB;     
    uint256 public protectionP;

    uint256 public lastPrice;


    function setProtection(bool _isProtection) public onlyOwner {
        isProtection = _isProtection;
    }

    function setAddLiquidity(bool _isAddliquidity) public onlyOwner {
        isAddliquidity = _isAddliquidity;
    }

    function setIsNotOpen(bool _isNotOpen) public onlyOwner {
        isNotOpen = _isNotOpen;
    }

    function setProtectionB(uint256 _protectionB) public onlyOwner {
        protectionB = _protectionB;
    }

    event ResetProtection(uint256 indexed blk, uint256 price);

    function feeInfo() public view returns (uint256, uint256, uint256, uint256, uint256, uint256 ) {
        return (buyFee, sellFee, transferFee, extraBuyFee, extraSellFee, getPrice());
    }


    function getPrice() public view returns( uint256 ) {
        return IERC20(usdt).balanceOf(uniswapV2Pair).mul(10 ** 18).div(IERC20(address(this)).balanceOf(uniswapV2Pair));
    }


    // private function
    function _resetProtection() private {
        if (isProtection) {
            if ( block.number <= protectionB.add(INTERVAL)) {   // today
                if (  currentPrice < lastPrice.mul(90).div(100) ) {
                   extraSellFee = 10;
                }
                if (  currentPrice < lastPrice.mul(80).div(100) ) {
                   extraSellFee = 20;
                }
                if (  currentPrice < lastPrice.mul(70).div(100) ) {
                   extraSellFee = 30;
                }
            } else {
                protectionB = protectionB.add(INTERVAL);
                lastPrice = currentPrice;
                extraSellFee = 0;
            }
        }
    }


    function setNftAddress(address nftAddress) public onlyOwner {
        _nftAddress = nftAddress;
    }

    function setAuctionAddress(address auctionAddress) public onlyOwner {
        _auctionAddress = auctionAddress;
    }

    function setSwapAddress(address swapAddress) public onlyOwner {
        _swapAddress = swapAddress;
    }


    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal override {
        // fee config
        _resetProtection();

        if( isWhiteListed[sender]  || isWhiteListed[recipient] ){
            super._transfer(sender,recipient,amount);
            return;
        }
        if(isNotOpen){
            return;
        }

        currentPrice = IERC20(usdt).balanceOf(uniswapV2Pair).mul(10 ** 18).div(IERC20(address(this)).balanceOf(uniswapV2Pair));
        for (uint256 i = priceStage.length - 1; i >= 0; i--) {
            if (currentPrice > priceStage[i]) {
                buyFee = SafeMath.min(buyFee, buyFees[i]);
                sellFee = SafeMath.min(sellFee, sellFees[i]);
                transferFee = SafeMath.min(transferFee, transferFees[i]);
                break;
            }
            if (i == 0) {
                break;
            }
        }
        uint256 tax =0;
        if (sender == uniswapV2Pair) {      // buy
            tax = amount.mul(buyFee).div(100);
            super._transfer(sender, recipient, amount);
            super._transfer(recipient, _swapAddress, tax);
        } else if (recipient == uniswapV2Pair) {        // sell
            tax = amount.mul(sellFee.add(extraSellFee)).div(100);
            super._transfer(sender,recipient,amount );
            super._transfer(sender,_swapAddress, tax );
        } else {        // transfer

                uint256 transferTax = amount.mul(transferFee).div(100);
                uint destroyFeeAmount =  transferTax.mul(_destroyFee)/100;             
                uint nftFeeAmount = transferTax.mul(_nftFee)/100 ;       
                uint auctionFeeAmount = transferTax.mul(_auctionFee)/100 ;         

                amount = amount.sub(transferTax);
                super._transfer(sender,recipient,amount);

                if(destroyFeeAmount>0) {
                    super._transfer(sender,_deadAddress,destroyFeeAmount);
                }

                if(nftFeeAmount>0) {
                    super._transfer(sender,_nftAddress,nftFeeAmount);
                }

                if(auctionFeeAmount>0) {
                    super._transfer(sender,_auctionAddress,auctionFeeAmount);
                }

        }

    }



    mapping (address => bool) public isWhiteListed;

    function addWhiteList (address _user) public onlyOwner {
        isWhiteListed[_user] = true;
        emit AddedWhiteList(_user);
    }

    function removeWhiteList (address _clearedUser) public onlyOwner {
        isWhiteListed[_clearedUser] = false;
        emit RemovedWhiteList(_clearedUser);
    }

    event AddedWhiteList(address indexed _user);

    event RemovedWhiteList(address indexed _user);

}



interface IUniswapV2Factory {
    event PairCreated(
        address indexed token0,
        address indexed token1,
        address pair,
        uint256
    );

    function feeTo() external view returns (address);

    function feeToSetter() external view returns (address);

    function getPair(address tokenA, address tokenB)
    external
    view
    returns (address pair);

    function allPairs(uint256) external view returns (address pair);

    function allPairsLength() external view returns (uint256);

    function createPair(address tokenA, address tokenB)
    external
    returns (address pair);

    function setFeeTo(address) external;

    function setFeeToSetter(address) external;
}

interface IUniswapV2Pair {
    event Approval(
        address indexed owner,
        address indexed spender,
        uint256 value
    );
    event Transfer(address indexed from, address indexed to, uint256 value);

    function name() external pure returns (string memory);

    function symbol() external pure returns (string memory);

    function decimals() external pure returns (uint8);

    function totalSupply() external view returns (uint256);

    function balanceOf(address owner) external view returns (uint256);

    function allowance(address owner, address spender)
    external
    view
    returns (uint256);

    function approve(address spender, uint256 value) external returns (bool);

    function transfer(address to, uint256 value) external returns (bool);

    function transferFrom(
        address from,
        address to,
        uint256 value
    ) external returns (bool);

    function DOMAIN_SEPARATOR() external view returns (bytes32);

    function PERMIT_TYPEHASH() external pure returns (bytes32);

    function nonces(address owner) external view returns (uint256);

    function permit(
        address owner,
        address spender,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;

    event Mint(address indexed sender, uint256 amount0, uint256 amount1);
    event Burn(
        address indexed sender,
        uint256 amount0,
        uint256 amount1,
        address indexed to
    );
    event Swap(
        address indexed sender,
        uint256 amount0In,
        uint256 amount1In,
        uint256 amount0Out,
        uint256 amount1Out,
        address indexed to
    );
    event Sync(uint112 reserve0, uint112 reserve1);

    function MINIMUM_LIQUIDITY() external pure returns (uint256);

    function factory() external view returns (address);

    function token0() external view returns (address);

    function token1() external view returns (address);

    function getReserves()
    external
    view
    returns (
        uint112 reserve0,
        uint112 reserve1,
        uint32 blockTimestampLast
    );

