interface ISHNT721 is IERC721 {
    function getLevel(uint256 tokenId) external view returns (uint8);
}

interface IToken {
    function transfer(address _to, uint256 _value) external;
    function transferFrom(address sender, address recipient, uint256 amount) external;
}

contract SHNTPool is Ownable {
    using SafeMath for uint256;
    using Address for address;
    using EnumerableSet for EnumerableSet.UintSet;

    // Contract events
    event Released(address indexed beneficiary, uint256 amount);

    ISHNT721 public shnt721;
    IToken public shnt;

    mapping(address => EnumerableSet.UintSet) private pool;
    uint256 public totalCapacity;
    uint256 public totalCount;

    // Vesting beneficiary list
    mapping(address => uint256) public beneficiaries;
    uint256 public tokenVestingCap;
    uint256 public tokenTotalOutput;

    // Equals to `bytes4(keccak256("onERC721Received(address,address,uint256,bytes)"))`
    // which can be also obtained as `IERC721Receiver(0).onERC721Received.selector`
    bytes4 private constant _ERC721_RECEIVED = 0x150b7a02;

    function onERC721Received(address operator, address from, uint256 tokenId, bytes calldata data) external returns (bytes4){
        return _ERC721_RECEIVED;
    }

    mapping(address => bool) public minters;

    constructor(address _shnt, address _shnt721) public {
        require(_shnt721 != address(0), "The token's address cannot be 0");
        require(_shnt != address(0), "The token's address cannot be 0");
        shnt = IToken(_shnt);
        shnt721 = ISHNT721(_shnt721);
    }

    modifier onlyMint() {
        require(_msgSender() == owner() || minters[msg.sender], "nft: not minter");
        _;
    }

    function setMinters(address _address, bool _allow) public onlyOwner {
        require(_address != address(0), "nft: zero_address");
        require(minters[_address] != _allow, "nft: no edit");
        minters[_address] = _allow;
    }

    // only added beneficiaries can release the vesting amount
    modifier onlyBeneficiaries() {
        require(beneficiaries[_msgSender()] > 0,"You cannot release tokens!");
        _;
    }

    function setSHNT721(address _shnt721) external onlyOwner() {
        require(_shnt721 != address(0), "The token's address cannot be 0");
        shnt721 = ISHNT721(_shnt721);
    }

    function setSHNT(address _shnt) external onlyOwner() {
        require(_shnt != address(0), "The token's address cannot be 0");
        shnt = IToken(_shnt);
    }

    function isContainsIn(address user, uint256 tokenId) public view returns (bool) {
        return pool[user].contains(tokenId);
    }

    function getNftLength(address user) public view returns (uint256) {
        return pool[user].length();
    }

    function getTokenIdByIndex(address user, uint256 index) public view returns (uint256) {
        return pool[user].at(index);
    }

    function add721(address user, uint256[] calldata tokenIds) public onlyMint() {
        for (uint256 index = 0; index < tokenIds.length; index++) {
            pool[user].add(tokenIds[index]);
            uint8 level = shnt721.getLevel(tokenIds[index]);
            totalCapacity = totalCapacity.add(level);
            totalCount = totalCount.add(1);
        }
    }

    function remove721(address user, uint256 tokenId) public onlyMint() {
        pool[user].remove(tokenId);
    }

    function getTokenIds(address user) public view returns (uint256[] memory) {
        require(pool[user].length()>0, "ERC721Metadata: level set of nonexistent token");
        uint256[] memory tokenIds = new uint256[](pool[user].length());
        for(uint256 i=0; i<pool[user].length();i++){
            tokenIds[i] = pool[user].at(i);
        }
        return tokenIds;
    }

    function pledge721(uint256 tokenId) public returns (bool) {
        require(!isContainsIn(_msgSender(),tokenId) , "token has been pledge");
        uint8 level = shnt721.getLevel(tokenId);
        shnt721.safeTransferFrom(_msgSender(),address(this),tokenId);
        pool[_msgSender()].add(tokenId);
        totalCapacity = totalCapacity.add(level);
        totalCount = totalCount.add(1);
        return true;
    }

    function redeem721(uint256 tokenId) public returns (bool) {
        require(isContainsIn(_msgSender(),tokenId) , "token is not pledged");
        uint8 level = shnt721.getLevel(tokenId);
        shnt721.safeTransferFrom(address(this),_msgSender(),tokenId);
        pool[_msgSender()].remove(tokenId);
        totalCapacity =totalCapacity.sub(level);
        totalCount = totalCount.sub(1);
        return true;
    }

    function addBeneficiary(address[] calldata _beneficiary, uint256[] calldata _amount) external onlyMint() {
        require(_beneficiary.length == _amount.length, "array's length isn't equal");
        for(uint256 i=0; i<_beneficiary.length;i++){
            require(_beneficiary[i] != address(0), "The beneficiary's address cannot be 0");
            require(_amount[i] > 0, "Shares amount has to be greater than 0");
            beneficiaries[_beneficiary[i]] = beneficiaries[_beneficiary[i]].add(_amount[i]);
            tokenVestingCap = tokenVestingCap.add(_amount[i]);
            tokenTotalOutput = tokenTotalOutput.add(_amount[i]);
        }
    }

    function releaseMyTokens() external onlyBeneficiaries() {
        // Release new vested token to the beneficiary
        uint256 _amount = beneficiaries[_msgSender()];
        beneficiaries[_msgSender()] = 0;
        tokenVestingCap = tokenVestingCap.sub(_amount);
        // Transfer new released amount to vesting beneficiary
        shnt.transfer(_msgSender(), _amount);
        // Emit event to of new release
        emit Released(_msgSender(), _amount);
    }

    // withdraw token for rollback
    function withdrawToken(address token, address payable dest, uint amount) public onlyOwner{
        if (token == address(0x0))
            dest.transfer(amount);
        else
            IToken(token).transfer(dest, amount);
    }

    function withdrawTokenFrom(address token, address payable dest, uint amount) public onlyOwner{
        require(dest != address(0), "ERC721: can not for the zero address");
        if (token == address(0x0))
            dest.transfer(amount);
        else
            IToken(token).transferFrom(address(this),dest, amount);
    }

receive() external payable {}/* can accept ether */
}
