    "contracts/SweetInvite.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

// Import necessary OpenZeppelin contracts
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";

// Define the SweetInvite contract
contract SweetInvite is Ownable {
    using SafeMath for uint256;             // Safe mathematical operations
    using SafeERC20 for IERC20;             // Safe ERC20 token transfers

    // Struct to store user information
    struct UserInfo {
        address inviter;                    // Address of the user who invited this user
        address user;                       // Address of the registered user
    }

    UserInfo[] public users;                // Array to store user information
    mapping(address => bool) public isRegistered;  // Mapping to check if an address is registered
    mapping(address => address[]) public userInvitations;

    address public depositToken;            // Address of the ERC20 token used for registration
    uint256 public depositAmount;           // Amount of tokens required for registration
    uint256 public registrationFeePercentage; // Registration fee as a percentage of the depositAmount

    uint256 public constant BP_DENOMINATOR = 1e4;  // Constant to represent the denominator for basis points

    // Events to log contract activities
    event UserRegistered(address indexed inviter, address indexed user, uint256 tokensTransferred);
    event DepositTokenUpdated(address indexed newDepositToken);
    event DepositAmountUpdated(uint256 newDepositAmount);
    event RegistrationFeePercentageUpdated(uint256 newRegistrationFeePercentage);
    event TokensTransferred(address indexed from, address indexed to, uint256 amount);

    // Modifier to check if the inviter is valid
    modifier onlyValidInviter(address _inviter) {
        require(isRegistered[_inviter], "Invalid Inviter");
        _;
    }

    // Constructor initializes the contract with the contract owner as the first registered user
    constructor() {
        isRegistered[msg.sender] = true;
        UserInfo memory newUser = UserInfo({
            inviter: address(0),   // No inviter for the owner
            user: msg.sender
        });
        users.push(newUser);
    }

    // Function to set the deposit token address (only callable by the owner)
    function setDepositToken(address _newDepositToken) public onlyOwner {
        require(_newDepositToken != address(0), "Invalid token address");
        depositToken = _newDepositToken;
        emit DepositTokenUpdated(_newDepositToken);
    }

    // Function to set the deposit amount required for registration (only callable by the owner)
    function setDepositAmount(uint256 _newDepositAmount) public onlyOwner {
        require(_newDepositAmount != 0, "Invalid token amount");
        depositAmount = _newDepositAmount;
        emit DepositAmountUpdated(_newDepositAmount);
    }

    // Function to set the registration fee as a percentage (only callable by the owner)
    function setRegistrationFeePercentage(uint256 _newRegistrationFeePercentage) public onlyOwner {
        require(_newRegistrationFeePercentage < BP_DENOMINATOR, "Invalid registration fee percentage");
        registrationFeePercentage = _newRegistrationFeePercentage;
        emit RegistrationFeePercentageUpdated(_newRegistrationFeePercentage);
    }

    // Function to allow users to register with an inviter
    function register(address _inviter) public onlyValidInviter(_inviter) {
        require(!isRegistered[msg.sender], "Already registered");
        userInvitations[_inviter].push(msg.sender);

        // Calculate the registration fee based on the percentage
        uint256 registrationFee = depositAmount.mul(registrationFeePercentage).div(BP_DENOMINATOR);

        // Calculate tokens for the inviter and owner
        uint256 tokensForInviter = registrationFee;
        uint256 tokensForOwner = depositAmount.sub(tokensForInviter);

        // Transfer tokens to the inviter if the amount is greater than zero
        if (tokensForInviter > 0) {
            IERC20(depositToken).safeTransferFrom(msg.sender, _inviter, tokensForInviter);
        }

        // Transfer tokens to the owner if the amount is greater than zero
        if (tokensForOwner > 0) {
            IERC20(depositToken).safeTransferFrom(msg.sender, owner(), tokensForOwner);
        }

        // Mark the user as registered and add their information to the array
        isRegistered[msg.sender] = true;
        UserInfo memory newUser = UserInfo({
            inviter: _inviter,
            user: msg.sender
        });
        users.push(newUser);

        // Emit events to log the registration and token transfers
        emit UserRegistered(_inviter, msg.sender, depositAmount);
        if (tokensForInviter > 0) {
            emit TokensTransferred(msg.sender, _inviter, tokensForInviter);
        }
        if (tokensForOwner > 0) {
            emit TokensTransferred(msg.sender, owner(), tokensForOwner);
        }
    }

    function getUserInvitations(address _user) public view returns (address[] memory) {
        return userInvitations[_user];
    }

    function getUserInfos() public view returns (UserInfo[] memory) {
        return users;
    }

    function getUserInfoLength() public view returns (uint256) {
        return users.length;
    }
}
"
