    event Pool__Transfer(
        address account,
        TransferType transferType,
        uint256 amount,
        uint256 pool
    );
    event Pool__Configured(
        bool state,
        uint256 apr,
        uint256 poolId,
        PoolType poolType,
        uint256 rewardRate,
        uint256 fixedDuration,
        uint256 totalSupply,
        uint256 totalClaimedReward,
        address stakeToken,
        address rewardToken,
        uint256 rewardBalance
    );

    // =========== ENUMS =========== //

    /** @dev Pool type */
    enum PoolType {
        FIXED,
        FLEXIBLE
    }

    /** @dev Transfer type */
    enum TransferType {
        STAKE,
        UNSTAKE,
        CLAIM,
        BONUS
    }

    // =========== STRUCTS =========== //

    /** @dev Position model */
    struct Position {
        uint256 stakedOn;
        uint256 principal;
        uint256 claimedReward;
        uint256 lastClaimedOn;
    }

    /** @dev Investor model */
    struct Investor {
        uint256 refId;
        uint256 referral;
        uint256 lastBonusClaimedOn;
        mapping(address => uint256) totalIn;
        mapping(address => uint256) totalOut;
        mapping(address => uint256) totalClaimed;
        mapping(address => uint256) unClaimedBonus;
    }

    /** @dev Pool model */
    struct Pool {
        bool state;
        uint256 apr;
        uint256 poolId;
        PoolType poolType;
        uint256 rewardRate;
        uint160 fixedDuration;
        uint256 totalClaimedReward;
        uint256 totalSupply;
        address stakeToken;
        address rewardToken;
        uint256 rewardBalance;
        mapping(address => Position) positions;
    }

    // =========== ENTRYPOINT =========== //

    constructor() ReentrancyGuard() Ownable2Step() {}

    // =========== FUNCTIONS =========== //

    function initRefId() external {
        address account = _msgSender();

        if (_investors[account].refId == 0) {
            _refId.increment();
            uint256 refId = _refId.current();

            _investors[account].refId = refId;
            _referral[refId] = account;
        }
    }

    /** @dev pool id and amount to stake */
    function stake(
        uint256 pool,
        uint256 principal,
        uint256 referral
    ) external whenNotPaused nonReentrant approve(pool) {
        // keep track of how much this user has staked
        // keep track of how much token we have total
        // transfer the tokens to this contract
        /** @notice Be mindful of reentrancy attack here */

        if (!_pools[pool].state) {
            revert Pool__Error("Error: Pool has been paused");
        }

        address account = _msgSender();

        /** check if account can stake in the pool */
        _canStake(pool, account);

        /** @dev add investor's position */
        _pools[pool].positions[account].principal = principal;
        _pools[pool].positions[account].stakedOn = block.timestamp;

        address stakeToken = _pools[pool].stakeToken;

        if (
            _investors[account].referral == 0 &&
            referral != _investors[account].refId
        ) {
            _investors[account].referral = referral;
        }

        _investors[account].totalIn[stakeToken] += principal;
        _pools[pool].totalSupply += principal;

        _updatePool(pool);

        ERC20 _stakingToken = ERC20(stakeToken);

        /** transfer token to smart contract */
        _stakingToken.safeTransferFrom(account, address(this), principal);

        emit Pool__Transfer(account, TransferType.STAKE, principal, pool);
    }

    /** @dev pool id and amount to unstake */
    function unstake(
        uint256 pool
    ) external whenNotPaused nonReentrant approve(pool) {
        address account = _msgSender();

        uint256 principal = _pools[pool].positions[account].principal;

        /** @dev claim all position earned tokens before unstaking */
        _claim(pool, account, true);

        if (_pools[pool].poolType == PoolType.FIXED) {
            uint256 stakedOn = _pools[pool].positions[account].stakedOn;
            uint256 duration = ONE_DAY.mul(_pools[pool].fixedDuration);

            if (block.timestamp < (stakedOn.add(duration))) {
                revert Staking__Error("Error: Deadline not reached");
            }
        }

        /** @dev delete investor's position */
        delete _pools[pool].positions[account];

        address stakeToken = _pools[pool].stakeToken;

        _investors[account].totalOut[stakeToken] += principal;
        _pools[pool].totalSupply -= principal;

        _updatePool(pool);

        /** @dev transfer token to account */
        ERC20 _stakingToken = ERC20(stakeToken);

        _stakingToken.safeTransfer(account, principal);
        emit Pool__Transfer(account, TransferType.UNSTAKE, principal, pool);
    }

    /** @dev pool id and amount to unstake */
    function claim(
        uint256 pool
    ) external whenNotPaused nonReentrant approve(pool) {
        address account = _msgSender();
        _claim(pool, account, false);
    }

    /** @dev pool id and amount to unstake */
    function claimBonus(
        address rewardToken
    ) external whenNotPaused nonReentrant {
        address account = _msgSender();

        uint256 bonus = _investors[account].unClaimedBonus[rewardToken];
        _investors[account].unClaimedBonus[rewardToken] = 0;

        uint256 _lastClaimedOn = _investors[account].lastBonusClaimedOn;

        if (block.timestamp < (_lastClaimedOn.add(ONE_DAY))) {
            revert Pool__Error("Error: Claim too soon");
        }

        ERC20 _rewardToken = ERC20(rewardToken);

        if (bonus > 0) {
            /** transfer reward token to account */
            _rewardToken.safeTransfer(account, bonus);
            emit Pool__Transfer(account, TransferType.BONUS, bonus, 0);
        }

        _investors[account].lastBonusClaimedOn = block.timestamp;
    }

    /** @dev diff between all claimable reward and claimed reward */
    function claimable(
        uint256 pool,
        address account
    ) public view returns (uint256) {
        if (!_pools[pool].state) return 0;

        uint256 allClaimable = _allClaimable(pool, account);
        uint256 claimed = _pools[pool].positions[account].claimedReward;

        if (claimed >= allClaimable) return 0;
        return (allClaimable.sub(claimed));
    }

    /** @dev get an investor account */
    function investor(
        address account,
        address tokenIn,
        address tokenOut,
        address tokenClaimed,
        address unClaimedBonus
    )
        external
        view
        returns (uint256, uint256, uint256, uint256, uint256, uint256)
    {
        Investor storage _investor = _investors[account];
        return (
            _investor.refId,
            _investor.lastBonusClaimedOn,
            _investor.totalIn[tokenIn],
            _investor.totalOut[tokenOut],
            _investor.totalClaimed[tokenClaimed],
            _investor.unClaimedBonus[unClaimedBonus]
        );
    }

    /** @dev get an investor position in a pool */
    function position(
        uint256 pool,
        address account
    ) external view returns (uint256, uint256, uint256, uint256) {
        Position storage _position = _pools[pool].positions[account];
        return (
            _position.principal,
            _position.claimedReward,
            _position.stakedOn,
            _position.lastClaimedOn
        );
    }

    /** @dev get total claimed of a token accross all pools */
    function totalClaimed(address rewardToken) external view returns (uint256) {
        uint256 result = 0;
        for (uint256 index = 1; index <= _poolId.current(); index++) {
            if (_pools[index].rewardToken != rewardToken) continue;
            result += _pools[index].totalClaimedReward;
        }
        return result;
    }

    /** @dev get total supply of a token accross all pools */
    function totalSupply(address stakeToken) external view returns (uint256) {
        uint256 result = 0;
        for (uint256 index = 1; index <= _poolId.current(); index++) {
            if (_pools[index].stakeToken != stakeToken) continue;
            result += _pools[index].totalSupply;
        }
        return result;
    }

    // =========== PRIVATE FUNCTIONS =========== //

    function _claim(
        uint256 pool,
        address account,
        bool force
    ) private returns (uint256, uint256) {
        uint256 reward = claimable(pool, account);

        uint256 _rewardBalance = _pools[pool].rewardBalance;
        uint256 _lastClaimedOn = _pools[pool].positions[account].lastClaimedOn;

        if (!force && block.timestamp < (_lastClaimedOn.add(ONE_DAY))) {
            revert Pool__Error("Error: Claim too soon");
        }

        _pools[pool].positions[account].claimedReward += reward;
        _pools[pool].positions[account].lastClaimedOn = block.timestamp;
        _pools[pool].totalClaimedReward += reward;

        address rewardToken = _pools[pool].rewardToken;

        _investors[account].totalClaimed[rewardToken] += reward;
        _rewardBalance -= reward;

        _updatePool(pool);

        uint256 bonus = _refPercent(reward);

        /** Insufficient reward funds */
        if (_rewardBalance < reward) {
            revert Pool__Error("Error: Insufficient reward funds");
        }

        ERC20 _rewardToken = ERC20(rewardToken);

        if (reward > 0) {
            /** transfer reward token to account */
            _rewardToken.safeTransfer(account, reward);
            emit Pool__Transfer(account, TransferType.CLAIM, reward, pool);

            /** credit referral their bonus */
            uint256 refId = _investors[account].referral;
            address referral = _referral[refId];

            if (referral != address(0)) {
                _investors[referral].unClaimedBonus[rewardToken] += bonus;
            }
        }

        return (reward, bonus);
    }

    function _refPercent(uint256 amount) private view returns (uint256) {
        if (amount == 0) return 0;
        return (amount * _refReward) / PERCENT;
    }

    function _allClaimable(
        uint256 pool,
        address account
    ) private view returns (uint256) {
        uint256 principal = _pools[pool].positions[account].principal;
        uint256 stakedOn = _pools[pool].positions[account].stakedOn;
        uint256 duration = ONE_DAY.mul(_pools[pool].fixedDuration);

        if (_pools[pool].poolType == PoolType.FIXED) {
            if (block.timestamp >= stakedOn.add(duration)) {
                return
                    _pools[pool].rewardRate.mul(duration).mul(principal).div(
                        REWARD_RATE_DENOMINATOR
                    );
            } else {
                return
                    _pools[pool]
                        .rewardRate
                        .mul(block.timestamp.sub(stakedOn))
                        .mul(principal)
                        .div(REWARD_RATE_DENOMINATOR);
            }
        } else if (_pools[pool].poolType == PoolType.FLEXIBLE) {
            return
                _pools[pool]
                    .rewardRate
                    .mul(block.timestamp.sub(stakedOn))
                    .mul(principal)
                    .div(REWARD_RATE_DENOMINATOR);
        }

        return 0;
    }

    function _updatePool(uint256 pool) private {
        emit Pool__Configured(
            _pools[pool].state,
            _pools[pool].apr,
            _pools[pool].poolId,
            _pools[pool].poolType,
            _pools[pool].rewardRate,
            _pools[pool].fixedDuration,
            _pools[pool].totalSupply,
            _pools[pool].totalClaimedReward,
            _pools[pool].stakeToken,
            _pools[pool].rewardToken,
            _pools[pool].rewardBalance
        );
    }

    function _calculateApr(uint256 rewardRate) private pure returns (uint256) {
        return rewardRate.mul(ONE_DAY).mul(365);
    }

    /** checks if account already has a position or not */
    function _canStake(uint256 pool, address account) private view {
        if (_pools[pool].positions[account].stakedOn > 0) {
            revert Staking__Error("Error: Account already staked");
        }
    }

    // =========== ADMIN FUNCTIONS =========== //

    function createPool(
        PoolType poolType,
        uint256 rewardRate,
        uint160 fixedDuration,
        address stakeToken,
        address rewardToken
    ) external onlyOwner {
        _poolId.increment();

        uint256 poolId = _poolId.current();
        uint256 apr = _calculateApr(rewardRate);

        Pool storage pool = _pools[poolId];
        pool.apr = apr;
        pool.poolId = poolId;
        pool.poolType = poolType;
        pool.rewardRate = rewardRate;
        pool.fixedDuration = fixedDuration;
        pool.stakeToken = stakeToken;
        pool.rewardToken = rewardToken;

        emit Pool__Configured(
            true,
            apr,
            poolId,
            poolType,
            rewardRate,
            fixedDuration,
            0,
            0,
            stakeToken,
            rewardToken,
            0
        );
    }

    function setPoolState(uint256 pool, bool state) external onlyOwner {
        _pools[pool].state = state;
        _updatePool(pool);
    }

    /** @dev add pool to approved pools */
    function addPool(uint256 pool) external onlyOwner {
        for (uint256 index = 0; index < _approvedPools.length; index++) {
            if (_approvedPools[index] == pool) {
                revert Pool__Error("Error: Pool has been added");
            }
        }

        _approvedPools.push(pool);

        emit Pool__Added(pool);
    }

    /** @dev remove pool from approved pools */
    function removePool(uint256 pool) external onlyOwner {
        for (uint256 index = 0; index < _approvedPools.length; index++) {
            if (_approvedPools[index] == pool) {
                emit Pool__Removed(_approvedPools[index]);
                delete _approvedPools[index];
                break;
            }
        }
    }

    /** @dev for depositing reward tokens */
    function deposit(uint256 pool, uint256 amount) external onlyOwner {
        address account = _msgSender();

        ERC20 _rewardToken = ERC20(_pools[pool].rewardToken);

        _rewardToken.safeTransferFrom(account, address(this), amount);
        _pools[pool].rewardBalance += amount;

        emit Pool__Deposit(amount);
    }

    /** @dev for withdrawing reward tokens */
    function withdraw(uint256 pool, uint256 amount) external onlyOwner {
        address account = _msgSender();

        uint256 _rewardBalance = _pools[pool].rewardBalance;

        if (_rewardBalance < amount) {
            revert Pool__Error("Error: Insufficient pool funds");
        }

        ERC20 _rewardToken = ERC20(_pools[pool].rewardToken);

        _rewardToken.safeTransfer(account, amount);
        _pools[pool].rewardBalance -= amount;

        emit Pool__Withdraw(amount);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    // =========== MODIFIERS =========== //

    /** @dev checks if a pool is approved before interacting */
    modifier approve(uint256 pool) {
        bool exists = false;
        for (uint256 index = 0; index < _approvedPools.length; index++) {
            if (_approvedPools[index] == pool) {
                exists = true;
                break;
            }
        }

        if (!exists) {
            revert Pool__Error("Error: Pool is not approve");
        }

        _;
    }
}
