{"Owned.sol":{"content":"pragma solidity ^0.5;

contract Owned{
    address public owner;

    function owned() public {
        owner = msg.sender;
    }

    modifier onlyOwner {
        require(msg.sender == owner, "Invalid Owner");
        _;
    }

    // to transfer contract owenership 
    function transferOwnership(address newOwner) public onlyOwner {
        owner = newOwner;
    }
}"},"StandardProduct.sol":{"content":"pragma solidity ^0.5;

import './Owned.sol';

contract ProductStore is Owned{
    struct productDetails{
        string productData;
        string extraData;
    }

    mapping(bytes32 => productDetails) Product;
    mapping(address => bool) authorizedCaller;

    // To store product data 
    event StoreProduct(bytes32 indexed proudctHash, string productData, string extraData);
    // Set the authorized user to access to this contract flag to true
    event AuthorizedCaller(address _caller);
    // Set the authorized user to access to this contract flag to false
    event DeAuthorizedCaller(address _caller);

    function authorizeCaller(address _caller) public onlyOwner returns (bool) {
        authorizedCaller[_caller] = true;
        emit AuthorizedCaller(_caller);
        return true;
    }

    function deAuthorizeCaller(address _caller) public onlyOwner returns (bool)
    {
        authorizedCaller[_caller] = false;
        emit DeAuthorizedCaller(_caller);
        return true;
    }

    function storeProduct(bytes32 _productHash, string memory _productData, string memory _extraData) public returns(bool){
        Product[_productHash].productData = _productData;
        Product[_productHash].extraData = _extraData;

        emit StoreProduct(_productHash, _productData, _extraData);
        return true;
    }

    function getProduct(bytes32 _productHash) public view returns(string memory productData, string memory extraData){
        return(Product[_productHash].productData, Product[_productHash].extraData);
    }

}"}}
