//VVT Token
contract VVT is ERC223("VIPSVERSE Token", "VVT"), Ownable {
    
    address public farm; //12% of tokens will be mint yearly by farming contract. 
    address public preBurnAddress; //we will constantly burn tokens. We will collect it on special address from different sources and burn it on weekly/monthly basis.

    constructor() {
        _mint(_msgSender(), 10000000 * 10**18);//10M tokens will be minted initially on BSC
    }

    function rescueERC20(address token, address to) external onlyOwner {
        uint256 value = IERC223(token).balanceOf(address(this));
        IERC223(token).transfer(to, value);
    }
    
    function mint(uint256 _amount) public  {
        require(_msgSender() == farm, "Only Farm contract can mint new tokens");
        _mint(farm, _amount);
    }

    function burn(uint256 _amount) public {
        require(_msgSender() == preBurnAddress, "Tokens can be burned only by team");
        _burn(preBurnAddress, _amount);
    }
    
    /*
    We don't know final farm contract address at time of token contract deployment/ICO start,
    so we need ability to change farm contract address in future. 
    */
    function setFarmAddress(address _farm) public onlyOwner{
        farm = _farm;
    }
    function setPreBurnAddress(address _preBurnAddress) public onlyOwner{
        preBurnAddress = _preBurnAddress;
    }

}
