interface IUniswapV2Router01 {
    function factory() external view returns (address);

    function WETH() external view returns (address);

    function addLiquidity(
        address tokenA,
        address tokenB,
        uint256 amountADesired,
        uint256 amountBDesired,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountA, uint256 amountB, uint256 liquidity);

    function addLiquidityETH(
        address token,
        uint256 amountTokenDesired,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    ) external payable returns (uint256 amountToken, uint256 amountETH, uint256 liquidity);

    function removeLiquidity(
        address tokenA,
        address tokenB,
        uint256 liquidity,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountA, uint256 amountB);

    function removeLiquidityETH(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountToken, uint256 amountETH);

    function removeLiquidityWithPermit(
        address tokenA,
        address tokenB,
        uint256 liquidity,
        uint256 amountAMin,
        uint256 amountBMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountA, uint256 amountB);

    function removeLiquidityETHWithPermit(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountToken, uint256 amountETH);

    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapTokensForExactTokens(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactETHForTokens(uint256 amountOutMin, address[] calldata path, address to, uint256 deadline)
        external
        payable
        returns (uint256[] memory amounts);

    function swapTokensForExactETH(
        uint256 amountOut,
        uint256 amountInMax,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapExactTokensForETH(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);

    function swapETHForExactTokens(uint256 amountOut, address[] calldata path, address to, uint256 deadline)
        external
        payable
        returns (uint256[] memory amounts);

    function quote(uint256 amountA, uint256 reserveA, uint256 reserveB) external pure returns (uint256 amountB);

    function getAmountOut(uint256 amountIn, uint256 reserveIn, uint256 reserveOut)
        external
        pure
        returns (uint256 amountOut);

    function getAmountIn(uint256 amountOut, uint256 reserveIn, uint256 reserveOut)
        external
        pure
        returns (uint256 amountIn);

    function getAmountsOut(uint256 amountIn, address[] calldata path)
        external
        view
        returns (uint256[] memory amounts);

    function getAmountsIn(uint256 amountOut, address[] calldata path)
        external
        view
        returns (uint256[] memory amounts);
}

interface IUniswapV2Router is IUniswapV2Router01 {
    function removeLiquidityETHSupportingFeeOnTransferTokens(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline
    ) external returns (uint256 amountETH);

    function removeLiquidityETHWithPermitSupportingFeeOnTransferTokens(
        address token,
        uint256 liquidity,
        uint256 amountTokenMin,
        uint256 amountETHMin,
        address to,
        uint256 deadline,
        bool approveMax,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external returns (uint256 amountETH);

    function swapExactTokensForTokensSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;

    function swapExactETHForTokensSupportingFeeOnTransferTokens(
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external payable;

    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external;
}

IERC20 constant LF = IERC20(0x16F61Fc62f1DB1f599B8D2560d42597b68713149);
IERC20 constant USDT = IERC20(0x55d398326f99059fF775485246999027B3197955);
IUniswapV2Router constant ROUTER = IUniswapV2Router(0x10ED43C718714eb63d5aA57B78B54704E256024E);

contract Staking is Owned {
    bool private initialized;

    struct Pair {
        uint256 amountUsdt;
        uint256 amountLafei;
    }

    mapping(address => Pair[]) public stakedOf;

    event Staked(address user, uint256 amountU, uint256 amountLF, uint256 blockTime);

    constructor() Owned(msg.sender) {}

    function initialize() public {
        require(!initialized, "Contract instance has already been initialized");
        initialized = true;
        owner = msg.sender;
        USDT.approve(address(ROUTER), type(uint256).max);
    }

    function stake_lf(uint256 amount) external {
        require(amount > 0, "b0");
        require(LF.transferFrom(msg.sender, address(this), amount), "ok");

        uint256 amount_usdt = getAmountsOut(amount);
        Pair[] storage pair = stakedOf[msg.sender];
        pair.push(Pair({amountUsdt: amount_usdt, amountLafei: amount}));
        emit Staked(msg.sender, amount_usdt, amount, block.timestamp);
    }

    function stake_usdt(uint256 amount, uint256 amountOutMin) external {
        require(amount > 0, "b0");
        require(USDT.transferFrom(msg.sender, address(this), amount), "ok");

        uint256 bal_lf_before = LF.balanceOf(address(this));
        address[] memory path = new address[](2);
        path[0] = address(USDT);
        path[1] = address(LF);
        ROUTER.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            amount, amountOutMin, path, address(this), block.timestamp
        );
        uint256 bal_lf_after = LF.balanceOf(address(this));
        uint256 stakeAmount = bal_lf_after - bal_lf_before;

        Pair[] storage pair = stakedOf[msg.sender];
        pair.push(Pair({amountUsdt: amount, amountLafei: stakeAmount}));
        emit Staked(msg.sender, amount, stakeAmount, block.timestamp);
    }

    function getAmountsOut(uint256 amountIn) public view returns (uint256 amountOut) {
        uint256[] memory amounts;
        address[] memory path = new address[](2);
        path[0] = address(LF);
        path[1] = address(USDT);
        amounts = ROUTER.getAmountsOut(amountIn, path);
        amountOut = amounts[1];
    }

    function withdrawToken(address token, address to, uint256 amount) external onlyOwner {
        IERC20(token).transfer(to, amount);
    }
}
