// AstroProtocol.
contract AstroToken is BEP20('AstroProtocol', 'ASTRO') {

    address public LAIKA = 0x2a49De60cc6204c3AFC3e770FDB30A0554147519;
    address public deadAddress = 0x000000000000000000000000000000000000dEaD;
    address public stakingPoolAddress = 0x000000000000000000000000000000000000dEaD;

    uint256 numTokensSellToAddToLiquidity = 1;
    uint256 _maxTxAmount = 1;

    uint256 _astroBurnt = 0;
    uint256 _astroConvertedToLaika = 0;

    uint256 private _liquidityFee = 0; // Placeholder, set dynamically for buys and sells.
    uint256 private _buyLiquidityFee = 2;
    uint256 private _sellLiquidityFee = 4;
    uint256 private _previousLiquidityFee = _liquidityFee;

    uint256 private _burnFee = 0; // Placeholder, set dynamically for buys and sells.
    uint256 private _buyBurnFee = 2;
    uint256 private _sellBurnFee = 4;
    uint256 private _previousBurnFee = _burnFee;

    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapV2Pair;

    mapping (address => bool) private _isMinter;
    mapping (address => bool) private _isBlacklisted;
    mapping (address => bool) private _isExcludedFromFee;
    
    bool inSwapAndLiquify;
    bool public swapAndLiquifyEnabled = true;

    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );

    event LaikaBurnt(
        uint256 tokensBurned
    );

    event UpdateUniswapV2Router(address indexed newAddress, address indexed oldAddress);
    
    event Recovered(address _token, uint256 amount);

    constructor () public {
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
         // Create a uniswap pair for this new token
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        // set the rest of the contract variables
        uniswapV2Router = _uniswapV2Router;
        
        //exclude owner and this contract from fee
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;
    }

    function mint(address _to, uint256 amount) public returns (bool) {
        require(_isMinter[msg.sender], "not minter");
        _mint(_to, amount);
        return true;
    }

    /// @notice Creates `_amount` token to `_to`.
    function _mint(address _to, uint256 _amount) internal override {
         require(_to != address(0), 'BEP20: mint to the zero address');

        _totalSupply = _totalSupply.add(_amount);
        _balances[_to] = _balances[_to].add(_amount);
        emit Transfer(address(0), _to, _amount);
    }

    function setBlacklist(address account) public onlyOwner {
        _isBlacklisted[account] = !_isBlacklisted[account];
    }

    function setMinter(address account) public onlyOwner {
        _isMinter[account] = !_isMinter[account];
    }

    function setExcludeFromFee(address account) public onlyOwner {
        _isExcludedFromFee[account] = !_isExcludedFromFee[account];
    }

    function setStakingPool(address _stakingPoolAddress) public onlyOwner {
        stakingPoolAddress = _stakingPoolAddress;
    }

    function isMinter(address account) public view returns (bool) {
        return _isMinter[account];
    }
    function isBlacklisted(address account) public view returns (bool) {
        return _isBlacklisted[account];
    }

    function astroBurnt() public view returns (uint256) {
        return _astroBurnt;
    }

    function astroConvertedToLaika() public view returns (uint256) {
        return _astroConvertedToLaika;
    }

    function setNumTokensSellToAddToLiquidity(uint256 numTokens) public onlyOwner {
        numTokensSellToAddToLiquidity = numTokens;
    }

    function setMaxTxAmount(uint256 maxTransactionAmount) public onlyOwner {
        _maxTxAmount = maxTransactionAmount; // UPDATE THIS
    }

    function updateUniswapV2Router(address newAddress) public onlyOwner {
        require(newAddress != address(uniswapV2Router), "AstroProtocol: The router already has that address");
        emit UpdateUniswapV2Router(newAddress, address(uniswapV2Router));
        uniswapV2Router = IUniswapV2Router02(newAddress);
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        require(!_isBlacklisted[_msgSender()], "Sender is Blacklisted");
        require(!_isBlacklisted[recipient], "Receiver is Blacklisted");
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(amount > 0, "Transfer amount must be greater than zero");

        if(from != owner() && to != owner())
            require(amount <= _maxTxAmount, "Transfer amount exceeds the maxTxAmount.");

        // is the token balance of this contract address over the min number of
        // tokens that we need to initiate a swap + liquidity lock?
        // also, don't get caught in a circular liquidity event.
        // also, don't swap & liquify if sender is uniswap pair.
        uint256 contractTokenBalance = balanceOf(address(this));
        
        if(contractTokenBalance >= _maxTxAmount)
        {
            contractTokenBalance = _maxTxAmount;
        }
        
        bool overMinTokenBalance = contractTokenBalance >= numTokensSellToAddToLiquidity;
        if (
            overMinTokenBalance &&
            !inSwapAndLiquify &&
            from != uniswapV2Pair &&
            swapAndLiquifyEnabled
        ) {
            contractTokenBalance = numTokensSellToAddToLiquidity;
            //add liquidity
            swapAndLiquify(contractTokenBalance);
        }
        
        //indicates if fee should be deducted from transfer
        bool takeFee = true;
        
        //if any account belongs to _isExcludedFromFee account then remove the fee
        if(_isExcludedFromFee[from] || _isExcludedFromFee[to]){
            takeFee = false;
        }
        
        //transfer amount, it will take tax, burn, liquidity fee
        _tokenTransfer(from,to,amount,takeFee);
    }

    function swapAndLiquify(uint256 contractTokenBalance) private lockTheSwap {
        // split the contract balance into quarters
        uint256 quarter1 = contractTokenBalance.div(4); // Liquify Astro
        uint256 quarter2 = contractTokenBalance.div(4); // Liquify Astro
        uint256 quarter3 = contractTokenBalance.div(4); // Burn Laika
	uint256 quarter4 = contractTokenBalance.div(4); // Restock Staking Pool

        // capture the contract's current ETH balance.
        // this is so that we can capture exactly the amount of ETH that the
        // swap creates, and not make the liquidity event include any ETH that
        // has been manually sent to the contract
        uint256 initialBalance = address(this).balance;

        // swap tokens for ETH && Laika
        swapTokensForEth(quarter1);
        swapTokensForLaika(quarter3);

        // how much ETH did we just swap into?
        uint256 newBalance = address(this).balance.sub(initialBalance);

        // add liquidity to uniswap
        addLiquidity(quarter2, newBalance);
        
        emit SwapAndLiquify(quarter1, newBalance, quarter2);
	emit LaikaBurnt(quarter3);


	_transfer(address(this), stakingPoolAddress, quarter4);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value: ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            owner(),
            block.timestamp
        );
    }

    function swapTokensForLaika(uint256 _tokenAmount) private {
        // generate the uniswap pair path of weth -> busd
        address[] memory path = new address[](3);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        path[2] = LAIKA;

        _approve(address(this), address(uniswapV2Router), _tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            _tokenAmount,
            0, // accept any amount of tokens
            path,
            deadAddress,
            block.timestamp
        );
    }

    function removeAllFee() private {
        if(_liquidityFee == 0 && _burnFee == 0) return;
        
        _previousLiquidityFee = _liquidityFee;
        _previousBurnFee = _burnFee;
        
        _liquidityFee = 0;
        _burnFee = 0;
    }
    
    function restoreAllFee() private {
        _liquidityFee = _previousLiquidityFee;
        _burnFee = _previousBurnFee;
    }

    function _setBuyTaxes() internal {
        _liquidityFee = _buyLiquidityFee;
        _burnFee = _buyBurnFee;
    }

    function _setSellTaxes() internal {
        _liquidityFee = _sellLiquidityFee;
        _burnFee = _sellBurnFee;
    }

    //this method is responsible for taking all fee, if takeFee is true
    function _tokenTransfer(address sender, address recipient, uint256 amount,bool takeFee) private {
        if(!takeFee)
            removeAllFee();
        else if (sender == uniswapV2Pair)
            _setBuyTaxes();
        else
            _setSellTaxes();

        _transferStandard(sender, recipient, amount, takeFee);
        
        if(!takeFee)
            restoreAllFee();
    }

    function _transferStandard(address sender, address recipient, uint256 amount, bool takeFee) private {
        if (
            !takeFee
        ) {
            _balances[sender] = _balances[sender].sub(amount);
            _balances[recipient] = _balances[recipient].add(amount);

            emit Transfer(sender, recipient, amount);
        } else if (
            takeFee
        ) {
            uint256 totalFee = calculateBurnFee(amount).add(calculateLiquidityFee(amount));
            uint256 transferAmount = amount.sub(totalFee);
	
            _balances[address(this)] = _balances[address(this)].add(totalFee);
            emit Transfer(sender, address(this), totalFee); // Transparency is the key to success.

	    _balances[sender] = _balances[sender].sub(amount);
            _balances[recipient] = _balances[recipient].add(transferAmount);

            emit Transfer(sender, recipient, transferAmount);
        }
    }

    function calculateBurnFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_burnFee).div(100);
    }

    function calculateLiquidityFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_liquidityFee).div(100);
    }

    //to recieve ETH from uniswapV2Router when swapping
    receive() external payable {}

    // Emergency only - Recover BEP20 Tokens
    function recoverToken(address _token, uint256 amount) external virtual onlyOwner {
        IBEP20(_token).transfer(owner(), amount);

        emit Recovered(_token, amount);
    }

    // Emergency only - Recover BNB
    function RecoverBNB(address payable to, uint256 amount) public onlyOwner {
        to.transfer(amount);
    }

    /**
     * funds are safu?
     */
    function fundsAreSafu() external pure returns (bool) {
        return true; // always ;)
    }
}
