      "content": "// SPDX-License-Identifier: MIT

pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Counters.sol";

import "../Interface/iTagTokenDirect.sol";
import "../Interface/iTagNFTDirect.sol";
import "../Interface/iTagPoolDirect.sol";

contract TagStaking is AccessControl {
    using SafeMath for uint256;
    using Counters for Counters.Counter;

    bytes32 public constant ORACLE_ROLE = keccak256("ORACLE_ROLE");

    iTagTokenDirect FT;

    iTagNFTDirect NFT;

    iTagPoolDirect Pool;

    mapping(address => uint256) private coolDown;

    mapping(address => StakeStruct) private StakeObj;

    mapping(address => bool) private pools;

    enum State { Undefined, Active, InActive }

    struct StakeStruct {
        uint256 NFTCount;
        State state;
        uint256 claimID;
        bool isPool;
    }

    uint256 private fees;

    uint256 private cooldownBlock;

    address private oracleAddress;

    uint256 private NFT_LIMIT;

    Counters.Counter private _claimID;

    modifier isValidCoolDown() {
        uint256 diff = block.number.sub(coolDown[msg.sender]);
        require(diff >= cooldownBlock, "TagStake : Within Cool Down");
        _;
    }

    modifier isValidFees() {
        require(msg.value > fees, "TagStake : Invalid Fees provided");
        _;
    }

    modifier isValidHolder() {
        require(NFT.balanceOf(msg.sender) > 0, "TagStake : NOT A VALID HOLDER OF NFT");
        require(NFT.balanceOf(msg.sender) <= NFT_LIMIT, "TagStake : NFT Limit Exceeds");
        _;
    }

    modifier isValidDispatch(address _to, uint256 _cid) {
        require(NFT.balanceOf(_to) >= StakeObj[_to].NFTCount, "TagStake : Invalid NFT Count");
        require(StakeObj[_to].state == State.Active, "TagStake : IS INACTIVE");
        require(StakeObj[_to].claimID == _cid, "TagStake : Invalid Claim ID");
        _;
    }

    modifier isValidReject(address _to, uint256 _cid) {
        require(StakeObj[_to].claimID == _cid, "TagStake : Invalid Claim ID");
        _;
    }

    modifier isInActive() {
        require(StakeObj[msg.sender].state != State.Active, "TagStake : IS INACTIVE");
        _;
    }

    event eventClaim(uint256 _claimId, address _to);

    event eventDispatch(uint256 _claimId, address _to, uint256 _amt);

    event eventReject(uint256 _claimId, address _to);

    constructor(address _FTAddress, address _NFTAddress) AccessControl() {
        FT = iTagTokenDirect(_FTAddress);
        NFT = iTagNFTDirect(_NFTAddress);
        _setupRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _setRoleAdmin(ORACLE_ROLE, DEFAULT_ADMIN_ROLE);
    }

    function updateFT(address _FTAddress) public virtual onlyRole(DEFAULT_ADMIN_ROLE) {
        FT = iTagTokenDirect(_FTAddress);
    }

    function updateNFT(address _NFTAddress) public virtual onlyRole(DEFAULT_ADMIN_ROLE) {
        NFT = iTagNFTDirect(_NFTAddress);
    }

    function FetchFT() public view virtual returns (address) {
        return address(FT);
    }

    function FetchNFT() public view virtual returns (address) {
        return address(NFT);
    }

    function iMintTokens(address _to, uint256 _amt) internal {
        FT.mint(_to, _amt);
    }

    function setFees(uint256 _amt) public onlyRole(DEFAULT_ADMIN_ROLE) {
        fees = _amt;
    }

    function setNFTLimit(uint256 _limit) public onlyRole(DEFAULT_ADMIN_ROLE) {
        NFT_LIMIT = _limit;
    }

    function setCooldownBlock(uint256 _cooldown) public onlyRole(DEFAULT_ADMIN_ROLE) {
        cooldownBlock = _cooldown;
    }

    function setOracleFeeWallet(address _addr) public onlyRole(DEFAULT_ADMIN_ROLE) {
        oracleAddress = _addr;
    }

    function setPool(address _addr) public virtual onlyRole(DEFAULT_ADMIN_ROLE) {
        pools[_addr] = true;
    }

    function unsetPool(address _addr) public virtual onlyRole(DEFAULT_ADMIN_ROLE) {
        pools[_addr] = false;
    }

    function reject(uint256 _cid, address _to)
        public
        virtual
        isValidReject(_to, _cid)
        onlyRole(ORACLE_ROLE)
        returns (bool)
    {
        uint256 cid = StakeObj[_to].claimID;
        emit eventReject(cid, _to);
        StakeObj[_to] = StakeStruct(0, State.InActive, 0, false);
        coolDown[_to] = block.number;
        NFT.UnlockAddress(_to);
        return true;
    }

    function claim() public payable virtual isValidFees isInActive isValidCoolDown isValidHolder returns (uint256) {
        _claimID.increment();
        uint256 claimID = _claimID.current();
        uint256 diff = msg.value.sub(fees);
        uint256 NFTBal = NFT.balanceOf(msg.sender);
        bool isPool = pools[msg.sender];
        payable(oracleAddress).transfer(fees);
        payable(msg.sender).transfer(diff);
        StakeObj[msg.sender] = StakeStruct(NFTBal, State.Active, claimID, isPool);
        NFT.LockAddress(msg.sender);
        emit eventClaim(claimID, msg.sender);
        return claimID;
    }

    function dispatch(
        uint256 _cid,
        address _to,
        uint256 _amt
    ) public virtual isValidDispatch(_to, _cid) onlyRole(ORACLE_ROLE) returns (bool) {
        iMintTokens(_to, _amt);
        uint256 cid = StakeObj[_to].claimID;
        if (StakeObj[_to].isPool) {
            Pool = iTagPoolDirect(_to);
            Pool.PoolDispatch(_cid, _amt);
        }
        StakeObj[_to] = StakeStruct(0, State.InActive, 0, false);
        coolDown[_to] = block.number;
        NFT.UnlockAddress(_to);
        emit eventDispatch(cid, _to, _amt);
        return true;
    }

    function fetchFees() public view returns (uint256) {
        return fees;
    }

    function fetchClaimID() public view virtual returns (uint256) {
        return _claimID.current();
    }

    function fetchNFTLimit() public view returns (uint256) {
        return NFT_LIMIT;
    }

    function fetchCooldownBlock() public view returns (uint256) {
        return cooldownBlock;
    }

    function fetchOracleAddress() public view returns (address) {
        return oracleAddress;
    }

    function fetchStakeDetails(address _sender)
        public
        view
        virtual
        returns (
            uint256 NFTCount,
            uint256 ClaimID,
            State state,
            bool isPool
        )
    {
        NFTCount = StakeObj[_sender].NFTCount;
        ClaimID = StakeObj[_sender].claimID;
        state = StakeObj[_sender].state;
        isPool = StakeObj[_sender].isPool;
    }
}
"
