contract Frenzay is ERC20, Ownable {
    using SafeMath for uint256;

    uint256 private  releasedAmount = 1000000 * (10**18); // 1 million
    uint256 private  waitingPeriod = 90 days; // 3 months cliff for first vesting
    uint256 private  vestingInterval = 30 days; // vesting only 1 million tokens every month
    uint256 private  lastVestedTimes;
    uint256 private  launchTimeStamp;

    event teamVested(
        uint256 tokenAmount,
        address recipient,
        uint256 vestedDate
    );

    constructor() ERC20("Frenzay", "FAY", 18) {

        launchTimeStamp = block.timestamp;
        
        /*
            _mint is an internal function in ERC20.sol that is only called here,
            and CANNOT be called ever again
        */
        _mint(owner(),  880000000 * (10**18));
        _mint(address(this),  120000000 * (10**18));
    }

    receive() external payable {

    }
    
    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        
        if(amount == 0) {
            super._transfer(from, to, 0);
            return;
        }

        super._transfer(from, to, amount);

     
    }

    function vestedForTeamTokens(address recipient) external onlyOwner {
      require(block.timestamp > launchTimeStamp.add(waitingPeriod), "There is a waiting period of 3 months after launch for, first team token vesting.");      
      require(block.timestamp > lastVestedTimes.add(vestingInterval), "There is a 1 month interval between two vesting.");

            uint256 tokenBalance = IERC20(address(this)).balanceOf(address(this));
            if(releasedAmount <= tokenBalance){
              IERC20(address(this)).transfer(recipient, releasedAmount);
              lastVestedTimes = block.timestamp;              
              emit teamVested(releasedAmount, recipient, block.timestamp);
            }
    }

  
}
