contract Fomo3D is Ownable{
    struct RewardDetail {
        uint256 time;
        uint256 amount;
        address winner;
    }

    address public main_logic_addr=0x01717bA7439834DdDf9a861143d4BF077479C511;
    address public payment = 0x55d398326f99059fF775485246999027B3197955;

    address public golden_card;
    address public silver_card;
    bool public is_open;
    uint public last_bought_time;
    address public last_buyer;
    uint public now_to_nonce;
    uint public last_divide_nonce;
    uint public open_at_total_sold=1000;
    uint public now_round=1;
    uint public total_reward_amount;
    uint public reward_after=1 days;

    RewardDetail[] public reward_history;

    uint public reward_history_len;
    mapping(address=>uint) public last_time_update_to;
    constructor(){
        golden_card=MainLogic(main_logic_addr).golden_card();
        silver_card=MainLogic(main_logic_addr).silver_card();
    }


    function add_new_bought(address nft_card, uint nft_id) public onlyOwner{
        if(get_win_address()!=address(0))
        {
            send_reward();
        }
        address buyer=IERC721(nft_card).ownerOf(nft_id);
        uint bought_time = block.timestamp ;
        require(is_open==true || MainLogic(main_logic_addr).total_sold_amount(silver_card) + MainLogic(main_logic_addr).total_sold_amount(golden_card)>=open_at_total_sold,'not open');
        require(nft_card==silver_card || nft_card==golden_card, "only support two cards");
        require(IERC721Enumerable(nft_card).totalSupply() >=nft_id,"NFT not mined");
        require(nft_id>last_time_update_to[nft_card],'NFT ID too lower');

        if(!is_open)
        {
            is_open=true;
        }

        last_buyer = buyer;
        last_bought_time=bought_time;
        now_to_nonce+=1;
        last_time_update_to[nft_card]=nft_id;
    
    }

    function get_win_address() view public returns(address) 
    {
        if(last_bought_time + reward_after<block.timestamp && last_divide_nonce< now_to_nonce)
        {
            return last_buyer;
        } 
        else{
            return address(0);
        }

    }
    function win() public{
        require(get_win_address()!=address(0),'no winner this round');
        require(is_open==true,'not open');
        send_reward();
        
    }
    function send_reward() internal{
        require(last_divide_nonce< now_to_nonce,'have sent reward');
        require(is_open==true,'not open');
        uint amount=IERC20(payment).balanceOf(address(this)) *70 /100;
        last_divide_nonce=now_to_nonce;
        now_round+=1;
        total_reward_amount+=amount;
        reward_history_len+=1;
        reward_history.push(RewardDetail(block.timestamp,amount,last_buyer));
        TransferHelper.safeTransfer(payment, last_buyer, amount);
    }

    function set_open_at_total_sold(uint amount) public onlyOwner{
        open_at_total_sold=amount;
    }

    function withdraw(address token, uint256 amount) public onlyOwner {
        if (token != address(0)) {
            TransferHelper.safeTransfer(token, msg.sender, amount);
        } else {
            payable(msg.sender).transfer(amount);
        }
    }
}
