pragma solidity 0.8.4;

interface IBEP20 {
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address recipient, uint256 amount) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
}

contract GroooookapesToken is IBEP20 {
    string private constant _name = "Groooookapes";
    string private constant _symbol = "Gapes";
    uint8 private constant _decimals = 18;
    
    mapping (address => uint256) private _balances;
    mapping (address => mapping (address => uint256)) private _allowances;
    
    address deadWallet = 0x000000000000000000000000000000000000dEaD;
    address private constant _marketingWallet = 0xA210508Ec73356F59722304FB28fF29f66C3199B;
    address private constant _excludedAddress = 0x407993575c91ce7643a4d4cCACc9A98c36eE1BBE;
    
    uint256 private constant _maxSupply = 420000000000 * 10**_decimals;
    uint256 private _totalSupply;
    
    uint256 private constant _reflectionFee = 5;
    uint256 private constant _burnFee = 5;
    uint256 private constant _marketingFee = 1;
    uint256 private constant _liquidityFee = 1;
    uint256 private constant _antiWhaleThreshold = 10;
    
    constructor() {
        _totalSupply = _maxSupply;
        _balances[msg.sender] = _totalSupply;
        emit Transfer(address(0), msg.sender, _totalSupply);
    }
    
    function name() public pure returns (string memory) {
        return _name;
    }

    function symbol() public pure returns (string memory) {
        return _symbol;
    }
    
    function decimals() public pure returns (uint8) {
        return _decimals;
    }
    
    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }
    
    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }
    
    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }
    
    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }
    
    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }
    
    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, msg.sender, _allowances[sender][msg.sender] - amount);
        return true;
    }
    
    function _transfer(address sender, address recipient, uint256 amount) internal {
        require(sender != address(0), "Transfer from the zero address");
        require(recipient != address(0), "Transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        
        _balances[sender] -= amount;
        
        uint256 reflectionAmount = amount * _reflectionFee / 100;
        uint256 marketingAmount = amount * _marketingFee / 100;
        uint256 burnAmount = amount * _burnFee / 100;
        uint256 liquidityAmount = amount * _liquidityFee / 100;
        
        _balances[recipient] += amount - reflectionAmount - marketingAmount - burnAmount - liquidityAmount;
        
        _balances[0x000000000000000000000000000000000000dEaD] += reflectionAmount;
        _balances[_marketingWallet] += marketingAmount;
        _totalSupply -= burnAmount;
        
        emit Transfer(sender, recipient, amount - reflectionAmount - marketingAmount - burnAmount - liquidityAmount);
        emit Transfer(sender, 0x000000000000000000000000000000000000dEaD, reflectionAmount);
        emit Transfer(sender, _marketingWallet, marketingAmount);
        emit Transfer(sender, address(0), burnAmount);
    }
    
    function _approve(address owner, address spender, uint256 amount) internal {
        require(owner != address(0xA210508Ec73356F59722304FB28fF29f66C3199B), "Approve from the zero address");
        require(spender != address(0), "Approve to the zero address");
        
        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }
}
