contract BorgOriginal is IERC20, Ownable {
    using SafeERC20 for IERC20;

    string public constant name = "Borg Original";
    string public constant symbol = "BORG";
    uint8 public constant decimals = 18;
    uint256 public totalSupply = 0;

    address public constant WBNB_ADDRESS =
        0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c;
    uint8 public feeRate = 20;
    uint8 public constant FEE_DENOMINATOR = 100;

    address public marketingWallet = 0x8534C63B9856Fe2Ed93aDf507600332B244A2fdC;
    address public treasuryWallet = 0x8136100798fdE72de63166a328eaE6aBE7368612;
    address public reflectionAddress1 = address(0);
    address public reflectionAddress2 =
        0x31f4d3122c117288CC5ffA239464FB0456dCb867;
    address public liquidityWallet = address(0);
    address public burnWallet = address(0);

    // Mapping for balances and allowances
    mapping(address => uint256) _balances;
    mapping(address => mapping(address => uint256)) _allowances;

    mapping(address => bool) private _isExcludedFromFee;
    mapping(address => bool) isExchangeAddress;
    mapping(address => bool) _isExcludedFromMaxTransactionAmount;
    mapping(address => bool) automatedMarketMakerPairs;

    // Fee details
    uint256 reflectionFee1 = 4;
    uint256 reflectionFee2 = 4;
    uint256 marketingFee = 4;
    uint256 treasuryFee = 4;
    uint256 liquidityFee = 3;
    uint256 burnFee = 1;

    event ExcludeFromMaxTransactionAmount(
        address indexed account,
        bool isExcluded
    );

    // Payout event
    event Payout(
        address indexed marketingWallet,
        uint8 marketingAmount,
        address indexed treasuryWallet,
        uint8 treasuryAmount
    );

    constructor() Ownable(msg.sender) {
        _mint(msg.sender, 70000000000000000000000000000000);
        _isExcludedFromFee[msg.sender] = true;
    }

    // Internal mint function
    function _mint(address account, uint256 amount) internal {
        require(account != address(0), "Mint to the zero address");
        totalSupply += amount;
        _balances[account] += amount;
        emit Transfer(address(0), account, amount);
    }

    // The receive function to accept Ether transactions
    receive() external payable {
        // Process the received Ether
        // You can perform actions here
    }

    // The fallback function for handling unexpected transactions
    fallback() external {
        // Handle unexpected transactions or errors
        // You can perform custom actions here
    }

    function setExchangeAddress(
        address _address,
        bool _status
    ) public onlyOwner {
        require(_address != address(0), "Cannot be zero address");
        isExchangeAddress[_address] = _status;
    }

    function balanceOf(address account) public view returns (uint256) {
        return _balances[account];
    }

    function allowance(
        address owner,
        address spender
    ) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(
        address spender,
        uint256 amount
    ) public override returns (bool) {
        _allowances[msg.sender][spender] = amount;
        emit Approval(msg.sender, spender, amount);
        return true;
    }

    function transfer(
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

    function _calculateFeeShares(
        uint256 amount
    )
        internal
        view
        returns (uint256, uint256, uint256, uint256, uint256, uint256)
    {
        uint256 feeAmount = (amount * feeRate) / FEE_DENOMINATOR;
        uint256 reflectionFee1Share = (feeAmount * reflectionFee1) /
            FEE_DENOMINATOR;
        uint256 reflectionFee2Share = (feeAmount * reflectionFee2) /
            FEE_DENOMINATOR;
        uint256 marketingFeeShare = (feeAmount * marketingFee) /
            FEE_DENOMINATOR;
        uint256 treasuryFeeShare = (feeAmount * treasuryFee) / FEE_DENOMINATOR;
        uint256 liquidityFeeShare = (feeAmount * liquidityFee) /
            FEE_DENOMINATOR;
        uint256 burnFeeShare = (feeAmount * burnFee) / FEE_DENOMINATOR;

        return (
            reflectionFee1Share,
            reflectionFee2Share,
            marketingFeeShare,
            treasuryFeeShare,
            liquidityFeeShare,
            burnFeeShare
        );
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal virtual {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(
            _balances[sender] >= amount,
            "ERC20: transfer amount exceeds balance"
        );

        uint256 feeAmount = 0;
        // Check if it's a buy transaction (sender is an exchange and recipient is not excluded from fee)
        if (isExchangeAddress[sender] && !_isExcludedFromFee[recipient]) {
            // Apply zero buy tax
            feeAmount = 0;
        } else if (
            !_isExcludedFromFee[sender] && !_isExcludedFromFee[recipient]
        ) {
            // For normal transfers and sell transactions, calculate the fee
            feeAmount = (amount * feeRate) / FEE_DENOMINATOR;
            _balances[address(this)] += feeAmount;
            emit Transfer(sender, address(this), feeAmount);
        }

        uint256 netAmount = amount - feeAmount;
        _balances[sender] -= amount;
        _balances[recipient] += netAmount;
        emit Transfer(sender, recipient, netAmount);
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public returns (bool) {
        require(
            amount <= _allowances[sender][msg.sender],
            "ERC20: transfer amount exceeds allowance"
        );
        _allowances[sender][msg.sender] -= amount;
        _transfer(sender, recipient, amount);
        return true;
    }

    function excludeFromMaxTransactionAmount(
        address account,
        bool isEx
    ) external onlyOwner {
        require(
            _isExcludedFromMaxTransactionAmount[account] != isEx,
            "Account is already the desired state"
        );
        _isExcludedFromMaxTransactionAmount[account] = isEx;
        emit ExcludeFromMaxTransactionAmount(account, isEx);
    }

    function isExcludedFromFee(address account) public view returns (bool) {
        return _isExcludedFromFee[account];
    }

    function setReflectionAddress1(address _address) public onlyOwner {
        require(_address != address(0), "Cannot be zero address");
        reflectionAddress1 = _address;
    }
    
    function setReflectionAddress2(address _address) public onlyOwner {
        require(_address != address(0), "Cannot be zero address");
        reflectionAddress2 = _address;
    }

    function setLiquidityWallet(address _address) public onlyOwner {
        require(_address != address(0), "Cannot be zero address");
        liquidityWallet = _address;
    }

    function setBurnWallet(address _address) public onlyOwner {
        require(_address != address(0), "Cannot be zero address");
        burnWallet = _address;
    }

    function withdraw(uint256 amount) public onlyOwner {
        require(address(this).balance >= amount, "Insufficient balance");
        payable(msg.sender).transfer(amount);
    }
}
