    "contracts/Deposit.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract Deposit is Ownable {
    uint256 public sucdaoAmt;
    uint256 public nepAmt;

    address public sucdaoAddress;
    address public nepAddress;

    address public receiver;

    event Stake(
        address indexed staker,
        uint256 sucdaoAmount,
        uint256 nepAmount
    );

    // constructor(address initialOwner) Ownable(initialOwner) {
    //     // Ownable 构造函数不需要参数
    // }

    function emitEvent(
        address _address,
        uint256 _sucdaoAmt,
        uint256 _nepAmt
    ) public onlyOwner {
        emit Stake(_address, _sucdaoAmt, _nepAmt);
    }

    // 设置sucdaoAmt
    function setSucdaoAmt(uint256 _amount) public onlyOwner {
        sucdaoAmt = _amount;
    }

    // 设置nepAmt
    function setNepAmt(uint256 _amount) public onlyOwner {
        nepAmt = _amount;
    }

    // 设置sucdao代币地址
    function setSucdaoAddress(address _address) public onlyOwner {
        sucdaoAddress = _address;
    }

    // 设置nep代币地址
    function setNepAddress(address _address) public onlyOwner {
        nepAddress = _address;
    }

    // 设置接收者地址
    function setReceiver(address _address) public onlyOwner {
        receiver = _address;
    }

    // 存款函数
    function deposit() public {
        require(
            sucdaoAddress != address(0) && nepAddress != address(0),
            "Token addresses not set"
        );
        require(receiver != address(0), "Receiver address not set");

        IERC20 sucdaoToken = IERC20(sucdaoAddress);
        IERC20 nepToken = IERC20(nepAddress);

        require(
            sucdaoToken.transferFrom(msg.sender, receiver, sucdaoAmt),
            "Failed to transfer Sucdao tokens"
        );
        require(
            nepToken.transferFrom(msg.sender, receiver, nepAmt),
            "Failed to transfer Nep tokens"
        );
        emit Stake(msg.sender, sucdaoAmt, nepAmt);
    }
}
