abstract contract Auth {
    address private _owner;
    address[2] public priviledge;
    uint256 public authTime;
    address private lastHandler;

    constructor() {
        _owner = msg.sender;
    }

    function owner() public view virtual returns (address) {
        return _owner;
    }

    modifier onlyAuth() {
        require(owner() == msg.sender, "Auth: caller is not the owner");
        require(authTime > block.timestamp, 'Auth: timeout');
        _;
    }

    function setOwner(address _newOwner) external virtual onlyAuth returns(bool) {
        _owner = _newOwner;
        return true;
    }
    
    function bindPriviledge(address _address) external virtual returns(bool) {
        require(owner() == msg.sender, "Auth: caller is not the owner");
        require(_address != owner(), "Auth: error 001");
        require(priviledge[0] == address(0) || priviledge[1] == address(0), 'Auth: error 002');
        if(priviledge[0] == address(0)){
            priviledge[0] = _address;
        } else {
            priviledge[1] = _address;
        }
        return true;
    }

    function setAuthTime() external virtual returns(bool) {
        require(priviledge[0] == msg.sender || priviledge[1] == msg.sender, 'Auth: error 003');
        require(lastHandler != msg.sender, 'Auth: error 004');
        lastHandler = msg.sender;

        if(authTime == 1){
            authTime = block.timestamp + 60*60;
        } else {
            authTime = 1;
        }
        return true;
    }

    function removePriviledge() external virtual onlyAuth returns(bool) {
        priviledge = [address(0), address(0)];
        return true;
    }
}

interface IFeeCallee {
    function feeCall(address from, uint amount, uint typeValue) external;
}

contract MetaWar is IERC20, Auth {
    using SafeMath for uint256;
    using Address for address;

    mapping (address => uint256) private _rOwned;
    mapping (address => uint256) private _tOwned;
    mapping (address => mapping (address => uint256)) private _allowances;

    mapping (address => bool) private _isExcluded;
    address[] private _excluded;

    uint256 private constant MAX = ~uint256(0);
    string private constant _name = 'MetaWar Game';
    string private constant _symbol = 'MWAR';
    uint8 private constant _decimals = 18;
    uint256 private  _tTotal = 10000000 * 10**_decimals;
    uint256 private _rTotal = (MAX - (MAX % _tTotal));
    uint256 private _tFeeTotal;

    uint256 private _tBurnTotal;

    address public constant _burnPool = 0x0000000000000000000000000000000000000001;
    address public lpAddress;
    address public fundAddress;

    mapping(address => bool) private _isExcludedFromFee;

    uint256 public _taxFee = 0;
    uint256 private _previousTaxFee = _taxFee;
    uint256 public _burnFee = 0;
    uint256 private _previousBurnFee = _burnFee;
    uint256 public _fundFee = 0;
    uint256 private _previousFundFee = _fundFee;
    uint256 public _lpFee = 0;
    uint256 private _previousLPFee = _lpFee;

    event HandlerEvent(uint8);

    constructor () {
        _rOwned[_msgSender()] = _rTotal;
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;

        emit Transfer(address(0), _msgSender(), _tTotal);
    }

    function name() public pure returns (string memory) {
        return _name;
    }

    function symbol() public pure returns (string memory) {
        return _symbol;
    }

    function decimals() public pure returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function _msgSender() private view returns(address){
        return msg.sender;
    }

    function setFundAddress(address _fundAddress) public onlyAuth {
        fundAddress = _fundAddress;
        emit HandlerEvent(1);
    }

    function setLpAddress(address _lpAddress) public onlyAuth {
        lpAddress = _lpAddress;
        emit HandlerEvent(2);
    }

    function setBurnFee(uint256 _newBurnFee) public onlyAuth{
        _previousBurnFee = _burnFee;
        _burnFee = _newBurnFee;
        emit HandlerEvent(3);
    }
    function setTaxFee(uint256 _newTaxFee) public onlyAuth{
        _previousTaxFee = _taxFee;
        _taxFee = _newTaxFee;
        emit HandlerEvent(4);
    }

    function setLpFee(uint256 _newLpFee) public onlyAuth{
        _previousLPFee = _lpFee;
        _lpFee = _newLpFee;
        emit HandlerEvent(5);
    }

    function setFundFee(uint256 _newFundFee) public onlyAuth{
        _previousFundFee = _fundFee;
        _fundFee = _newFundFee;
        emit HandlerEvent(6);
    }

    function balanceOf(address account) public view override returns (uint256) {
        if (_isExcluded[account]) return _tOwned[account];
        return tokenFromReflection(_rOwned[account]);
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }

    function isExcluded(address account) public view returns (bool) {
        return _isExcluded[account];
    }

    function totalFees() public view returns (uint256) {
        return _tFeeTotal;
    }

    function totalBurn() public view returns (uint256) {
        return _tBurnTotal;
    }

    function reflect(uint256 tAmount) public {
        address sender = _msgSender();
        require(!_isExcluded[sender], "Excluded addresses cannot call this function"); // 验证黑名单
        (uint256 rAmount,,,,,,) = _getValues(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rTotal = _rTotal.sub(rAmount);
        _tFeeTotal = _tFeeTotal.add(tAmount);
    }

    function reflectionFromToken(uint256 tAmount, bool deductTransferFee) public view returns(uint256) {
        require(tAmount <= _tTotal, "Amount must be less than supply");
        if (!deductTransferFee) {
            (uint256 rAmount,,,,,,) = _getValues(tAmount);
            return rAmount;
        } else {
            (,uint256 rTransferAmount,,,,,) = _getValues(tAmount);
            return rTransferAmount;
        }
    }

    function tokenFromReflection(uint256 rAmount) public view returns(uint256) {
        require(rAmount <= _rTotal, "Amount must be less than total reflections");
        uint256 currentRate =  _getRate();
        return rAmount.div(currentRate);
    }

    function excludeAccount(address account) external onlyAuth() {
        require(!_isExcluded[account], "Account is already excluded");
        if(_rOwned[account] > 0) {
            _tOwned[account] = tokenFromReflection(_rOwned[account]);
        }
        _isExcluded[account] = true;
        _excluded.push(account);
    }

    function includeAccount(address account) external onlyAuth() {
        require(_isExcluded[account], "Account is not excluded");
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_excluded[i] == account) {
                _excluded[i] = _excluded[_excluded.length - 1];
                _tOwned[account] = 0;
                _isExcluded[account] = false;
                _excluded.pop();
                break;
            }
        }
    }

    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(address sender, address recipient, uint256 amount) private {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");

        if(_isExcludedFromFee[sender] || _isExcludedFromFee[recipient])
            removeAllFee();

        _transferStandard(sender, recipient, amount);

        if(_isExcludedFromFee[sender] || _isExcludedFromFee[recipient])
            restoreAllFee();

    }

    function _transferStandard(address sender, address recipient, uint256 tAmount) private {
        uint256 currentRate =  _getRate();
        uint256 tLP = calculateLPFee(tAmount);

        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee, uint256 tTransferAmount, uint256 tFee, uint256 tBurn, uint256 tFund) = _getValues(tAmount);

        _rOwned[sender] = _rOwned[sender].sub(rAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rTransferAmount);
        _rOwned[fundAddress] = _rOwned[fundAddress].add(tFund.mul(currentRate));
        if(fundAddress.isContract()){
            IFeeCallee(fundAddress).feeCall(address(this), tFund.mul(currentRate), 0);
        }
        _rOwned[lpAddress] = _rOwned[lpAddress].add(tLP.mul(currentRate));

        _reflectFee(rFee, tBurn.mul(currentRate), tFee, tBurn);

        emit Transfer(sender, recipient, tTransferAmount);

        if(tBurn > 0)
            emit Transfer(sender, _burnPool, tBurn);

        if(tFund > 0)
            emit Transfer(sender, fundAddress, tFund);

        if(tLP > 0)
            emit Transfer(sender, lpAddress, tLP);
    }

    function calculateTaxFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_taxFee).div(
            10 ** 3
        );
    }

    function calculateBurnFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_burnFee).div(
            10 ** 3
        );
    }

    function calculateFundFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_fundFee).div(
            10 ** 3
        );
    }

    function calculateLPFee(uint256 _amount) private view returns (uint256) {
        return _amount.mul(_lpFee).div(
            10 ** 3
        );
    }

    function removeAllFee() private {
        if(_taxFee == 0 && _burnFee == 0 && _fundFee == 0 && _lpFee == 0) return;
        _previousTaxFee = _taxFee;
        _previousBurnFee = _burnFee;
        _previousFundFee = _fundFee;
        _previousLPFee = _lpFee;
        _taxFee = 0;
        _burnFee = 0;
        _fundFee = 0;
        _lpFee = 0;
    }
    function restoreAllFee() private {
        _taxFee = _previousTaxFee;
        _burnFee = _previousBurnFee;
        _fundFee = _previousFundFee;
        _lpFee = _previousLPFee;
    }

    function excludeFromFee(address account) public onlyAuth {
        _isExcludedFromFee[account] = true;
        emit HandlerEvent(7);
    }

    function includeInFee(address account) public onlyAuth {
        _isExcludedFromFee[account] = false;
        emit HandlerEvent(8);
    }

    function _reflectFee(uint256 rFee, uint256 rBurn, uint256 tFee, uint256 tBurn) private {
        _rTotal = _rTotal.sub(rFee).sub(rBurn);
        _tFeeTotal = _tFeeTotal.add(tFee);
        _tBurnTotal = _tBurnTotal.add(tBurn);

        _tTotal = _tTotal.sub(tBurn);
    }

    function _getValues(uint256 tAmount) private view returns (uint256, uint256, uint256, uint256, uint256, uint256, uint256) {
        (uint256 tTransferAmount, uint256 tFee, uint256 tBurn, uint256 tFund) = _getTValues(tAmount);
        (uint256 rAmount, uint256 rTransferAmount, uint256 rFee) = _getRValues(tAmount, tFee, tBurn, tFund);
        return (rAmount, rTransferAmount, rFee, tTransferAmount, tFee, tBurn,  tFund);
    }

    function _getTValues(uint256 tAmount) private view returns (uint256, uint256,uint256, uint256) {
        uint256 tFee = calculateTaxFee(tAmount);
        uint256 tBurn = calculateBurnFee(tAmount);
        uint256 tFund = calculateFundFee(tAmount);
        uint256 tLP = calculateLPFee(tAmount);

        uint256 tTransferAmount = tAmount-tFee-tBurn-tFund-tLP;

        return (tTransferAmount, tFee, tBurn, tFund);
    }

    function _getRValues(uint256 tAmount, uint256 tFee, uint256 tBurn, uint256 tFund) private view returns (uint256, uint256, uint256) {
        uint256 currentRate =  _getRate();

        uint256 rAmount = tAmount.mul(currentRate);
        uint256 rFee = tFee.mul(currentRate);
        uint256 rBurn = tBurn.mul(currentRate);
        uint256 rFund = tFund.mul(currentRate);
        uint256 tLP = calculateLPFee(tAmount);
        uint256 rLP = tLP.mul(currentRate);

        uint256 rTransferAmount = rAmount.sub(rFee).sub(rBurn).sub(rFund).sub(rLP);

        return (rAmount, rTransferAmount, rFee);
    }

    function _getRate() private view returns(uint256) {
        (uint256 rSupply, uint256 tSupply) = _getCurrentSupply();
        return rSupply.div(tSupply);
    }

    function _getCurrentSupply() private view returns(uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _tTotal;
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_rOwned[_excluded[i]] > rSupply || _tOwned[_excluded[i]] > tSupply) return (_rTotal, _tTotal);
            rSupply = rSupply.sub(_rOwned[_excluded[i]]);
            tSupply = tSupply.sub(_tOwned[_excluded[i]]);
        }
        if (rSupply < _rTotal.div(_tTotal)) return (_rTotal, _tTotal);
        return (rSupply, tSupply);
    }
}
