    // To implement this library for multiple types with as little code
    // repetition as possible, we write it in terms of a generic Map type with
    // bytes32 keys and values.
    // The Map implementation uses private functions, and user-facing
    // implementations (such as Uint256ToAddressMap) are just wrappers around
    // the underlying Map.
    // This means that we can only create new EnumerableMaps for types that fit
    // in bytes32.

    struct Map {
        // Storage of keys
        EnumerableSet.Bytes32Set _keys;
        mapping(bytes32 => bytes32) _values;
    }

    /**
     * @dev Adds a key-value pair to a map, or updates the value for an existing
     * key. O(1).
     *
     * Returns true if the key was added to the map, that is if it was not
     * already present.
     */
    function _set(
        Map storage map,
        bytes32 key,
        bytes32 value
    ) private returns (bool) {
        map._values[key] = value;
        return map._keys.add(key);
    }

    /**
     * @dev Removes a key-value pair from a map. O(1).
     *
     * Returns true if the key was removed from the map, that is if it was present.
     */
    function _remove(Map storage map, bytes32 key) private returns (bool) {
        delete map._values[key];
        return map._keys.remove(key);
    }

    /**
     * @dev Returns true if the key is in the map. O(1).
     */
    function _contains(Map storage map, bytes32 key)
        private
        view
        returns (bool)
    {
        return map._keys.contains(key);
    }

    /**
     * @dev Returns the number of key-value pairs in the map. O(1).
     */
    function _length(Map storage map) private view returns (uint256) {
        return map._keys.length();
    }

    /**
     * @dev Returns the key-value pair stored at position `index` in the map. O(1).
     *
     * Note that there are no guarantees on the ordering of entries inside the
     * array, and it may change when more entries are added or removed.
     *
     * Requirements:
     *
     * - `index` must be strictly less than {length}.
     */
    function _at(Map storage map, uint256 index)
        private
        view
        returns (bytes32, bytes32)
    {
        bytes32 key = map._keys.at(index);
        return (key, map._values[key]);
    }

    /**
     * @dev Tries to returns the value associated with `key`.  O(1).
     * Does not revert if `key` is not in the map.
     */
    function _tryGet(Map storage map, bytes32 key)
        private
        view
        returns (bool, bytes32)
    {
        bytes32 value = map._values[key];
        if (value == bytes32(0)) {
            return (_contains(map, key), bytes32(0));
        } else {
            return (true, value);
        }
    }

    /**
     * @dev Returns the value associated with `key`.  O(1).
     *
     * Requirements:
     *
     * - `key` must be in the map.
     */
    function _get(Map storage map, bytes32 key) private view returns (bytes32) {
        bytes32 value = map._values[key];
        require(
            value != 0 || _contains(map, key),
            "EnumerableMap: nonexistent key"
        );
        return value;
    }

    /**
     * @dev Same as {_get}, with a custom error message when `key` is not in the map.
     *
     * CAUTION: This function is deprecated because it requires allocating memory for the error
     * message unnecessarily. For custom revert reasons use {_tryGet}.
     */
    function _get(
        Map storage map,
        bytes32 key,
        string memory errorMessage
    ) private view returns (bytes32) {
        bytes32 value = map._values[key];
        require(value != 0 || _contains(map, key), errorMessage);
        return value;
    }

    // UintToAddressMap

    struct UintToUintMap {
        Map _inner;
    }

    /**
     * @dev Adds a key-value pair to a map, or updates the value for an existing
     * key. O(1).
     *
     * Returns true if the key was added to the map, that is if it was not
     * already present.
     */
    function set(
        UintToUintMap storage map,
        uint256 key,
        uint256 value
    ) internal returns (bool) {
        return _set(map._inner, bytes32(key), bytes32(value));
    }

    /**
     * @dev Removes a value from a set. O(1).
     *
     * Returns true if the key was removed from the map, that is if it was present.
     */
    function remove(UintToUintMap storage map, uint256 key)
        internal
        returns (bool)
    {
        return _remove(map._inner, bytes32(key));
    }

    /**
     * @dev Returns true if the key is in the map. O(1).
     */
    function contains(UintToUintMap storage map, uint256 key)
        internal
        view
        returns (bool)
    {
        return _contains(map._inner, bytes32(key));
    }

    /**
     * @dev Returns the number of elements in the map. O(1).
     */
    function length(UintToUintMap storage map) internal view returns (uint256) {
        return _length(map._inner);
    }

    /**
     * @dev Returns the element stored at position `index` in the set. O(1).
     * Note that there are no guarantees on the ordering of values inside the
     * array, and it may change when more values are added or removed.
     *
     * Requirements:
     *
     * - `index` must be strictly less than {length}.
     */
    function at(UintToUintMap storage map, uint256 index)
        internal
        view
        returns (uint256, uint256)
    {
        (bytes32 key, bytes32 value) = _at(map._inner, index);
        return (uint256(key), uint256(value));
    }

    /**
     * @dev Tries to returns the value associated with `key`.  O(1).
     * Does not revert if `key` is not in the map.
     *
     * _Available since v3.4._
     */
    function tryGet(UintToUintMap storage map, uint256 key)
        internal
        view
        returns (bool, uint256)
    {
        (bool success, bytes32 value) = _tryGet(map._inner, bytes32(key));
        return (success, uint256(value));
    }

    /**
     * @dev Returns the value associated with `key`.  O(1).
     *
     * Requirements:
     *
     * - `key` must be in the map.
     */
    function get(UintToUintMap storage map, uint256 key)
        internal
        view
        returns (uint256)
    {
        return uint256(_get(map._inner, bytes32(key)));
    }

    /**
     * @dev Same as {get}, with a custom error message when `key` is not in the map.
     *
     * CAUTION: This function is deprecated because it requires allocating memory for the error
     * message unnecessarily. For custom revert reasons use {tryGet}.
     */
    function get(
        UintToUintMap storage map,
        uint256 key,
        string memory errorMessage
    ) internal view returns (uint256) {
        return uint256(_get(map._inner, bytes32(key), errorMessage));
    }
}

// File: contracts/farming/NFTFarming.sol

pragma solidity 0.8.1;













contract NFTFarming is
    Context,
    ReentrancyGuard,
    IERC1155Receiver,
    ERC165,
    IFarming
{
    using Math for uint256;
    using SafeERC20 for IERC20;
    using EnumerableSet for EnumerableSet.UintSet;
    using EnumerableMap for EnumerableMap.UintToUintMap;

    // emit when user stake NFT
    event StakeNFT(address indexed user, uint256[] ids);
    // emit when user unstake NFT
    event UnstakeNFT(address indexed user, uint256[] ids);
    // emit when user withdraw MELOS reward to wallet.
    event WithdrawMELOS(address indexed user, uint256 amount);

    // Info of each user.
    struct UserInfo {
        // Reward debt
        uint256 rewardDebt;
        // number of minerNFTs in this pool
        uint256 numMinerNFTs;
        // sum of minerNFT power in this pool
        uint256 sumPower;
    }

    // Info of Reward Pool.
    struct PoolInfo {
        // Last block number that MELOSs distribution occurs.
        uint256 lastRewardBlock;
        // The accumulated MELOS reward per power.
        uint256 accMelosPerPower;
        // Total power staked in the pool.
        uint256 totalPower;
        // Total number of minerNFTs staked in the pool.
        uint256 numMinerNFTs;
    }

    // MELOS token contract.
    IERC20 public immutable melos;
    // MinerNFT contract.
    MinerNFT public immutable minerNFT;
    // Supplier contract for distributing MELOS to the farming pool.
    IMELOSSupplier private immutable supplier;

    // farm pool info
    PoolInfo public poolInfo;

    uint256 public immutable lockedPeroid; // should be the delta of block height

    // Info of each user.
    mapping(address => UserInfo) public userInfo;

    // Info of each user that staked NFT ids.
    mapping(address => EnumerableSet.UintSet) private userMinerNFTs;

    // releaseblock => balance mapping that reflects user's T + n locking status.
    mapping(address => EnumerableMap.UintToUintMap) private timeLockReward;

    uint256 private constant SAFE_MULTIPLIER = 1e16;

    constructor(
        IERC20 _melos, // MELOS token contract
        MinerNFT _minerNFT, // NFT contract
        IMELOSSupplier _supplier, // MELOS token supplier contract
        uint256 _startBlock, // Start block from which this farming pool calculate rewards.
        uint256 _rewardLockedPeriod // number of blocks rewards should be locked
    ) {
        melos = _melos;
        minerNFT = _minerNFT;
        supplier = _supplier;
        lockedPeroid = _rewardLockedPeriod;

        poolInfo = PoolInfo({
            lastRewardBlock: block.number.max(_startBlock),
            accMelosPerPower: 0,
            totalPower: 0,
            numMinerNFTs: 0
        });
    }

    modifier fromMinerNFT() {
        require(
            _msgSender() == address(minerNFT),
            "NFTFarming: received minerNFT from unauthenticated contract"
        );
        _;
    }

    /**
     * @dev View `_user` staked NFT
     */
    function stakedNFT(address _user) external view returns (uint256[] memory) {
        return _nftInPool(_user);
    }

    /**
     * @dev View `_user` pending reward
     */
    function pendingReward(address _user) external view returns (uint256) {
        UserInfo storage user = userInfo[_user];

        if (user.sumPower == 0) {
            return 0;
        }

        if (poolInfo.totalPower == 0) {
            return 0;
        }

        //Get the current accumlated MELOS reward per power in this pool.
        uint256 accMelosPerPower = poolInfo.accMelosPerPower;

        // If the current block is higher than the last reward block then invoke MELOS Supply contract.
        if (block.number > poolInfo.lastRewardBlock) {
            uint256 total = supplier.preview(
                address(this),
                poolInfo.lastRewardBlock
            );

            // calculate the current accumlated MELOS reward per power.
            accMelosPerPower += (total * SAFE_MULTIPLIER) / poolInfo.totalPower;
        }

        // calculate the curent pending reward for '_user'.
        return
            _calcPendingReward(
                user.sumPower,
                accMelosPerPower,
                user.rewardDebt
            );
    }

    /**
     * @dev View `_user` paid reward
     */
    function lockedRewardBalance(address _user)
        external
        view
        returns (uint256 total, uint256 withdrawable)
    {
        EnumerableMap.UintToUintMap storage timeLock = timeLockReward[_user];

        uint256 totalRecords = timeLock.length();
        for (uint256 i = 0; i < totalRecords; i++) {
            (uint256 releaseBlock, uint256 balance) = timeLock.at(i);
            total += balance;
            if (releaseBlock <= block.number) {
                withdrawable += balance;
            }
        }
    }

    /**
     * @dev Update reward variables of the given pool to be up-to-date.
     */
    function updatePool() public override {
        if (block.number <= poolInfo.lastRewardBlock) {
            return;
        }

        if (poolInfo.totalPower == 0) {
            poolInfo.lastRewardBlock = block.number;
            return;
        }

        uint256 newSupplied = supplier.distribute(poolInfo.lastRewardBlock);

        poolInfo.accMelosPerPower +=
            (newSupplied * SAFE_MULTIPLIER) /
            poolInfo.totalPower;

        poolInfo.lastRewardBlock = block.number;
    }

    /**
     * @dev Retrieve caller's staked minerNFTs
     */
    function retrieve(uint256[] calldata _ids) external nonReentrant {
        require(_ids.length > 0, "NFTFarming: invalid argument");

        address sender = _msgSender();
        UserInfo storage user = userInfo[sender];

        updatePool();

        _payPendingRewardIfAvailable(sender, user);

        // may revert if there are unknown tokenId.
        (uint256 remainNumMinerNFT, uint256 remainPower) = _calcDeletion(
            sender,
            user,
            _ids
        );

        //transfer out NFTs
        _safeNFTTransferFrom(address(this), sender, _ids);

        // Update the user info.
        EnumerableSet.UintSet storage minerNFTs = userMinerNFTs[sender];
        for (uint256 i = 0; i < _ids.length; i++) {
            minerNFTs.remove(_ids[i]);
        }

        uint256 powerToDel = user.sumPower - remainPower;
        uint256 numNFTToDel = user.numMinerNFTs - remainNumMinerNFT;

        user.sumPower = remainPower;
        user.numMinerNFTs = remainNumMinerNFT;

        // Update the pool info.
        poolInfo.totalPower -= powerToDel;
        poolInfo.numMinerNFTs -= numNFTToDel;

        // and calc. user debt
        user.rewardDebt = _calcRewardDebt(
            user.sumPower,
            poolInfo.accMelosPerPower
        );

        emit UnstakeNFT(sender, _ids);
    }

    /**
     * @dev Emergency retrieve staked NFT
     * EMERGENCY ONLY.
     */
    function emergencyRetrieve() external nonReentrant {
        address sender = _msgSender();

        UserInfo storage user = userInfo[sender];
        require(user.numMinerNFTs > 0, "NFTFarming: insufficient balance");

        // Update the pool info.
        poolInfo.totalPower -= user.sumPower;
        poolInfo.numMinerNFTs -= user.numMinerNFTs;
        delete userInfo[sender];

        uint256[] memory ids = _nftInPool(sender);
        _safeNFTTransferFrom(address(this), sender, ids);

        delete userMinerNFTs[sender];

        emit UnstakeNFT(sender, ids);
    }

    /**
     *  @dev Withdraw user reward, the reward will be transfer to user directly if 
            it's been locked for lockPeriod of time.  
        @param _maxRecords  - max records to withdraw. 0 means all drawable records. This is used in 
        extreme situation where there are huge records to withdraw that may cause 
        gas limit isssue.
     */
    function withdrawReward(uint256 _maxRecords) external nonReentrant {
        address sender = _msgSender();
        EnumerableMap.UintToUintMap storage timeLock = timeLockReward[sender];

        uint256 drawableBalance = 0;

        uint256 totalRecords = timeLock.length();
        uint256 executeRecords = totalRecords;
        if (_maxRecords > 0) {
            executeRecords = totalRecords.min(_maxRecords);
        }

        uint256[] memory releasedBlocks = new uint256[](executeRecords);
        uint256 released = 0;

        for (uint256 i = 0; i < executeRecords; i++) {
            (uint256 releaseBlock, uint256 balance) = timeLock.at(i);
            if (releaseBlock <= block.number) {
                drawableBalance += balance;
                releasedBlocks[released++] = releaseBlock;
            }
        }

        // cleanup released records
        for (uint256 i = 0; i < released; i++) {
            timeLock.remove(releasedBlocks[i]);
        }

        require(drawableBalance > 0, "NFTFarming: no withdrawable balance");

        _safeMELOSTransfer(sender, drawableBalance);
        emit WithdrawMELOS(sender, drawableBalance);
    }

    /**
     * @dev Claim user reward, the reward will be locked for lockedPeriod
     */
    function claim() external nonReentrant {
        address sender = _msgSender();
        UserInfo storage user = userInfo[sender];

        require(user.sumPower > 0, "NFTFarming: need stake NFT with power > 0");

        updatePool();

        _payPendingRewardIfAvailable(sender, user);

        user.rewardDebt = _calcRewardDebt(
            user.sumPower,
            poolInfo.accMelosPerPower
        );
    }

    function onERC1155Received(
        address,
        address,
        uint256,
        uint256,
        bytes calldata
    ) external override nonReentrant fromMinerNFT returns (bytes4) {
        require(false, "NFTFarming: only support batch transfer");

        return this.onERC1155Received.selector;
    }

    function onERC1155BatchReceived(
        address,
        address _from,
        uint256[] calldata _ids,
        uint256[] calldata,
        bytes calldata
    ) external override nonReentrant fromMinerNFT returns (bytes4) {
        UserInfo storage user = userInfo[_from];

        updatePool();

        _payPendingRewardIfAvailable(_from, user);

        (uint256 numMinerNFTs, uint256 sumPower) = _calcAddition(user, _ids);

        EnumerableSet.UintSet storage _minerNFTs = userMinerNFTs[_from];
        for (uint256 i = 0; i < _ids.length; i++) {
            require(_minerNFTs.add(_ids[i]), "NFTFarming: existing tokenId");
        }

        uint256 powerDelta = sumPower - user.sumPower;
        uint256 numNFTDelta = numMinerNFTs - user.numMinerNFTs;

        // Update the user data
        user.numMinerNFTs = numMinerNFTs;
        user.sumPower = sumPower;

        // Update the pool data
        poolInfo.totalPower += powerDelta;
        poolInfo.numMinerNFTs += numNFTDelta;

        // update user debt
        user.rewardDebt = _calcRewardDebt(
            user.sumPower,
            poolInfo.accMelosPerPower
        );

        emit StakeNFT(_from, _ids);
        return this.onERC1155BatchReceived.selector;
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC165, IERC165)
        returns (bool)
    {
        return
            interfaceId == type(IERC1155Receiver).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function _calcRewardDebt(uint256 _sumPower, uint256 _accMelosPerPower)
        private
        pure
        returns (uint256)
    {
        return (_sumPower * _accMelosPerPower) / SAFE_MULTIPLIER;
    }

    function _calcPendingReward(
        uint256 _sumPower,
        uint256 _accMelosPerPower,
        uint256 _debt
    ) private pure returns (uint256) {
        return _calcRewardDebt(_sumPower, _accMelosPerPower) - _debt;
    }

    // pay rewards to user if any. The rewards will be locked for specified period of time.
    function _payPendingRewardIfAvailable(address sender, UserInfo storage user)
        private
    {
        if (user.sumPower > 0) {
            uint256 _pending = _calcPendingReward(
                user.sumPower,
                poolInfo.accMelosPerPower,
                user.rewardDebt
            );
            if (_pending > 0) {
                EnumerableMap.UintToUintMap storage timeLock = timeLockReward[
                    sender
                ];
                uint256 releaseBlock = block.number + lockedPeroid;
                (bool succ, uint256 balance) = timeLock.tryGet(releaseBlock);
                if (succ) {
                    _pending += balance;
                }

                timeLock.set(releaseBlock, _pending);
            }
        }
    }

    function _calcAddition(UserInfo storage _user, uint256[] memory _ids)
        private
        view
        returns (uint256 numMinerNFTs, uint256 sumPower)
    {
        numMinerNFTs = _user.numMinerNFTs + _ids.length;
        sumPower = _user.sumPower;

        for (uint256 i = 0; i < _ids.length; i++) {
            uint256 id = _ids[i];
            require(id != 0, "NFTFarming: invalid minerNFT");

            uint256 power = minerNFT.mpower(id);
            require(power > 0, "NFTFarming: invalid minerNFT power");
            sumPower += power;
        }
    }

    function _calcDeletion(
        address owner,
        UserInfo storage _user,
        uint256[] memory _ids
    ) private view returns (uint256 remainNumMinerNFT, uint256 remainPower) {
        remainNumMinerNFT = _user.numMinerNFTs - _ids.length;
        remainPower = _user.sumPower;

        EnumerableSet.UintSet storage _minerNFTs = userMinerNFTs[owner];
        for (uint256 i = 0; i < _ids.length; i++) {
            uint256 id = _ids[i];
            require(
                _minerNFTs.contains(id),
                "minerNFTFarm: original owner not found"
            );

            uint256 power = minerNFT.mpower(id);
            remainPower -= power;
        }
    }

    // just in case if rounding error causes pool to not have enough MELOSs.
    function _safeMELOSTransfer(address _to, uint256 _amount) private {
        uint256 balance = melos.balanceOf(address(this));
        require(balance >= _amount, "NFTFarming: out of balance");
        melos.safeTransfer(_to, _amount);
    }

    function _safeNFTTransferFrom(
        address _from,
        address _to,
        uint256[] memory _ids
    ) private {
        uint256[] memory amounts = new uint256[](_ids.length);
        for (uint256 i = 0; i < _ids.length; i++) {
            amounts[i] = 1;
        }
        minerNFT.safeBatchTransferFrom(_from, _to, _ids, amounts, new bytes(0));
    }

    function _nftInPool(address _user) private view returns (uint256[] memory) {
        EnumerableSet.UintSet storage _minerNFTs = userMinerNFTs[_user];
        return _minerNFTs.values();
    }
}
