     * - the caller must have a balance of at least `amount`.
     * - releaseTime.
     */
    function transferWithLock(address recipient, uint256 amount, uint256 releaseTime) public onlyOwner returns (bool) {
        require(recipient != address(0), "OPA: lockup zero address");
        require(releaseTime > block.timestamp, "OPA: release time before lock time");
        require(_timelock[recipient].releaseTime == 0, "OPA: already locked");

        TimeLock memory timelock = TimeLock({
            releaseTime : releaseTime,
            amount      : amount
        });
        _timelock[recipient] = timelock;
        
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

