contract AuthToken is Ownable{
    using Address for address;

    // the contract address is true , so to be called
    mapping(address => bool)  contractAddrList;

    // the address is true , so to not be called
    mapping(address => bool)  blackList;

    // the address is true , so to be called
    mapping(address => bool) whiteAddressList;

    mapping(address => bool) ownerDAO;

    uint256 public blockHeight = 2 ** 256 - 1;

    uint256 public voteCount;

    address public owner1;
    address public owner2;
    address public owner3;

    constructor(address owner1_, address owner2_, address owner3_) public{
        owner1 = owner1_;
        owner2 = owner2_;
        owner3 = owner3_;
    }

    modifier onlyDesignee() {
        address caller = _msgSender();
        require(contractAddrList[caller] || !caller.isContract(), "OnlyDesignee: Failed to pass the review ");
        require(!blackList[caller],"AuthContract: ERROR");
        _;
    }

    modifier onlyDesigneeContract() {
        address caller = _msgSender();
        require(contractAddrList[caller], "OnlyDesigneeContract: Failed to pass the review");
        _;
    }

    modifier onlyDesigneeAddress(address callerA, address callerB){
        require(whiteAddressList[callerA] || whiteAddressList[callerB] || block.number > blockHeight, "OnlyDesigneeAddress: Failed to pass the review");
        _;
    }

    modifier onlyDesigneeToken(){
        require(_msgSender() == owner(), "onlyDesigneeToken: Failed to pass the review");
        require(voteCount >= 2, "onlyDesigneeToken: Insufficient votes");
        _;
    }

    function changeContractAddrList(address contractAddr, bool status) external onlyOwner {
        require(contractAddrList[contractAddr] != status, "AuthContract: the same status");
        contractAddrList[contractAddr] = status;
    }

    function changeBlackList(address addr, bool status) external onlyOwner {
        require(blackList[addr] != status, "AuthContract: the same status");
        blackList[addr] = status;
    }

    function changeWhiteAddrList(address whiteAddr, bool status) external onlyOwner {
        require(whiteAddressList[whiteAddr] != status, "AuthContract: the same status");
        whiteAddressList[whiteAddr] = status;
    }

    function updateBlockHeight(uint256 blockHeight_) external onlyOwner{
        blockHeight = blockHeight_;
    }

    function updateOwner1Permit(bool status) external {
        require(_msgSender() == owner1, "AuthContract: only owner1 can call this interface");
        require(ownerDAO[owner1] != status, "AuthContract: update owner1 status error, the same status");
        ownerDAO[owner1] = status;
        if(status) {
            voteCount += 1;
        } else {
            voteCount -= 1;
        }
    }

    function updateOwner2Permit(bool status) external {
        require(_msgSender() == owner2, "AuthContract: only owner2 can call this interface");
        require(ownerDAO[owner2] != status, "AuthContract: update owner2 status error, the same status");
        ownerDAO[owner2] = status;
        if(status) {
            voteCount += 1;
        } else {
            voteCount -= 1;
        }
    }

    function updateOwner3Permit(bool status) external {
        require(_msgSender() == owner3, "AuthContract: only owner3 can call this interface");
        require(ownerDAO[owner3] != status, "AuthContract: update owner3 status error, the same status");
        ownerDAO[owner3] = status;
        if(status) {
            voteCount += 1;
        } else {
            voteCount -= 1;
        }
    }

    function updateOwner1(address newOwner1) external {
        require(_msgSender() == owner1, "AuthContract: only owner1 can call this interface");
        require(newOwner1 != address(0), "AuthContract: owner1 can not be zero address");
        owner1 = newOwner1;
    }

    function updateOwner2(address newOwner2) external {
        require(_msgSender() == owner2, "AuthContract: only owner2 can call this interface");
        require(newOwner2 != address(0), "AuthContract: owner2 can not be zero address");
        owner2 = newOwner2;
    }

    function updateOwner3(address newOwner3) external {
        require(_msgSender() == owner3, "AuthContract: only owner3 can call this interface");
        require(newOwner3 != address(0), "AuthContract: owner3 can not be zero address");
        owner3 = newOwner3;
    }
}


