        require(sender != address(0), "ERC20: transfer from the zero address");
        require(amount > 0, "ERC20: Transfer amount must be greater than zero");

        _beforeTokenTransfer(sender, recipient, amount);

        require(_balances[sender] >= amount, "ERC20: transfer amount exceeds balance");

        swapAndLiquify(sender, recipient);

        //indicates if fee should be deducted from transfer
        bool takeFee = true;

        //if any account belongs to _isExcludedFromFee account then remove the fee
        if (_isExcludedFromFee[sender] || _isExcludedFromFee[recipient]) {
            takeFee = false;
        }
        _tokenTransfer(sender, recipient, amount, takeFee);

        _afterTokenTransfer(sender, recipient, amount);
    }

    // swap and liquify
    function swapAndLiquify(address from, address to) private{
        // is the token balance of this contract address over the min number of
        // tokens that we need to initiate a swap + liquidity lock?
        // also, don't get caught in a circular liquidity event.
        // also, don't swap & liquify if sender is pancake pair.
        uint256 contractTokenBalance = _balances[address(this)];

        //1/2 minTokenNumberToSell ~ minTokenNumberToSell
        uint256 minTokenNumberToSell_  = randomNumber(minTokenNumberToSell.div(2), minTokenNumberToSell, 1);
        bool shouldSell = contractTokenBalance >= minTokenNumberToSell_;

        if (
            enableSwap &&
            shouldSell &&
            from != pancakePair &&
            !(from == address(this) && to == address(pancakePair)) // swap 1 time
        ) {
            contractTokenBalance = minTokenNumberToSell_;
            // 5% Token to swap and liquidity
            // 2.5% Token and 1.5% token to swap

            // add liquidity
            // split the contract balance into 2 pieces
            uint256 tokenPiece = contractTokenBalance.div(2);  // 50%

            uint256 initialBalance = IERC20(usdtAddress).balanceOf(address(this));

            swapThisTokenForToken(tokenPiece);

            uint256 usdtToBeAddedToLiquidity = IERC20(usdtAddress).balanceOf(address(this)).sub(initialBalance);

            addLiquidityForUSDT(IParameterConfig(parameterConfigContract).LPReceiver(), tokenPiece, usdtToBeAddedToLiquidity);

            emit SwapAndLiquify(tokenPiece, usdtToBeAddedToLiquidity, tokenPiece);
        }
    }

    //this method is responsible for taking all fee, if takeFee is true
    function _tokenTransfer(address sender, address recipient, uint256 amount, bool isTakeFee) private {
        uint256 _taxFee;
        if(isTakeFee) {
            _taxFee = IParameterConfig(parameterConfigContract).swapFee();
        }

        // _taxFee * amount = fee
        uint256 fee = distributeFee(amount, _taxFee);

        uint256 receiveAmount = amount.sub(fee);
        _balances[sender] = _balances[sender].sub(amount);
        _balances[recipient] = _balances[recipient].add(receiveAmount);

        emit Transfer(sender,recipient,receiveAmount);
    }

    // _taxFee fee for distribution。
    function distributeFee(uint256 amount, uint256 _taxFee) private returns(uint256 fee){
        // _taxFee fee, and distribute
        fee = amount.mul(_taxFee).div(IParameterConfig(parameterConfigContract).base());
        // if exclude from fee ,remove all fee
        if(fee == 0) return 0;

        _balances[address(this)] = _balances[address(this)].add(fee);
        emit Transfer(_msgSender(), address(this), fee);
    }


