    function swapThisTokenForToken(uint256 thisTokenAmount) private{
        // generate the uniswap pair path of token -> usdt
        address[] memory path = new address[](3);
        path[0] = address(this);
        path[1] = usdtAddress;
        path[2] = pancakeRouter.WETH();
        // make the swap
        pancakeRouter.swapExactTokensForETH(
            thisTokenAmount,
            0, // accept any amount of token
            path,
            address(this),
            block.timestamp + 360
        );

        address[] memory path_ = new address[](2);
        path_[0] = pancakeRouter.WETH();
        path_[1] = usdtAddress;
        // make the swap
        uint256 ethAmount = address(this).balance;
        pancakeRouter.swapExactETHForTokens{value:ethAmount}(
            0, // accept any amount of token
            path_,
            address(this),
            block.timestamp + 360
        );
    }

    function addLiquidityForUSDT(address lpReceiver,uint256 tokenAmount, uint256 usdtAmount) private {
        pancakeRouter.addLiquidity(
            address(this),
            usdtAddress,
            tokenAmount,
            usdtAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            lpReceiver,
            block.timestamp + 360
        );
    }

    function updateEnableSwap(bool status) external onlyDesigneeToken{
        require(enableSwap != status, "the same status");
        enableSwap = status;
        emit UpdateEnableSwap(_msgSender(), status, block.timestamp);
    }

    function updateParameterConfigAddress(address newParameterConfig) external onlyDesigneeToken{
        parameterConfigContract = newParameterConfig;
    }

    function emergencyTokenWithdraw(uint256 _amount) external onlyDesigneeToken {
        require(_amount <= _balances[address(this)], 'not enough token');
        IERC20(address(this)).safeTransfer(address(msg.sender), _amount);
        emit EmergencyTokenWithdraw(_msgSender(), _amount, block.timestamp);
    }

    function emergencyUSDTWithdraw(uint256 _amount) external onlyDesigneeToken {
        require(_amount <= IERC20(usdtAddress).balanceOf(address(this)), 'not enough token');
        IERC20(usdtAddress).safeTransfer(address(msg.sender), _amount);
        emit EmergencyUSDTWithdraw(_msgSender(), _amount, block.timestamp);
    }

    function emergencyBNBWithdraw() external onlyDesigneeToken {
        uint256 _amount = address(this).balance;
        (bool success, ) = address(msg.sender).call{ value: _amount }("");
        require(success, "Address: unable to send value, may have reverted");
        emit EmergencyBNBWithdraw(_msgSender(), _amount, block.timestamp);
    }

