      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.13;

import "solmate/auth/Owned.sol";
import {IERC20} from "./interfaces/IERC20.sol";

contract Staking is Owned {
    mapping(address => uint256) public stakedOf;
    address public immutable ts;

    event Staked(address user, uint256 amount, uint256 blockTime);

    constructor(address _ts) Owned(msg.sender) {
        ts = _ts;
    }

    function stake(uint256 amount) external {
        require(IERC20(ts).transferFrom(msg.sender, address(0xdead), amount), "ok");

        stakedOf[msg.sender] = stakedOf[msg.sender] + amount;
        emit Staked(msg.sender, amount, block.timestamp);
    }
}
