{"BEP20Shisha.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import { ERC20Freezable } from './ERC20Freezable.sol';

contract Shish is ERC20Freezable {
    constructor(uint256 initialSupply) ERC20Freezable("Shisha", "SHISH") {
        _mint(msg.sender, initialSupply);
    }

    function increaseTotalSupply(uint256 amount) public onlyOwner {
        require(amount > 0, "Amount should be greater than 0");
        _mint(owner, amount);
    }
}
