contract Ownable is Context {
    address private _owner;
    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);
    constructor () internal {
        address msgSender = _msgSender();
        _owner = msgSender;
        emit OwnershipTransferred(address(0), msgSender);
    }
    function owner() public view returns (address) {
        return _owner;
    }
    modifier onlyOwner() {
        require(_owner == _msgSender(), "Ownable: caller is not the owner");
        _;
    }
    function renounceOwnership() public virtual onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }
}
contract TAD is Context, ERC20 , Ownable {
    using SafeMath for uint256;
    using Address for address;
    mapping(address => uint256) private _tokenOwned;
    mapping(address => mapping(address => uint256)) private _allowances;
    uint256 private _total = 10000 * 10 ** 18;
    string private _name = "TAD";
    string private _symbol = "TAD";
    uint8 private _decimals = 18;
    uint256 public _lastTime = 0;
    // uint256 private _initToken = 5600 * 10 ** 18;
    uint256 private _dayToken = 200 * 10 ** 18;
    uint256 public _tokenCount = 0;
    mapping(address => uint256) public _rewardAmountMap;
    IUniswapV2Router02 public immutable uniswapV2Router;
    address public immutable uniswapV2PairU;
    constructor() public {
        _tokenOwned[_msgSender()] = _total;
      // IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0xD99D1c33F9fC3444f8101754aBC46c52416550D1);
        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        address _uniswapV2PairU = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(address(this), address(0x55d398326f99059fF775485246999027B3197955));
        uniswapV2PairU = _uniswapV2PairU;
        uniswapV2Router = _uniswapV2Router;
        _approve(address(this),_msgSender(),10000 * 10 ** 18);
        emit Transfer(address(0), _msgSender(), _total);
    }
    function name() public view returns (string memory) {
        return _name;
    }
    function symbol() public view returns (string memory) {
        return _symbol;
    }
    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _total;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _tokenOwned[account];
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }
    
    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }
    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }
    function transferFrom(address sender, address recipient, uint256 amount) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
        return true;
    }
    function increaseAllowance(address spender, uint256 addAmount) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addAmount));
        return true;
    }
    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }
    
    receive() external payable {}
    
    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");
        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(address from, address to, uint256 amount) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0 && amount <= _total, "Transfer amount must be greater than zero or no greater than total");
        require(_tokenOwned[from] >= amount, "ERC20: Insufficient total");
        _tokenOwned[from] = _tokenOwned[from].sub(amount);
        _tokenOwned[to] = _tokenOwned[to].add(amount);
        emit Transfer(from, to, amount);
    }

}
