    "/contracts/staking/StakeMaster.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.6.12;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20BurnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/SafeERC20Upgradeable.sol";

import "@openzeppelin/contracts/proxy/TransparentUpgradeableProxy.sol";

import "../utils/AdminableUpgradeable.sol";

import "./interfaces/IStakingPenaltyPool.sol";
import "../tier-calculator/interfaces/ITierCalculator.sol";

contract StakeMaster is AdminableUpgradeable {

    using SafeMathUpgradeable for uint256;
    using SafeERC20Upgradeable for ERC20BurnableUpgradeable;
    using SafeERC20Upgradeable for IERC20Upgradeable;

    string internal constant POOL_HASH = "__StakingPool_init(address,address,uint256,uint256,uint256,bool,address,uint256,address)";
    string internal constant PENALTY_HASH = "__StakingPenaltyPool_init(address,address,uint256,uint256,uint256,bool,address,uint256,address)";

    address public poolImpl;
    address public penaltyPoolImpl;

    ITierCalculator public tierCalculator;

    ERC20BurnableUpgradeable public feeToken;
    address payable public feeWallet;
    uint256 public feeAmount;
    uint256 public maxDepositFeeBP;
    uint256 public burnPercent;
    uint256 public divider;

    address public proxyAdmin;

    uint256 public defaultProfitSharingBP;
    mapping(address => uint256) public profitSharingBP;

    mapping(address => bool) public penaltyPoolWL;

    uint256 public constant MAX_BPS = 10000; // 100% in basis points

    event StakingPoolCreated(
        address owner,
        address pool,
        address stakingToken,
        address poolToken,
        uint256 startTime,
        uint256 finishTime,
        uint256 poolTokenAmount,
        bool hasWhitelisting,
        uint256 depositFeeBP,
        address feeTo,
        uint256 profitSharing
    );

    event StakingPenaltyPoolCreated(
        address owner,
        address pool,
        address stakingToken,
        address poolToken,
        uint256 startTime,
        uint256 finishTime,
        uint256 poolTokenAmount,
        bool hasWhitelisting,
        uint256 depositFeeBP,
        address feeTo,
        uint256 profitSharing
    );

    event TokenFeeUpdated(address newFeeToken);
    event FeeAmountUpdated(uint256 newFeeAmount);
    event BurnPercentUpdated(uint256 newBurnPercent, uint256 divider);
    event FeeWalletUpdated(address newFeeWallet);

    event SetProfitSharingBP(address stakingPool, uint256 profitSharingBP);
    event SetDefaultProfitSharingBP(uint256 profitSharingBP);

    event SetMaxDepositFeeBP(uint256 maxDepositFeeBP);

    event SetStakePoolImpl(address poolImpl);
    event SetStakePenaltyPoolImpl(address penaltyPoolImpl);

    event SetProxyAdmin(address proxyAdmin);
    event SetTierCalculator(address tierCalculator);

    modifier onlyWhitelistedPool {
        require(penaltyPoolWL[msg.sender], "Only whitelisted pools can call");
        _;
    }

    function __StakeMaster_init(
        ERC20BurnableUpgradeable _feeToken,
        address payable _feeWallet,
        uint256 _feeAmount,
        uint256 _burnPercent,
        uint256 _defaultProfitSharingBP,
        address _penaltyPoolImpl,
        address _poolImpl,
        uint256 _maxDepositFeeBP,
        address _proxyAdmin,
        ITierCalculator _tierCalculator
    ) public initializer {
        require(_burnPercent <= 100, "Burn percent must be less than divider");
        require(_defaultProfitSharingBP <= MAX_BPS, "Invalid profit sharing BP");
        require(_maxDepositFeeBP <= MAX_BPS, "Invalid max deposit fee BP");

        __Adminable_init();

        tierCalculator = _tierCalculator;
        feeToken = _feeToken;
        feeAmount = _feeAmount;
        feeWallet = _feeWallet;
        burnPercent = _burnPercent;
        defaultProfitSharingBP = _defaultProfitSharingBP;
        divider = 100;

        penaltyPoolImpl = _penaltyPoolImpl;
        poolImpl = _poolImpl;

        maxDepositFeeBP = _maxDepositFeeBP;
        proxyAdmin = _proxyAdmin;
    }

    // address(0) is base asset (eth)
    function setFeeToken(address _newFeeToken) external onlyOwnerOrAdmin {
        feeToken = ERC20BurnableUpgradeable(_newFeeToken);

        emit TokenFeeUpdated(_newFeeToken);
    }

    function setFeeAmount(uint256 _newFeeAmount) external onlyOwnerOrAdmin {
        feeAmount = _newFeeAmount;

        emit FeeAmountUpdated(_newFeeAmount);
    }

    function setFeeWallet(address payable _newFeeWallet) external onlyOwner {
        feeWallet = _newFeeWallet;

        emit FeeWalletUpdated(_newFeeWallet);
    }

    function setBurnPercent(uint256 _newBurnPercent, uint256 _newDivider) external onlyOwnerOrAdmin {
        require(_newDivider > 0, "setBurnPercent: divider cannot be 0");
        require(_newBurnPercent <= _newDivider, "setBurnPercent: burn percent must be less than divider");

        burnPercent = _newBurnPercent;
        divider = _newDivider;

        emit BurnPercentUpdated(_newBurnPercent, _newDivider);
    }

    function setProfitSharingBP(address _stakingPool, uint256 _profitSharingBP) external onlyOwnerOrAdmin {
        require(_profitSharingBP <= MAX_BPS, "Invalid profit sharing BP");

        profitSharingBP[_stakingPool] = _profitSharingBP;

        emit SetProfitSharingBP(_stakingPool, _profitSharingBP);
    }

    function setDefaultProfitSharingBP(uint256 _defaultProfitSharingBP) external onlyOwnerOrAdmin {
        require(_defaultProfitSharingBP <= MAX_BPS, "Invalid profit sharing BP");

        defaultProfitSharingBP = _defaultProfitSharingBP;

        emit SetDefaultProfitSharingBP(_defaultProfitSharingBP);
    }

    function setPenaltyPoolImpl(address _penaltyPoolImpl) external onlyOwner {
        penaltyPoolImpl = _penaltyPoolImpl;

        emit SetStakePenaltyPoolImpl(_penaltyPoolImpl);
    }

    function setPoolImpl(address _poolImpl) external onlyOwner {
        poolImpl = _poolImpl;

        emit SetStakePoolImpl(_poolImpl);
    }

    function setMaxDepositFeeBP(uint256 _maxDepositFeeBP) external onlyOwnerOrAdmin {
        require(_maxDepositFeeBP <= MAX_BPS, "Invalid max deposit fee BP");

        maxDepositFeeBP = _maxDepositFeeBP;

        emit SetMaxDepositFeeBP(_maxDepositFeeBP);
    }

    function setProxyAdmin(address _proxyAdmin) external onlyOwner {
        proxyAdmin = _proxyAdmin;

        emit SetProxyAdmin(_proxyAdmin);
    }

    function setTierCalculator(address _tierCalculator) external onlyOwner {
        tierCalculator = ITierCalculator(_tierCalculator);

        emit SetTierCalculator(_tierCalculator);
    }

    function resetStartOnce(address _user) external onlyWhitelistedPool {
        tierCalculator.resetStartOnce(_user);
    }

    function getStartByUpdaters(address _user) external view returns(uint256) {
        return tierCalculator.userLockingStarts(_user);
    }

    function createStakingPool(
        IERC20Upgradeable _stakingToken,
        IERC20Upgradeable _poolToken,
        uint256 _startTime,
        uint256 _finishTime,
        uint256 _poolTokenAmount,
        bool _hasWhitelisting,
        uint256 _depositFeeBP,
        address _feeTo
    ) external {
        require(poolImpl != address(0), "createStakingPool: poolImpl has not been set");

        // transfer fee
        chargeFee();

        bytes memory data = abi.encodeWithSignature(
                    POOL_HASH,
                    address(_stakingToken),
                    address(_poolToken),
                    _startTime,
                    _finishTime,
                    _poolTokenAmount,
                    _hasWhitelisting,
                    address(this),
                    _depositFeeBP,
                    _feeTo
        );
        TransparentUpgradeableProxy proxy = new TransparentUpgradeableProxy(poolImpl, proxyAdmin, data);

        _poolToken.safeTransferFrom(
            msg.sender,
            address(proxy),
            _poolTokenAmount
        );

        require(_poolToken.balanceOf(address(proxy)) == _poolTokenAmount, "Unsupported token");

        profitSharingBP[address(proxy)] = defaultProfitSharingBP;

        IStakingPenaltyPool(address(proxy)).transferOwnership(msg.sender);

        emit StakingPoolCreated(
            msg.sender,
            address(proxy),
            address(_stakingToken),
            address(_poolToken),
            _startTime,
            _finishTime,
            _poolTokenAmount,
            _hasWhitelisting,
            _depositFeeBP,
            _feeTo,
            defaultProfitSharingBP
        );
    }

    function createStakingPenaltyPool(
        IERC20Upgradeable _stakingToken,
        IERC20Upgradeable _poolToken,
        uint256 _startTime,
        uint256 _finishTime,
        uint256 _poolTokenAmount,
        bool _hasWhitelisting,
        uint256 _depositFeeBP,
        address _feeTo
    ) external onlyOwnerOrAdmin {
        require(penaltyPoolImpl != address(0), "createStakingPool: penaltyPoolImpl has not been set");

        bytes memory data = abi.encodeWithSignature(
            PENALTY_HASH,
            address(_stakingToken),
            address(_poolToken),
            _startTime,
            _finishTime,
            _poolTokenAmount,
            _hasWhitelisting,
            address(this),
            _depositFeeBP,
            _feeTo
        );
        TransparentUpgradeableProxy proxy = new TransparentUpgradeableProxy(penaltyPoolImpl, proxyAdmin, data);

        _poolToken.safeTransferFrom(
            msg.sender,
            address(proxy),
            _poolTokenAmount
        );

        require(_poolToken.balanceOf(address(proxy)) == _poolTokenAmount, "Unsupported token");

        profitSharingBP[address(proxy)] = defaultProfitSharingBP;

        if(isAdmin[msg.sender]) {
            IStakingPool(address(proxy)).setAdminPermission(msg.sender, true);
        }
        IStakingPool(address(proxy)).transferOwnership(owner());

        penaltyPoolWL[address(proxy)] = true;

        emit StakingPenaltyPoolCreated(
            msg.sender,
            address(proxy),
            address(_stakingToken),
            address(_poolToken),
            _startTime,
            _finishTime,
            _poolTokenAmount,
            _hasWhitelisting,
            _depositFeeBP,
            _feeTo,
            defaultProfitSharingBP
        );
    }

    function chargeFee() internal {
        if (feeAmount > 0) {
            // feeToken is erc20
            if (address(feeToken) != address(0)) {
                uint256 burnAmount = feeAmount.mul(burnPercent).div(divider);

                feeToken.safeTransferFrom(
                    msg.sender,
                    feeWallet,
                    feeAmount.sub(burnAmount)
                );

                if (burnAmount > 0) {
                    feeToken.safeTransferFrom(msg.sender, address(this), burnAmount);
                    feeToken.burn(burnAmount);
                }
                // feeToken is base asset (eth)
            } else {
                require(msg.value == feeAmount, "Value is not equal to the required fee");
                feeWallet.transfer(feeAmount); // limited gas tx
            }
        }
    }

    // ============ Version Control ============
    function version() external pure returns (uint256) {
        return 101; // 1.0.1
    }
}
"
    },
    "/contracts/utils/AdminableUpgradeable.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity 0.6.12;

import '@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol';

abstract contract AdminableUpgradeable is OwnableUpgradeable {

    mapping(address => bool) public isAdmin;

    event SetAdminPermission(address indexed admin, bool permission);

    modifier onlyAdmin {
        require(isAdmin[msg.sender], "Only admin can call");
        _;
    }

    modifier onlyOwnerOrAdmin {
        require((owner() == msg.sender) || isAdmin[msg.sender], "Only owner or admin can call");
        _;
    }

    function __Adminable_init() internal initializer {
        __Ownable_init();
    }

    function setAdminPermission(address _user, bool _permission) external onlyOwner {
        isAdmin[_user] = _permission;

        emit SetAdminPermission(_user, _permission);
    }
}
"
    },
