contract JuiceRewards is Ownable {
    event Claimed(address indexed beneficiary, uint256 amount);
    mapping (address => bool) public beneficiaries;
    mapping (address => uint) public claimDates;
    uint256 public rAmount; 
    uint256 public interval;

    constructor() {
        interval = 30 days;
    }

    receive() external payable {
        
    }

    function setRAmount(uint256 amount) external onlyOwner {
        rAmount = amount;
    }

    function setBeneficiary(address beneficiary, bool active) external onlyOwner {
        beneficiaries[beneficiary] = active;
    }

    function setClaimDate(address beneficiary, uint256 timestamp) external onlyOwner {
        claimDates[beneficiary] = timestamp;
    }

    function claim() public {
        require(beneficiaries[msg.sender], "Not registered beneficiary");
        require(block.timestamp - claimDates[msg.sender] >= interval, "Interval");

        claimDates[msg.sender] = block.timestamp;

        uint256 claimAmount = rAmount;
        if (claimAmount > address(this).balance) {
            claimAmount = address(this).balance;
        }

        require(claimAmount > 0, "Nothing to claim");
        payable(msg.sender).transfer(claimAmount);

        emit Claimed(msg.sender, claimAmount);
    }

	function withdraw() external onlyOwner {
        payable(owner()).transfer(address(this).balance);
    }
	
    function withdrawTokens(IBEP20 token) external onlyOwner {
        uint256 tokens = token.balanceOf(address(this));
        token.transfer(owner(), tokens);
    }
}
