pragma solidity ^0.8.10;




/**
 * Token Standard Vesting Contract
 */
contract TokenVesting is Ownable {
    using SafeMath for uint256;

    struct Schedule {
        uint256 totalAmount;
        uint256 claimedAmount;
        uint256 startTime;
        uint256 cliffTime;
        uint256 endTime;
        uint256 vestType; // 锁仓类型描述
    }

    // user => scheduleId => schedule
    mapping(address => mapping(uint256 => Schedule)) public schedules;
    mapping(address => uint256) public numberOfSchedules;
    uint256 public lockBalance = 0;
    uint256 public openClaimTime = 0;
    address public tokenAddress = address(0);

    // events
    event Claim(address indexed claimer, uint256 amount);
    event Vest(address indexed to, uint256 amount);
    event Cancelled(address account, uint256 amount);

    function vest(
        address account,
        uint256 amount,
        uint256 cliffSeconds,
        uint256 vestingSeconds,
        uint256 startTime,
        uint256 vestType
    ) public onlyOwner {
        require(
            vestingSeconds > 0 && 
            vestingSeconds > cliffSeconds &&
            amount > 0,
            "Vesting: invalid vesting params"
        );

        // create the schedule
        uint256 currentNumSchedules = numberOfSchedules[account];
        schedules[account][currentNumSchedules] = Schedule(
            amount,
            0,
            startTime,
            startTime + cliffSeconds,
            startTime + vestingSeconds,
            vestType
        );
        numberOfSchedules[account] = currentNumSchedules + 1;
        lockBalance = lockBalance + amount;
        emit Vest(account, amount);
    }

    function multiVest(
        address[] calldata accounts,
        uint256[] calldata amount,
        uint256 cliffSeconds,
        uint256 vestingSeconds,
        uint256 startTime,
        uint256 vestType
    ) external onlyOwner {
        uint256 numberOfAccounts = accounts.length;
        require(
            amount.length == numberOfAccounts,
            "Vesting: Array lengths differ"
        );
        for (uint256 i = 0; i < numberOfAccounts; i++) {
            vest(
                accounts[i],
                amount[i],
                cliffSeconds,
                vestingSeconds,
                startTime,
                vestType
            );
        }
    }

    function getClaimBalance(Schedule memory schedule) internal view returns (uint256){
        if (openClaimTime == 0) {
            return 0;
        }

        if (schedule.totalAmount == 0 || block.timestamp < schedule.cliffTime) {
            return 0;
        }

        // Get the amount to be distributed
        uint256 initTime = schedule.startTime == 0 ? openClaimTime : 0;
        uint256 amount = calcDistribution(
            schedule.totalAmount,
            block.timestamp,
            initTime + schedule.cliffTime,
            initTime + schedule.endTime
        );

        // Cap the amount at the total amount
        amount = amount > schedule.totalAmount ? schedule.totalAmount : amount;
        return amount - schedule.claimedAmount;
    }

    function calcDistribution(
        uint256 amount,
        uint256 currentTime,
        uint256 cliffTime,
        uint256 endTime
    ) public pure returns (uint256) {
        // avoid uint underflow
        if (currentTime < cliffTime) {
            return 0;
        }
        
        // if endTime < cliffTime, this will throw. Since endTime should never be
        // less than cliffTime in safe operation, this is fine.
        return (amount * (currentTime - cliffTime) / (endTime - cliffTime));
    }

    /**
     * @notice Withdraws tokens from the contract.
     * @dev blocks withdrawing locked tokens.
     */
    function withdraw() external onlyOwner {
        IERC20 token = IERC20(tokenAddress);
        uint256 amount = token.balanceOf(address(this));
        token.transfer(owner(), amount);
    }

    function getScheduleNumber() public view returns (uint256){
        return numberOfSchedules[msg.sender];
    }
    
    function getSchedule(address account, uint256 scheduleNumber) public view returns (uint256, //totalAmount
        uint256, // claimedAmount
        uint256, // startTime;
        uint256, // cliffTime;
        uint256, // endTime
        uint256, // vestType
        uint256  // claim balance
    ) {
        Schedule storage schedule = schedules[account][scheduleNumber];
        uint256 initTime = (openClaimTime > 0 && schedule.startTime == 0) ? openClaimTime : 0;
        return (schedule.totalAmount,
                schedule.claimedAmount,
                initTime + schedule.startTime,
                initTime + schedule.cliffTime,
                initTime + schedule.endTime,
                schedule.vestType,
                getClaimBalance(schedule));
    }

    /**
     * @notice allows users to claim vested tokens if the cliff time has passed.
     * @param scheduleNumber which schedule the user is claiming against
     */
    function claim(uint256 scheduleNumber) external {
        require(openClaimTime > 0, "Vesting: not open claim");

        Schedule storage schedule = schedules[msg.sender][scheduleNumber];
        require(
            schedule.cliffTime <= block.timestamp,
            "Vesting: cliff not reached"
        );
        require(schedule.totalAmount > 0, "Vesting: not claimable");

        // Get the amount to be distributed
        uint256 amount = getClaimBalance(schedule);
        require(amount > 0, "Vesting: not claimable");

        // Cap the amount at the total amount
        schedule.claimedAmount += amount; // set new claimed amount based 
        lockBalance = lockBalance.sub(amount);
        IERC20(tokenAddress).transfer(msg.sender, amount);
        emit Claim(msg.sender, amount);
    }
    
    /**
     * @notice Allows a vesting schedule to be cancelled.
     * @dev Any outstanding tokens are returned to the system.
     * @param account the account of the user whos vesting schedule is being cancelled.
     */
    function rug(address account, uint256 scheduleId) external onlyOwner {
        Schedule storage schedule = schedules[account][scheduleId];
        uint256 outstandingAmount = schedule.totalAmount - schedule.claimedAmount;
        require(outstandingAmount != 0, "Vesting: no outstanding tokens");
        schedule.totalAmount = 0;
        lockBalance = lockBalance.sub(outstandingAmount);
        emit Cancelled(account, outstandingAmount);
    }

    function setTokenAddress(address token) public onlyOwner {
        tokenAddress = token;
    }

    function openClaim() public onlyOwner {
        openClaimTime = block.timestamp;
    }
}
