    "./contracts/MiniBearToken.sol": {
      "content": "// SPDX-License-Identifier: NO LICENSE
pragma solidity ^0.7.0;

import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/math/SafeMath.sol";
import "./utils/Ownable.sol";
import "./ERC165/IERC165.sol";
import "./ERC20/IERC20.sol";
import "./IBearNBearToken.sol";

/**
 *
 * MiniBearToken Contract (The native token of BearNBear)
 * @dev Extends standard ERC20 contract
 */
contract MiniBearToken is Context, Ownable, IERC20 {
    using SafeMath for uint256;

    // Constants
    uint256 public SECONDS_IN_A_DAY = 86400;

    // Public variables
    uint256 public emissionStart;

    uint256 public emissionEnd;

    uint256 public emissionPerDay = 1 * (10 ** 18); // multiple by the mutiplier

    uint256 public constant PRE_REVEAL_MULTIPLIER = 2;

    mapping (address => uint256) private _balances;

    mapping (address => mapping (address => uint256)) private _allowances;

    mapping(uint256 => uint256) private _lastClaim;

    uint256 private _totalSupply;

    string private _name;
    string private _symbol;
    uint8 private _decimals;
    address private _bbtAddress;

    /**
     * @dev Sets the values for {name} and {symbol}, initializes {decimals} with
     * a default value of 18. Also initalizes {emissionStart}
     *
     * To select a different value for {decimals}, use {_setupDecimals}.
     *
     * All three of these values are immutable: they can only be set once during
     * construction.
     */
    constructor (string memory name, string memory symbol, uint256 emissionStartTimestamp) {
        _name = name;
        _symbol = symbol;
        _decimals = 18;
        emissionStart = emissionStartTimestamp;
        emissionEnd = emissionStart + (86400 * 365 * 10);
    }

    /**
     * @dev Returns the name of the token.
     */
    function name() public view returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() public view returns (string memory) {
        return _symbol;
    }

    /**
     * @dev Returns the number of decimals used to get its user representation.
     * For example, if `decimals` equals `2`, a balance of `505` tokens should
     * be displayed to a user as `5,05` (`505 / 10 ** 2`).
     *
     * Tokens usually opt for a value of 18, imitating the relationship between
     * Ether and Wei. This is the value {ERC20} uses, unless {_setupDecimals} is
     * called.
     *
     * NOTE: This information is only used for _display_ purposes: it in
     * no way affects any of the arithmetic of the contract, including
     * {IERC20-balanceOf} and {IERC20-transfer}.
     */
    function decimals() public view returns (uint8) {
        return _decimals;
    }

    /**
     * @dev See {IERC20-totalSupply}.
     */
    function totalSupply() public view override returns (uint256) {
        return _totalSupply;
    }

    /**
     * @dev See {IERC20-balanceOf}.
     */
    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    /**
     * @dev When getAccumulated MBT have last been claimed for a BearNBear index
     */
    function lastClaim(uint256 tokenIndex) public view returns (uint256) {
        require(IBearNBearToken(_bbtAddress).ownerOf(tokenIndex) != address(0), "Owner cannot be 0 address");
        require(tokenIndex < IBearNBearToken(_bbtAddress).accumulatedSupply(), "NFT at index has not been minted yet");

        uint256 lastClaimed = uint256(_lastClaim[tokenIndex]) != 0 ? uint256(_lastClaim[tokenIndex]) : emissionStart;
        return lastClaimed;
    }

    /**
     * @dev Accumulated MBT tokens for a BearNBear token index.
     */
    function getAccumulated(uint256 tokenIndex) public view returns (uint256) {
        require(block.timestamp >= emissionStart, "Emission has not started yet");
        require(IBearNBearToken(_bbtAddress).ownerOf(tokenIndex) != address(0), "Owner cannot be 0 address");
        require(tokenIndex < IBearNBearToken(_bbtAddress).accumulatedSupply(), "NFT at index has not been minted yet");

        uint256 lastClaimed = lastClaim(tokenIndex); // return emissionStart if claim havs't been done before

        // If emission ends, no MBT can be claimed
        if (lastClaimed >= emissionEnd) return 0;

        uint256 accumulationPeriod = block.timestamp < emissionEnd ? block.timestamp : emissionEnd; // Getting the min(block.timestamp, emissionEnd)
        uint256 emissionPerDayMutiplier = IBearNBearToken(_bbtAddress).tokenMBTGeneratingMultiplierByIndex(tokenIndex);
        uint256 totalAccumulated = accumulationPeriod.sub(lastClaimed).mul(emissionPerDay).mul(emissionPerDayMutiplier).div(SECONDS_IN_A_DAY);

        // If claim hasn't been done before, add initial MBT
        if (lastClaimed == emissionStart) {
            uint256 INIT_GRANTED_MBT = IBearNBearToken(_bbtAddress).initGrantedMBT();
            // bonus MBT if it's been minted in the first 30 days of the sale event
            uint256 initialAllotment = IBearNBearToken(_bbtAddress).isMintedWithBonusMBT(tokenIndex) == true ? INIT_GRANTED_MBT.mul(PRE_REVEAL_MULTIPLIER) : INIT_GRANTED_MBT;
            totalAccumulated = totalAccumulated.add(initialAllotment);
        }

        return totalAccumulated;
    }

    /**
     * @dev Permissioning not added because it is only callable once. It is set right after deployment and verified.
     */
    function setBBTAddress(address bbtAddress) onlyOwner external {
        require(_bbtAddress == address(0), "BBT address is already set");

        _bbtAddress = bbtAddress;
    }

    /**
     * @dev overwrite emissionStart timestamp
     */
    function setEmissionStart(uint256 timestamp) external {
        require(_msgSender() == _bbtAddress, "Only BBT contract can overwrite emissionStart");

        emissionStart = timestamp;
        emissionEnd = emissionStart + (86400 * 365 * 10);
    }

    /**
     * @dev Claim MBTs for one or multiple BearNBear token indices at once.
     */
    function claim(uint256[] memory tokenIndices) public returns (uint256) {
        require(block.timestamp >= emissionStart, "Emission has not started yet");

        uint256 totalClaimQty = 0;
        for (uint i = 0; i < tokenIndices.length; i++) {
            // Sanity check for non-minted index
            require(tokenIndices[i] < IBearNBearToken(_bbtAddress).accumulatedSupply(), "NFT at index has not been minted yet");
            // check if token index is duplicated
            if(i > 0) require(tokenIndices[i] > tokenIndices[i-1], "Token index is duplicated");

            uint tokenIndex = tokenIndices[i];
            require(IBearNBearToken(_bbtAddress).ownerOf(tokenIndex) == _msgSender(), "Non-owner cannot claim MBT");

            uint256 claimQty = getAccumulated(tokenIndex);
            if (claimQty != 0) {
                totalClaimQty = totalClaimQty.add(claimQty);
                _lastClaim[tokenIndex] = block.timestamp;
            }
        }

        require(totalClaimQty != 0, "No MBT available at the moment.");
        _mint(_msgSender(), totalClaimQty);
        return totalClaimQty;
    }

    /**
     * @dev See {IERC20-transfer}.
     *
     * Requirements:
     *
     * - `recipient` cannot be the zero address.
     * - the caller must have a balance of at least `amount`.
     */
    function transfer(address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    /**
     * @dev See {IERC20-allowance}.
     */
    function allowance(address owner, address spender) public view virtual override returns (uint256) {
        return _allowances[owner][spender];
    }

    /**
     * @dev See {IERC20-approve}.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function approve(address spender, uint256 amount) public virtual override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    /**
     * @dev See {IERC20-transferFrom}.
     *
     * Emits an {Approval} event indicating the updated allowance. This is not
     * required by the EIP. See the note at the beginning of {ERC20}.
     *
     * Requirements:
     *
     * - `sender` and `recipient` cannot be the zero address.
     * - `sender` must have a balance of at least `amount`.
     * - the caller must have allowance for ``sender``'s tokens of at least
     * `amount`.
     */
    function transferFrom(address sender, address recipient, uint256 amount) public virtual override returns (bool) {
        _transfer(sender, recipient, amount);
        // Approval check is skipped if the caller of transferFrom is the BearNBear contract. For better UX.
        if (_msgSender() != _bbtAddress) {
            _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance"));
        }
        return true;
    }

    /**
     * @dev Atomically increases the allowance granted to `spender` by the caller.
     *
     * This is an alternative to {approve} that can be used as a mitigation for
     * problems described in {IERC20-approve}.
     *
     * Emits an {Approval} event indicating the updated allowance.
     *
     * Requirements:
     *
     * - `spender` cannot be the zero address.
     */
    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }

    // ++
    /**
     * @dev Burns a quantity of tokens held by the caller.
     *
     * Emits an {Transfer} event to 0 address
     *
     */
    function burn(uint256 burnQuantity) public virtual override returns (bool) {
        _burn(_msgSender(), burnQuantity);
        return true;
    }
    // ++

    /**
     * @dev Atomically decreases the allowance granted to `spender` by the caller.
     *
     * This is an alternative to {approve} that can be used as a mitigation for
     * problems described in {IERC20-approve}.
     *
