        creator2Shares = 0;
        creator1Shares = 0;
        creator1SharesLocked = false;
        creator1SharesLocked = false;
    }

    function creator1LockShares(uint lockAmount) onlyCreator1 public {
        require(lockAmount == creator1Shares, "lockAmount not correct");
        creator1SharesLocked = true;
    }

