contract MetaWarden is VRFConsumerBase, ERC721Enumerable, AllowList, ReentrancyGuard {
    using SafeERC20 for IERC20;
    using Strings for uint256;
    
    struct Chance {
        uint128 maxWadHold;
        uint16 goldChance;
        uint16 superChance;
        uint16 commonChance;
    }

    // Provenance hash for all metas (Gold, Super, Common)
    string public constant MWAD_PROVENANCE = "7d99f0f387c5aa02bae12d469a8ea36fe6f07bab0bb3aea492dd742cc6bc3e0d";

    // Chainlink VRF, provides cryptographically secure randomness
    bytes32 private constant keyHash = 0xc251acd21ec4fb7f31bb8868288bfdbaeb4fbfec2df3735ddbd4f7dc8d60103c;
    uint256 private constant fee = 0.2 * 10 ** 18; // 0.2 LINK (Varies by network);
    uint256 public vrfRandomResult;
    bool public usingChainlinkVRF;
    
    // IDs   0  -   29: Gold Meta Warden
    // IDs  30  -  329: Super Meta Warden
    // IDs 330  - 2999: Common Meta Warden

    uint256 private constant GOLD_META_TYPE = 0;
    uint256 private constant SUPER_META_TYPE = 1;
    uint256 private constant COMMON_META_TYPE = 2;

    uint16 private constant MIN_GOLD_META_ID = 0;
    uint16 private constant MAX_GOLD_META_ID = 29;
    uint256 public constant NUM_GOLD_META = MAX_GOLD_META_ID - MIN_GOLD_META_ID + 1;

    uint16 private constant MIN_SUPER_META_ID = 30;
    uint16 private constant MAX_SUPER_META_ID = 329;
    uint256 public constant NUM_SUPER_META = MAX_SUPER_META_ID - MIN_SUPER_META_ID + 1;
    
    uint16 private constant MIN_COMMON_META_ID = 330;
    uint16 private constant MAX_COMMON_META_ID = 2999;
    uint256 public constant NUM_COMMON_META = MAX_COMMON_META_ID - MIN_COMMON_META_ID + 1;
    
    Chance[] internal chances;

    uint256 private constant PS_MAX_META_WARDEN_PURCHASE = 10;

    // Sale switches
    bool public publicSaleActive;

    // Starting index for Gold Metas
    uint256 public goldMetaStartingIndex;
    // Starting index for Super Metas
    uint256 public superMetaStartingIndex;
    // Starting index for Common Metas
    uint256 public commonMetaStartingIndex;
    

    uint16 internal currentGoldMetaId = MIN_GOLD_META_ID;
    uint16 internal currentSuperMetaId = MIN_SUPER_META_ID;
    uint16 internal currentCommonMetaId = MIN_COMMON_META_ID;

    string private baseURI;
    IERC20 public wad; // Warden token

    uint256 public constant mintPrice = 400 ether; // 400 WAD
    
    uint256 public constant MAX_NFT_PURCHASE = 25;
    uint256 private constant MAX_GOLD_PURCHASE = 2;
    uint256 private constant MAX_SUPER_PURCHASE = 4;
    mapping(address => uint256) public purchased;
    mapping(address => mapping(uint256 => uint256)) public classPurchased;
    uint256[] private limitedClassOwned;

    event PublicSaleStart(
        uint256 indexed _saleStartTime
    );
    event PublicSalePaused(
        uint256 indexed _salePausedTime
    );
    event StartingIndicesSet(
        uint256 indexed _goldMetaStartingIndex,
        uint256 indexed _superMetaStartingIndex,
        uint256 indexed _commonMetaStartingIndex
    );

    modifier whenPublicSaleActive() {
        require(publicSaleActive, "Public sale is not active");
        _;
    }

    modifier startingIndicesNotSet() {
        require(
            goldMetaStartingIndex == 0,
            "Gold Metas starting index is already set"
        );
        require(
            superMetaStartingIndex == 0,
            "Super Metas starting index is already set"
        );
        require(
            commonMetaStartingIndex == 0,
            "Common Metas starting index is already set"
        );
        _;
    }

    constructor(
        string memory name,
        string memory symbol,
        IERC20 _wad
    )
        VRFConsumerBase(
            0x747973a5A2a4Ae1D3a8fDF5479f1514F65Db9C31, // VRF Coordinator
            0x404460C6A5EdE2D891e8297795264fDe62ADBB75  // LINK Token
        )
        ERC721(name, symbol)
    {
        wad = _wad;
        
        limitedClassOwned = [MAX_GOLD_PURCHASE, MAX_SUPER_PURCHASE, MAX_NFT_PURCHASE];
    }
    
    function setupUserChances(
        uint128[] calldata maxWadHold,
        uint16[] calldata goldChance,
        uint16[] calldata superChance,
        uint16[] calldata commonChance
    )
        external
        onlyOwner
    {
        require(
            maxWadHold.length == goldChance.length
            && maxWadHold.length == superChance.length
            && maxWadHold.length == commonChance.length,
            "lengths mismatch"
        );
        
        delete chances;
        for (uint256 i; i < maxWadHold.length; i++) {
            require(goldChance[i] + superChance[i] + commonChance[i] == 10000, "all chances combined must be equal to 100%");
            chances.push(
                Chance(
                    maxWadHold[i],
                    goldChance[i],
                    superChance[i],
                    commonChance[i]
                )
            );
        }
    }

    function startPublicSale()
        external
        onlyOwner
    {
        require(!publicSaleActive, "Public sale has already begun");
        publicSaleActive = true;
        emit PublicSaleStart(block.timestamp);
    }

    function pausePublicSale()
        external
        onlyOwner
        whenPublicSaleActive
    {
        publicSaleActive = false;
        emit PublicSalePaused(block.timestamp);
    }

    function withdraw()
        external
        onlyOwner
    {
        wad.safeTransfer(owner(), wad.balanceOf(address(this)));
    }

    function _randomInRange(
        uint256 _range
    ) internal view returns (uint256) {
        return _random(totalSupply()) % _range;
    }

    function _random(
        uint256 _totalSupply
    ) internal view returns (uint256) {
        return
            uint256(
                keccak256(
                    abi.encodePacked(
                        block.difficulty,
                        block.number,
                        block.timestamp,
                        msg.sender,
                        _totalSupply
                    )
                )
            );
    }

    function _mintWithChance(
        uint256[] memory _userChances
    )
        private
    {
        uint256 totalChance;
        for (uint256 i; i < 3; i++) {
            if (
                _isClassAvailable(i)
                && classPurchased[msg.sender][i] < limitedClassOwned[i]
            ) {
                totalChance += _userChances[i];
            }
        }
        require(totalChance > 0, "Minting would exceed max supply");
        uint256 chanceIndex = _randomInRange(totalChance);

        uint256 currentChance;
        for (uint256 i; i < 3; i++) {
            if (
                _isClassAvailable(i)
                && classPurchased[msg.sender][i] < limitedClassOwned[i]
            ) {
                currentChance += _userChances[i];
                if (chanceIndex < currentChance) {
                    _mintWithClass(i, msg.sender);
                    classPurchased[msg.sender][i] += 1;
                    break;
                }
            }
        }
    }
    
    function _getUserChances(uint256 _balance)
        internal
        view
        returns (uint256[] memory _classChances)
    {
        for(uint256 i; i < chances.length; i++) {
            Chance memory chance = chances[i];
            if (_balance < chance.maxWadHold) {
                _classChances = new uint256[](3);
                _classChances[GOLD_META_TYPE] = uint256(chance.goldChance);
                _classChances[SUPER_META_TYPE] = uint256(chance.superChance);
                _classChances[COMMON_META_TYPE] = uint256(chance.commonChance);
                return _classChances;
            }
        }
        
        revert("failed to get minting chances");
    }

    function mint(uint256 numMetas)
        external
        whenPublicSaleActive
        canMint(msg.sender)
        nonReentrant
    {
        require(purchased[msg.sender] + numMetas <= MAX_NFT_PURCHASE, 'Can only mint up to 25 per address');
        require(numMetas > 0, "Must mint at least one Meta Warden");
        require(
            numMetas <= PS_MAX_META_WARDEN_PURCHASE,
            "Requested number exceeds maximum"
        );
        
        purchased[msg.sender] += numMetas;

        // Payment
        uint256 costToMint = mintPrice * numMetas;
        uint256 userBalance = wad.balanceOf(msg.sender);
        require(costToMint <= userBalance, "User balance is not enough");
        wad.safeTransferFrom(msg.sender, address(this), costToMint);

        uint256[] memory userChances = _getUserChances(userBalance);

        for (uint256 i; i < numMetas; i++) {
            _mintWithChance(userChances);
        }
    }
    
    function _mintWithClass(
        uint256 classType,
        address _receiver
    )
        private
    {
        uint256 metaId;

        if (classType == GOLD_META_TYPE) {
            require(
                _isClassAvailable(GOLD_META_TYPE),
                "Would exceed supply of gold class"
            );

            metaId = currentGoldMetaId;
            currentGoldMetaId++;

        } else if (classType == SUPER_META_TYPE) {
            require(
                _isClassAvailable(SUPER_META_TYPE),
                "Would exceed supply of super class"
            );

            metaId = currentSuperMetaId;
            currentSuperMetaId++;

        } else if (classType == COMMON_META_TYPE) {
            require(
                _isClassAvailable(COMMON_META_TYPE),
                "Would exceed supply of common class"
            );

            metaId = currentCommonMetaId;
            currentCommonMetaId++;

        } else {
            revert("this classType is not supported");
        }

        _safeMint(_receiver, metaId);
    }
    
    function mintWithClass(
        uint256 _classId,
        uint256 _amount,
        address _receiver
    )
        external
        onlyOwner
        nonReentrant
    {
        for (uint256 i; i < _amount; i++) {
            _mintWithClass(_classId, _receiver);
        }
    }

    function _isClassAvailable(uint256 classType)
        internal
        view
        returns (bool)
    {
        if (classType == GOLD_META_TYPE) {
            return currentGoldMetaId <= MAX_GOLD_META_ID;

        } else if (classType == SUPER_META_TYPE) {
            return currentSuperMetaId <= MAX_SUPER_META_ID;

        } else if (classType == COMMON_META_TYPE) {
            return currentCommonMetaId <= MAX_COMMON_META_ID;

        } else {
            revert("this classType is not supported");
        }
    }

    function isMinted(uint256 tokenId) external view returns (bool) {
        require(
            tokenId <= MAX_COMMON_META_ID,
            "tokenId outside collection bounds"
        );
        return _exists(tokenId);
    }
    
    function mintingStats()
        external
        view
        returns (
            uint256 goldClass,
            uint256 superClass,
            uint256 commonClass
        )
    {
        goldClass = currentGoldMetaId;
        superClass = currentSuperMetaId - 30;
        commonClass = currentCommonMetaId - 330;
    }

    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    function setBaseURI(string memory uri)
        external
        onlyOwner
    {
        baseURI = uri;
    }

    function tokenURI(uint256 _tokenId)
        public
        view
        override
        returns (string memory)
    {
        require(_exists(_tokenId), "URI query for nonexistent token");
        return string(abi.encodePacked(baseURI, _tokenId.toString(), ".json"));
    }

    function _calculateStartingIndex(
        uint256 _randomNum,
        uint256 _collectionSize
    )
        internal
        pure
        returns (uint256)
    {
        return _randomNum % _collectionSize;
    }

    function _calculateStartingIndices(
        uint256 _randomNumber
    )
        internal
        pure
        returns (
            uint256 _goldMetaStartingIndex,
            uint256 _superMetaStartingIndex,
            uint256 _commonMetaStartingIndex
        )
    {
        uint256 fixGoldReserve = 11;

        _goldMetaStartingIndex = _calculateStartingIndex(
            _randomNumber,
            NUM_GOLD_META - fixGoldReserve // first 11 Gold Metas are for reserve
        );

        _superMetaStartingIndex = _calculateStartingIndex(
            _randomNumber,
            NUM_SUPER_META
        );

        _commonMetaStartingIndex = _calculateStartingIndex(
            _randomNumber,
            NUM_COMMON_META
        );

        // Prevent default sequence
        if (_goldMetaStartingIndex == 0) {
            _goldMetaStartingIndex++;
        }
        if (_superMetaStartingIndex == 0) {
            _superMetaStartingIndex++;
        }
        if (_commonMetaStartingIndex == 0) {
            _commonMetaStartingIndex++;
        }

        _goldMetaStartingIndex += MIN_GOLD_META_ID + fixGoldReserve;
        _superMetaStartingIndex += MIN_SUPER_META_ID;
        _commonMetaStartingIndex += MIN_COMMON_META_ID;
    }

    ///////////////////
    // Chainlink VRF //
    ///////////////////
    /** 
     * Requests randomness 
     */
    function getVRFRandomNumber()
        external
        onlyOwner
        returns (bytes32 requestId)
    {
        require(vrfRandomResult == 0, "Random number's already in place");
        require(LINK.balanceOf(address(this)) >= fee, "Not enough LINK - fill contract with faucet");
        return requestRandomness(keyHash, fee);
    }

    /**
     * Callback function used by VRF Coordinator
     */
    function fulfillRandomness(
        bytes32, // requestId
        uint256 randomness
    ) internal override {
        vrfRandomResult = randomness;
    }

    function _setStartingIndices(
        uint256 _randomNumber
    )
        private
    {
        (
            uint256 _goldMetaStartingIndex,
            uint256 _superMetaStartingIndex,
            uint256 _commonMetaStartingIndex
        ) = _calculateStartingIndices(_randomNumber);

        goldMetaStartingIndex = _goldMetaStartingIndex;
        superMetaStartingIndex = _superMetaStartingIndex;
        commonMetaStartingIndex = _commonMetaStartingIndex;
        
        emit StartingIndicesSet(
            goldMetaStartingIndex,
            superMetaStartingIndex,
            commonMetaStartingIndex
        );
    }

    function setStartingIndicesVRF() 
        external
        startingIndicesNotSet
        onlyOwner
    {
        require(vrfRandomResult != 0, "call getVRFRandomNumber first");
        usingChainlinkVRF = true;
        _setStartingIndices(vrfRandomResult);
    }
    
    // Random Starting Indices when Chailink Randomness doesn't work
    function planBSetStartingIndices() 
        external
        startingIndicesNotSet
        onlyOwner
    {
        uint256 randomNumber = _random(totalSupply());
        _setStartingIndices(randomNumber);
    }
}
