contract VCH_StakeTokenContract is Ownable {
    using SafeMath for uint256;

    uint256 public mthStakeTotalSupply = 0;
    IERC20 public stakeToken;
    uint256 public minStakeQuantity = 0;
    address[] private stakers;
    address public airdropAddr;
    uint256 public hoursBlockNum = 7;
    mapping(address => uint256) public userHoursStake;

    mapping(address => uint) public stakingBalance;
    mapping(address => bool) public hasStaked;
    mapping(address => bool) public isStaking;

    event stakeMthTransfer(address indexed user, uint256 amount);
    event unStakeMthTransfer(address indexed user, uint256 amount);
    event emergencyExtract(address indexed user, address to, uint256 amount);

    constructor(address _stakeToken, address _airdropAddr) {
        stakeToken = IERC20(_stakeToken);
        airdropAddr = _airdropAddr;
    }

    function setHoursBlockNum(uint256 _hoursBlockNum) public onlyOwner {
        hoursBlockNum = _hoursBlockNum;
    }

    function setMinStakeQuantity(uint256 _minStakeQuantity) public onlyOwner {
        minStakeQuantity = _minStakeQuantity;
    }

    function setStakeToken(address newAddress) public onlyOwner {
        stakeToken = IERC20(newAddress);
    }

    function setAirdropAddr(address newAddress) public onlyOwner() {
        airdropAddr = newAddress;
    }

    function stakeTokens(uint256 _amount) public {
        require(_amount >= minStakeQuantity, "Amount cannot more than minStakeQuantity");
        require(!isStaking[msg.sender], "Already staked");

        uint256 lastStakedBlockNum = userHoursStake[msg.sender];
        if (lastStakedBlockNum == 0) {
            userHoursStake[msg.sender] = block.number;
        } else {
            require(block.number > lastStakedBlockNum + hoursBlockNum, "Only stake once an hour");
            userHoursStake[msg.sender] = block.number;
        }

        stakeToken.transferFrom(msg.sender, address(this), _amount);
        mthStakeTotalSupply = mthStakeTotalSupply.add(_amount);
        stakingBalance[msg.sender] = _amount;
        if (!hasStaked[msg.sender]) {
            stakers.push(msg.sender);
        }
        isStaking[msg.sender] = true;
        hasStaked[msg.sender] = true;
        emit stakeMthTransfer(msg.sender, _amount);
    }

    function unstakeTokens() public {
        uint256 amount = stakingBalance[msg.sender];
        require(amount > 0, "stakingBalance cannot be 0");
        require(isStaking[msg.sender], "Not currently staking");

        uint256 totalFee = amount.mul(5).div(100);
        uint256 airdropAddrFee = amount.mul(3).div(100);
        stakeToken.transfer(msg.sender, amount.sub(totalFee));
        stakeToken.transfer(airdropAddr, airdropAddrFee);

        stakingBalance[msg.sender] = 0;
        mthStakeTotalSupply = mthStakeTotalSupply.sub(amount);
        isStaking[msg.sender] = false;
        emit unStakeMthTransfer(msg.sender, amount);
    }

    function extract(address _to, uint256 _amount) public onlyOwner {
        require(_amount > 0, "Amount cannot be 0");
        uint addrBalance = stakeToken.balanceOf(address(this));
        require(addrBalance >= _amount, "This contract does not have enough balance");
        stakeToken.transfer(_to, _amount);
        emit emergencyExtract(msg.sender, _to, _amount);
    }

    function getStakers(uint256 number) public view returns (address) {
        require(number >= 0, "Number must be positive");
        return stakers[number];
    }

    function getStakeslen() public view returns (uint) {
        return stakers.length;
    }

}
