contract StrategyBiswap is StratManager, FeeManager {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    // Tokens used
    address constant public wbnb = address(0xbb4CdB9CBd36B01bD1cBaEBF2De08d9173bc095c);
    address constant public cake = address(0x965F527D9159dCe6288a2219DB51fc6Eef120dD1);
    address constant public sfp  = address(0xD41FDb03Ba84762dD66a0af1a6C8540FF1ba5dfb);
    bool public staking;
    bool public autoHarvest;
    address public want;
    address public lpToken0;
    address public lpToken1;

    // Third party contracts
    address constant public masterchef = address(0xDbc1A13490deeF9c3C12b44FE77b503c1B061739);
    uint256 public poolId;

    // Routes
    address[] public cakeToLp0Route;
    address[] public cakeToLp1Route;
    address[] public cakeToSfpRoute = [cake, wbnb, sfp];

    /**
     * @dev Event that is fired each time someone harvests the strat.
     */
    event StratHarvest(address indexed harvester);

    constructor(
        address _want,
        uint256 _poolId,
        address _vault,
        address _unirouter,
        address _keeper,
        address _treasuryPool,
        bool _staking,
        bool _autoHarvest
    ) StratManager(_keeper, _unirouter, _vault, _treasuryPool) public {
        want = _want;
        staking = _staking;
        poolId = _poolId;
        autoHarvest = _autoHarvest;

        if (!staking){
            lpToken0 = IUniswapV2Pair(want).token0();
            lpToken1 = IUniswapV2Pair(want).token1();

            if (lpToken0 == wbnb) {
                cakeToLp0Route = [cake, wbnb];
            } else if (lpToken0 != cake) {
                cakeToLp0Route = [cake, wbnb, lpToken0];
            }

            if (lpToken1 == wbnb) {
                cakeToLp1Route = [cake, wbnb];
            } else if (lpToken1 != cake) {
                cakeToLp1Route = [cake, wbnb, lpToken1];
            }
        }

        _giveAllowances();
    }

    // puts the funds to work
    function deposit() public whenNotPaused {
        uint256 wantBal = IERC20(want).balanceOf(address(this));
        if (wantBal > 0) {
            if (staking){
                IMasterChef(masterchef).enterStaking(wantBal);
            }else{
                IMasterChef(masterchef).deposit(poolId, wantBal);
            }
        }
    }

    function withdraw(uint256 _amount) external whenNotPaused {
        require(msg.sender == vault, "!vault");

        uint256 wantBal = IERC20(want).balanceOf(address(this));

        if (wantBal < _amount) {
            if (staking){
                IMasterChef(masterchef).leaveStaking(_amount.sub(wantBal));
            }else{
                IMasterChef(masterchef).withdraw(poolId, _amount.sub(wantBal));
            }
            wantBal = IERC20(want).balanceOf(address(this));
        }
    
        if (_amount > wantBal) {
            _amount = wantBal;
        }

        IERC20(want).safeTransfer(vault, _amount);

        if (autoHarvest == true){
            chargeFees();
            addLiquidity();
            deposit();
            emit StratHarvest(msg.sender);
        }
    }

    function setHarvestMode(bool _autoHarvest) external onlyManager {
        autoHarvest = _autoHarvest;
    }

    function beforeDeposit() external override {
        if (autoHarvest == true){
            harvest();
        }
    }

    // compounds earnings and charges performance fee
    function harvest() public whenNotPaused {
        require(tx.origin == msg.sender || msg.sender == vault, "!contract");

        if (staking){
            IMasterChef(masterchef).leaveStaking(0);
        }else{
            IMasterChef(masterchef).deposit(poolId, 0);
        }

        chargeFees();
        addLiquidity();
        deposit();
        emit StratHarvest(msg.sender);
    }
    
    function freeFeeDuration() public pure returns (uint256) {
        return WITHDRAWAL_FREE_FEE_DURATION;
    }
    
    function withdrawFee() public pure returns (uint256) {
        return WITHDRAWAL_FEE;
    }
    
    function withdrawFeeMax() public pure returns (uint256) {
        return WITHDRAWAL_MAX;
    }

    function chargeFees() internal {
        uint256 cakeBal = IERC20(cake).balanceOf(address(this));
        if (cakeBal == 0){
            return;
        }
        uint256 num = IVault(vault).getMax();
        uint256 realNum = IVault(vault).balance();
        uint256 penalty = 0;
        if(num < realNum) {
            penalty = (realNum.sub(num)).mul(PENALTY_MAX).div(realNum);
        }
        uint256 penaltyNum =  cakeBal.mul(penalty).div(PENALTY_MAX);
        if (penaltyNum > 0) {
            penaltyNum = penaltyNum.mul(penaltyFee).div(PENALTY_MAX);
        }

        uint256 toSfp = (cakeBal.sub(penaltyNum)).mul(50).div(1000);
        toSfp = toSfp.add(penaltyNum);

        IUniswapRouterETH(unirouter).swapExactTokensForTokens(toSfp, 0, cakeToSfpRoute, address(this), now);
        uint256 sfpBal = IERC20(sfp).balanceOf(address(this));

        uint256 rewardAmount = sfpBal.mul(getRewardFee()).div(MAX_FEE);
        IERC20(sfp).safeTransfer(vault, rewardAmount);

        uint256 callFeeAmount = sfpBal.mul(callFee).div(MAX_FEE);
        IERC20(sfp).safeTransfer(tx.origin, callFeeAmount);

        uint256 treasuryFeeAmount = sfpBal.mul(treasuryFee).div(MAX_FEE);
        IERC20(sfp).safeTransfer(treasuryPool, treasuryFeeAmount);
    }

    // Adds liquidity to AMM and gets more LP tokens.
    function addLiquidity() internal {
        if  (staking){
            return;
        }
        uint256 cakeBal = IERC20(cake).balanceOf(address(this));
        if (cakeBal == 0){
            return;
        }
        uint256 cakeHalf = cakeBal.div(2);

        if (lpToken0 != cake) {
            IUniswapRouterETH(unirouter).swapExactTokensForTokens(cakeHalf, 0, cakeToLp0Route, address(this), now);
        }

        if (lpToken1 != cake) {
            IUniswapRouterETH(unirouter).swapExactTokensForTokens(cakeHalf, 0, cakeToLp1Route, address(this), now);
        }

        uint256 lp0Bal = IERC20(lpToken0).balanceOf(address(this));
        uint256 lp1Bal = IERC20(lpToken1).balanceOf(address(this));
        IUniswapRouterETH(unirouter).addLiquidity(lpToken0, lpToken1, lp0Bal, lp1Bal, 1, 1, address(this), now);
    }

    // calculate the total underlaying 'want' held by the strat.
    function balanceOf() public view returns (uint256) {
        return balanceOfWant().add(balanceOfPool());
    }

    // it calculates how much 'want' this contract holds.
    function balanceOfWant() public view returns (uint256) {
        return IERC20(want).balanceOf(address(this));
    }

    // it calculates how much 'want' the strategy has working in the farm.
    function balanceOfPool() public view returns (uint256) {
        (uint256 _amount, ) = IMasterChef(masterchef).userInfo(poolId, address(this));
        return _amount;
    }

    // called as part of strat migration. Sends all the available funds back to the vault.
    function retireStrat() external {
        require(msg.sender == vault, "!vault");

        IMasterChef(masterchef).emergencyWithdraw(poolId);

        uint256 wantBal = IERC20(want).balanceOf(address(this));
        IERC20(want).transfer(vault, wantBal);
    }

    // pauses deposits and withdraws all funds from third party systems.
    function panic() public onlyManager {
        pause();
        IMasterChef(masterchef).emergencyWithdraw(poolId);
    }

    function pause() public onlyManager {
        _pause();

        _removeAllowances();
    }

    function unpause() external onlyManager {
        _unpause();

        _giveAllowances();

        deposit();
    }

    function _giveAllowances() internal {
        IERC20(want).safeApprove(masterchef, uint256(-1));
        IERC20(cake).safeApprove(unirouter, uint256(-1));

        if (!staking) {
            IERC20(lpToken0).safeApprove(unirouter, 0);
            IERC20(lpToken0).safeApprove(unirouter, uint256(-1));

            IERC20(lpToken1).safeApprove(unirouter, 0);
            IERC20(lpToken1).safeApprove(unirouter, uint256(-1));
        }
    }

    function _removeAllowances() internal {
        IERC20(want).safeApprove(masterchef, 0);
        IERC20(cake).safeApprove(unirouter, 0);

        if (!staking) {
            IERC20(lpToken0).safeApprove(unirouter, 0);
            IERC20(lpToken1).safeApprove(unirouter, 0);
        }
    }
}
