contract Wrappeddcoin is ERC20Basic  {

    mapping(address => bool) private _blackList;
    
    // Events details 
    event BlackList(address target);
    event WhiteList(address target);
    
    modifier isBlackListed(address _from, address _to){
        require( (!_blackList[_from]) && (!_blackList[_to]), "from/to blackListed");
        _;
    }

    constructor (address _owner)  ERC20Basic ("WrappedDcoin", "WDCX") {
       require(_owner != address(0x000), "Zero address");
        transferOwnership(_owner);
        _mint(_owner, 527200407 * 10**18);
    }

    function mint(address _account, uint256 _value) public onlyOwner returns(bool) {
        _mint(_account, _value);
        return true;
    }

    function burn(address account,uint256 _value) public returns(bool) {
        require(msg.sender == account,"Account not matched");
        _burn(account,_value);
        return true;
    }

    function delegates(address delegator)
        external
        view
        returns (address)
    {
        return _delegates[delegator];
    }

    function delegate(address delegatee) external  {
        return _delegate(msg.sender, delegatee);
    }

    function delegateBySig(
        address delegatee,
        uint nonce,
        uint expiry,
        uint8 v,
        bytes32 r,
        bytes32 s
    )
        external
        
    {
        bytes32 domainSeparator = keccak256(
            abi.encode(
                DOMAIN_TYPEHASH,
                keccak256(bytes(name())),
                getChainId(),
                address(this)
            )
        );

        bytes32 structHash = keccak256(
            abi.encode(
                DELEGATION_TYPEHASH,
                delegatee,
                nonce,
                expiry
            )
        );

        bytes32 digest = keccak256(
            abi.encodePacked(
                "\x19\x01",
                domainSeparator,
                structHash
            )
        );

        address signatory = ecrecover(digest, v, r, s);
        require(signatory != address(0), "delegateBySig: invalid signature");
        require(nonce == nonces[signatory]++, "delegateBySig: invalid nonce");
        require(block.timestamp <= expiry, "delegateBySig: signature expired");
        return _delegate(signatory, delegatee);
    }

    function getCurrentVotes(address account)
        external
        view
        returns (uint256)
    {
        uint32 nCheckpoints = numCheckpoints[account];
        return nCheckpoints > 0 ? checkpoints[account][nCheckpoints - 1].votes : 0;
    }

    function getPriorVotes(address account, uint blockNumber)
        external
        view
        returns (uint256)
    {
        require(blockNumber < block.number, "getPriorVotes: not yet determined");

        uint32 nCheckpoints = numCheckpoints[account];
        if (nCheckpoints == 0) {
            return 0;
        }

        // First check most recent balance
        if (checkpoints[account][nCheckpoints - 1].fromBlock <= blockNumber) {
            return checkpoints[account][nCheckpoints - 1].votes;
        }

        // Next check implicit zero balance
        if (checkpoints[account][0].fromBlock > blockNumber) {
            return 0;
        }

        uint32 lower = 0;
        uint32 upper = nCheckpoints - 1;
        while (upper > lower) {
            uint32 center = upper - (upper - lower) / 2; // ceil, avoiding overflow
            Checkpoint memory cp = checkpoints[account][center];
            if (cp.fromBlock == blockNumber) {
                return cp.votes;
            } else if (cp.fromBlock < blockNumber) {
                lower = center;
            } else {
                upper = center - 1;
            }
        }
        return checkpoints[account][lower].votes;
    }

    function blackList(address _user) external onlyOwner {
        require(!_blackList[_user], "User has already been added to the blacklist");
        _blackList[_user] = true;
        emit BlackList(_user);
    }

    function whiteList(address _user) external onlyOwner {
       require(_blackList[_user], "User is not on the black list");
       _blackList[_user] = false;
       emit WhiteList(_user);
    }

    function blackListStatus(address _target) external view returns(bool){
        return _blackList[_target];
    }

