// Amazy Sneaker ERC721 + sale contract

contract AmazySneaker721 is ERC721, ERC721Enumerable, ERC721URIStorage, ERC721Burnable, Pausable, AccessControl, ReentrancyGuard {
    using Counters for Counters.Counter;
    using Address for address;
    using Strings for uint256;

    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");
    bytes32 public constant INFO_ROLE = keccak256("INFO_ROLE");
    Counters.Counter private _tokenIdCounter;

    string base;

    constructor() ERC721("Amazy Sneaker", "AMAZY") {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);
        _grantRole(INFO_ROLE, msg.sender);
        base = "https://app.amazy.io/item/";
    }

    function _baseURI() internal view override returns (string memory) {
        return base;
    }
    
    function baseURI() public view returns (string memory) {
        return _baseURI();
    }
    
    function changeBaseURI(string memory _base) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "Must have admin role to chande baseURI");
        base = _base;
        emit ChangeBaseURI(_base);
    }

    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    function _safeMint(address to, string memory _item, uint256 _season, string memory _info) internal {
        uint256 tokenId = _tokenIdCounter.current();
        _tokenIdCounter.increment();
        _safeMint(to, tokenId);
        string memory _tokenURI = uint(sha256(abi.encodePacked(address(this),tokenId))).toString();
        _setTokenURI(tokenId, _tokenURI);
        token[tokenId] = TokenInfo(_info, _item, _season, block.timestamp, _tokenURI, 0);
        emit Mint(to, tokenId, _season, _info);
    }

    function _beforeTokenTransfer(address from, address to, uint256 tokenId)
        internal
        whenNotPaused
        override(ERC721, ERC721Enumerable)
    {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    // EVENTS

    event Mint(address indexed to, uint256 indexed tokenId, uint256 indexed season, string info);
    event Burn(uint256 indexed tokenId);
    event NewSeason(uint256 indexed _seasonId);
    event TokenData(uint256 indexed tokenId, string indexed tokenData, uint256 indexed level); 
    event AddChangeItem(string indexed _item, bool indexed _active);
    event SaleMint(address indexed to, uint256 indexed season);
    event ChangeBaseURI(string indexed baseURI);
    event WhiteListed(address[] indexed buyer, uint256 indexed season);

    // VARIABLES

    uint256 public season;

    mapping (uint256 => Season) ssn;

    struct Season {
        uint256    seasonStart;
        uint256    seasonStop;
        uint256    seasonPrice;
        uint256    seasonAmount;
        uint256    initAmount;
        address    withdraw;
        address    tokenContract;
        bool       tokens;
        bool       whitelist;
        uint256    tokensPerBuyer;
        string     seasonName;
        string     seasonItem;
        string     defaultInfo;
    }

    mapping (uint256 => TokenInfo) token;

    struct TokenInfo {
        string     info;
        string     item;
        uint256    season;
        uint256    time;
        string     uri;
        uint256    level; // 0 by default
    }

    mapping(uint256 => mapping(address=>bool)) whitelist;
    mapping(uint256 => mapping(address=>uint256)) tokensBought;
    
    string[] _items;
    mapping(string => bool) items_active;
    mapping(string => bool) items_exist;

    // WHITELIST

    function addToWhitelist(address[] memory _address, uint256 _season) external returns (uint256) {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "Must have admin role to add to whitelist");
        require(_address.length <= 100, "More than 100 addresses");
        uint256 j = 0;
        for (j; j < _address.length; j++) {
            whitelist[_season][_address[j]] = true;
        }
        emit WhiteListed(_address, _season);
        return(_address.length);
    }
    
    function isWhitelisted(address _address, uint256 _season) external view returns (bool) {
        return(whitelist[_season][_address]);
    }

    // ITEMS

    function addChangeItem(string memory _item, bool _item_bool) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "Must have admin role to add or change items");
        if (!items_exist[_item]) { _items.push(_item); }
        items_active[_item] = _item_bool;
        items_exist[_item] = true;
        emit AddChangeItem(_item, _item_bool);
    }

    function itemInfo(string memory _item) external view returns (bool _exists, bool _active) {
        return (items_exist[_item], items_active[_item]);
    }

    function itemsList() external view returns (string[] memory) {
        return _items;
    }

    // INIT NEW SEASON

    function initNewSeason(
            uint256         _seasonStart, 
            uint256         _seasonStop, 
            uint256         _seasonPrice, 
            uint256         _seasonAmount, 
            bool            _whitelist, 
            address         _withdraw, 
            address         _tokenContract, 
            string memory   _seasonItem, 
            string memory   _seasonName, 
            uint256         _tokensPerBuyer,
            string memory   _defaultInfo) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "Must have admin role to init new season");
        require(_seasonStart < _seasonStop, "Start time must be less than stop time");
        require(_withdraw != address(0), "Empty withdraw address");
        require(items_active[_seasonItem], "Item not active");
        require(_seasonAmount != 0, "Zero tokens amount for the season");
        require(_tokensPerBuyer != 0, "Zero tokens per buyer");
        season++;
        bool _token;
        if (_tokenContract == address(0)) { _token = false; } else { _token = true; }
        ssn[season] = Season(_seasonStart, _seasonStop, _seasonPrice, _seasonAmount, _seasonAmount, _withdraw, _tokenContract, _token, _whitelist, _tokensPerBuyer, _seasonName, _seasonItem, _defaultInfo);
        emit NewSeason(season);
    }

    // RESET SEASON

    function resetSeason(uint256 _season) external {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), "Must have admin role to reset season");
        ssn[_season] = Season(0, 0, 0, 0, 0, address(0), address(0), false, false, 0, "", "", "");
    }

    // SALE MINT

    function saleMint(address _to, uint256 _season) external nonReentrant payable {
        require(_season !=0 && _season <= season, "Season is wrong");
        require(ssn[_season].seasonAmount !=0, "Out of available amout for sale");
        require(block.timestamp >= ssn[_season].seasonStart && block.timestamp <= ssn[_season].seasonStop, "Season not active - time");
        require(items_active[ssn[_season].seasonItem], "Item not active");
        if (ssn[_season].whitelist) {
            require(whitelist[_season][msg.sender], "You are not in whitelist");
        }
        require(tokensBought[_season][msg.sender] < ssn[_season].tokensPerBuyer, "Out of limits per buyer");
        if (ssn[_season].seasonPrice > 0) {
        if (!ssn[_season].tokens) {
            // pay with coins
            require(msg.value == ssn[_season].seasonPrice, "Price not right!");
            (bool succ, ) = payable(ssn[_season].withdraw).call{value: ssn[_season].seasonPrice}("");
            require(succ, "Coins transfer failed");
        } else {
            // pay with tokens
            require(msg.value == 0, "Pay with tokens");
            require(IERC20(ssn[_season].tokenContract).transferFrom(msg.sender, ssn[_season].withdraw, ssn[_season].seasonPrice), "Tokens transfer failed");
        } }
        _safeMint(_to, ssn[_season].seasonItem, _season, ssn[_season].defaultInfo);
        ssn[_season].seasonAmount -= 1;
        tokensBought[_season][msg.sender] += 1;
        emit SaleMint(_to,_season);
    }

    // SYSTEM MINT

    function systemMint(address to, string memory _item, string memory _info) external {
        require(hasRole(INFO_ROLE, _msgSender()), "Must have info role to mint");
        require(items_active[_item], "Item not active");
        _safeMint(to, _item, 0, _info);
    }

    // TOKEN INFO SET

    function setTokenInfo(uint256 tokenId, string memory data, uint256 level) external {
        require(hasRole(INFO_ROLE, _msgSender()), "Must have info role to change info");
        require(_exists(tokenId), "Query for nonexistent token");
        token[tokenId].info = data;
        token[tokenId].level = level;
        emit TokenData(tokenId, data, level);
    }

    // VIEW

    function currentId() external view returns (uint256 _currentId) {
        return _tokenIdCounter.current();
    }

    function tokensBoughtByAddress(uint256 _season, address _buyer) external view returns (uint256 _amount) {
        return tokensBought[_season][_buyer];
    }

    function seasonInfo(uint256 seasonId) external view returns (uint256 _seasonStart, uint256 _seasonStop, uint256 _seasonPrice, uint256 _seasonAmount, uint256 _initAmount, uint256 _tokensPerBuyer) {
        return (ssn[seasonId].seasonStart, ssn[seasonId].seasonStop, ssn[seasonId].seasonPrice, ssn[seasonId].seasonAmount, ssn[seasonId].initAmount, ssn[seasonId].tokensPerBuyer);
    }
    
    function seasonOptions(uint256 seasonId) external view returns (string memory _seasonName, string memory _seasonItem, address _tokenContract, bool _tokens, bool _whitelist, string memory _defaultInfo) {
        return (ssn[seasonId].seasonName, ssn[seasonId].seasonItem, ssn[seasonId].tokenContract, ssn[seasonId].tokens, ssn[seasonId].whitelist, ssn[seasonId].defaultInfo);
    }

    function tokenInfo(uint256 tokenId) external view returns (address _owner, string memory _tokenUri, uint256 _tokenSeason, uint256 _tokenTime) {
        require(_exists(tokenId), "Query for nonexistent token");
        return (ownerOf(tokenId), tokenURI(tokenId), token[tokenId].season, token[tokenId].time);
    }
    
    function tokenData(uint256 tokenId) external view returns (address _owner, string memory _rnd, string memory _item, string memory _data, uint256 _level) {
        require(_exists(tokenId), "Query for nonexistent token");
        return (ownerOf(tokenId), token[tokenId].uri, token[tokenId].item, token[tokenId].info, token[tokenId].level);
    }

    // The following functions are overrides required by Solidity.

    function _burn(uint256 tokenId) internal override(ERC721, ERC721URIStorage) {
        super._burn(tokenId);
        emit Burn(tokenId);
    }

    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable, AccessControl)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}
