    "contracts/ContractPaymentsService.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.17;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

contract ContractPaymentsService is Ownable {

    IERC20 public usdtToken;
    address public usdtTokenAddress;  
    mapping(address => bool) public whitelist;
    mapping(address => uint256) public lastWithdrawalTime;  
    uint256 public withdrawalLimit;
    uint256 public withdrawalCooldown = 1 weeks;  

    event Withdrawal(address indexed beneficiary, uint256 amount, uint256 timestamp);

    constructor(address _usdtToken) {
        usdtToken = IERC20(_usdtToken);
        usdtTokenAddress = _usdtToken;  
    }


    function addToWhitelist(address _address) external onlyOwner {
        whitelist[_address] = true;
    }

    function removeFromWhitelist(address _address) external onlyOwner {
        whitelist[_address] = false;
    }

    function setWithdrawalLimit(uint256 _withdrawalLimit) external onlyOwner {
        withdrawalLimit = _withdrawalLimit;
    }

    function withdraw(uint256 _amount) external {
        require(whitelist[msg.sender], "Address not whitelisted");
        require(_amount <= withdrawalLimit, "Exceeds withdrawal limit");
        require(block.timestamp >= lastWithdrawalTime[msg.sender] + withdrawalCooldown, "Withdrawal not allowed yet");
        
        require(usdtToken.balanceOf(address(this)) >= _amount, "Insufficient funds in the contract");

        lastWithdrawalTime[msg.sender] = block.timestamp;

        require(usdtToken.transfer(msg.sender, _amount), "Transfer failed");

        emit Withdrawal(msg.sender, _amount, block.timestamp);
    }

    // Función para que el propietario retire USDT del contrato
    function withdrawUSDT(uint256 _amount) external onlyOwner {
        require(usdtToken.balanceOf(address(this)) >= _amount, "Insufficient funds in the contract");
        require(usdtToken.transfer(owner(), _amount), "Transfer failed");
    }
}
