    "project:/contracts/Presale/TokenPresaleStaged.sol": {
      "content": "// SPDX-License-Identifier: MIT

pragma solidity =0.8.6;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "../Staking/IStakingV3.sol";

contract TokenPresaleStaged is Ownable {
    using SafeERC20 for IERC20;

    IERC20 public token;

    uint256 public minAmount;
    uint256 public maxAmount;
    bool public wasStarted;
    bool public wasStopped;
    uint256 public startedTimestamp;
    uint256 public stoppedTimestamp;

    struct PresaleStage {
        uint256 tokensUSDMaxLimit; // keep this for compatibility, same value as tokensBNBMaxLimit
        uint256 tokensBNBMaxLimit;
        uint256 tokensBNBAcquired;
    }

    uint256 public totalAllocated;
    mapping(address => uint256) public allocations;

    PresaleStage[] public stages;
    uint256 public curStage;
    uint256 public maxStage;

    bool public constraintsSet;
    address public requiredStaking;
    address public requiredToken;
    uint256 public minBalance;

    event SaleStarted();
    event SaleStopped();
    event Allocated(address indexed addr, address indexed ref, uint256 alloc, uint256 stage);
    event Withdrawn(uint256 amount);
    event TokenAddressChanged(address indexed token);
    event ConstraintsChanged();
    event StageAdded(uint256 usdMaxLimit);

    function startSale(uint256 _minAmount, uint256 _maxAmount) external onlyOwner {
        require(!wasStarted, 'PresaleStaged: Sale was already started');
        require(_minAmount > 0, 'PresaleStaged: Min allocation needs to be larger than 0');
        require(_maxAmount > 0, 'PresaleStaged: Max allocation needs to be larger than 0');
        require(_maxAmount > _minAmount, 'PresaleStaged: Min allocation cannot be larger than max allocation');
        require(address(token) != address(0), 'PresaleStaged: Token address needs to be set first');
        require(curStage < maxStage, 'PresaleStaged: Stages need to be set first');

        minAmount = _minAmount;
        maxAmount = _maxAmount;

        startedTimestamp = block.timestamp;
        wasStarted = true;

        emit SaleStarted();
    }

    function closeSale() external onlyOwner {
        require(wasStarted, 'PresaleStaged: Sale was not started yet');
        require(!wasStopped, 'PresaleStaged: Sale was already closed');

        stoppedTimestamp = block.timestamp;
        wasStopped = true;

        emit SaleStopped();
    }

    function setTokenAddress(IERC20 _token) public virtual onlyOwner {
        require(address(_token) != address(0), 'Presale: token address needs to be different than zero!');
        require(address(token) == address(0), 'Presale: token already set!');

        token = _token;

        emit TokenAddressChanged(address(token));
    }

    function addStagesArray(uint256[] memory limits) external onlyOwner {
        require(limits.length > 0, 'PresaleStaged: Limit array must be larger than 0 elements');

        for (uint256 i; i < limits.length; ++i) {
            require(limits[i] > 0, 'PresaleStaged: Limit needs to be larger than 0');
        }
        for (uint256 i; i < limits.length; ++i) {
            stages.push(PresaleStage({
                tokensUSDMaxLimit: limits[i],
                tokensBNBMaxLimit: limits[i],
                tokensBNBAcquired: 0
            }));
            emit StageAdded(limits[i]);
        }
        maxStage += limits.length;
    }

    function allocate(uint256 amount) external {
        _allocate(amount, address(0));
    }

    function allocate(uint256 amount, address referrer) external {
        _allocate(amount, referrer);
    }

    function _allocate(uint256 amount, address referrer) internal {
        require(wasStarted, 'PresaleStaged: Cannot allocate yet');
        require(!wasStopped, 'PresaleStaged: Cannot allocate anymore');
        require(amount != 0, 'PresaleStaged: Allocation value cannot be 0');
        require(amount >= minAmount, 'PresaleStaged: Allocation is too small');
        require(allocations[msg.sender] + amount <= maxAmount, 'PresaleStaged: Allocation is too large');

        token.safeTransferFrom(address(msg.sender), address(this), amount);

        totalAllocated += amount;
        allocations[msg.sender] += amount;

        allocateInStage(msg.sender, amount, referrer);
    }

    function withdraw(address addr) external onlyOwner {
        uint256 currentBalance = token.balanceOf(address(this));
        token.safeTransfer(addr, currentBalance);

        emit Withdrawn(currentBalance);
    }

    function allocation(address addr) public view returns (uint256) {
        return allocations[addr];
    }

    function allocateInStage(address addr, uint256 amount, address referrer) internal {
        require(curStage < maxStage, 'PresaleStaged: Allocation goes over max target');

        PresaleStage storage stage = stages[curStage];

        uint256 curAmount = amount;
        uint256 freeAmount = stage.tokensBNBMaxLimit - stage.tokensBNBAcquired;

        if (amount > freeAmount) {
            curAmount = freeAmount;
        }

        stage.tokensBNBAcquired += curAmount;
        emit Allocated(addr, referrer, curAmount, curStage);

        if (amount > freeAmount) {
            ++curStage;
            allocateInStage(addr, amount - freeAmount, referrer);
        }
    }
}
"
