pragma solidity 0.8.19;

interface IERC20 {
    event Transfer(address indexed from, address indexed to, uint256 value);
    event Approval(address indexed owner, address indexed spender, uint256 value);
    function totalSupply() external view returns (uint256);
    function balanceOf(address account) external view returns (uint256);
    function transfer(address to, uint256 value) external returns (bool);
    function allowance(address owner, address spender) external view returns (uint256);
    function approve(address spender, uint256 value) external returns (bool);
    function transferFrom(address from, address to, uint256 value) external returns (bool);
}

interface IERC20Metadata is IERC20 {
    function name() external view returns (string memory);
    function symbol() external view returns (string memory);
    function decimals() external view returns (uint8);
}

interface IERC20Errors {
    error ERC20InsufficientAllowance(address spender, uint256 allowance, uint256 needed);
    error ERC20InsufficientBalance(address sender, uint256 balance, uint256 needed);
    error ERC20InvalidReceiver(address receiver);
    error ERC20InvalidApprover(address approver);
    error ERC20InvalidSpender(address spender);
    error ERC20InvalidSender(address sender);
    error ERC20MaxWallet();
    error ERC20MaxTx();
}

interface IDexRouter {
    function WETH() external pure returns (address);
    function factory() external pure returns (address);
    function swapExactTokensForETHSupportingFeeOnTransferTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external;
}

interface IDexFactory {
    function createPair(address tokenA, address tokenB) external returns (address pair);
}

abstract contract Context {
    function _msgSender() internal view virtual returns (address) {
        return msg.sender;
    }
}

abstract contract Ownable is Context {
    address private _owner;

    error OwnableUnauthorizedAccount(address account);
    error OwnableInvalidOwner(address owner);

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    constructor() {
        address initialOwner = _msgSender();
        _transferOwnership(initialOwner);
    }

    modifier onlyOwner() {
        _checkOwner();
        _;
    }

    function owner() public view returns (address) {
        return _owner;
    }

    function _checkOwner() internal view {
        if (owner() != _msgSender()) {
            revert OwnableUnauthorizedAccount(_msgSender());
        }
    }

    function _transferOwnership(address newOwner) private {
        address oldOwner = _owner;
        _owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }

    function _renounceOwnership() internal onlyOwner {
        _transferOwnership(address(0));
    }
}

contract AV2Tax is IERC20Metadata, IERC20Errors, Ownable { // modify
    mapping(address account => mapping(address spender => uint256)) private _allowances;
    

    IDexRouter private immutable _dexRouter;
    address private immutable _dexPair;
    address[] private _path = new address[](2);
    
    bool private _swapActive;
    
    uint256 private constant _threshold = 50000 * 10 ** 18; // modify 0,5% of supply
    uint256 public constant _maxWallet = 200000 * 10 ** 18;
    address _tax; // modify
    address _deployer; // modify
    mapping(address account => uint256) private _balances;
    uint256 private constant _totalSupply = 10000000 * 10 ** 18; // 10 million
    uint256 public constant _maxTxAmount = 200000 * 10 ** 18;

    constructor() {
        _dexRouter = IDexRouter(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        _dexPair = IDexFactory(_dexRouter.factory()).createPair(address(this),_dexRouter.WETH());
        _path[1] = _dexRouter.WETH();
        _path[0] = address(this);
        _deployer = _msgSender();
        _balances[_msgSender()] = _totalSupply;
        emit Transfer(address(0), _msgSender(), _totalSupply);
        _renounceOwnership();
    }

    modifier swapping() {
        _swapActive = true;
        _;
        _swapActive = false;
    }

    function name() external pure override returns (string memory) {
        return "DO-NOT-BUY-THIS-IS-A-TEST"; // modify
    }

    function symbol() external pure override returns (string memory) {
        return "DO-NOT-BUY"; // modify
    }

    function decimals() external pure override returns (uint8) {
        return 18;
    }

    function totalSupply() external pure override returns (uint256) {
        return _totalSupply;
    }

    function balanceOf(address account) external view override returns (uint256) {
        return _balances[account];
    }

    function transfer(address to, uint256 value) external override returns (bool) {
        address owner = _msgSender();
        _transfer(owner, to, value);
        return true;
    }

    function approve(address spender, uint256 value) external override returns (bool) {
        address owner = _msgSender();
        _approve(owner, spender, value);
        return true;
    }

    function transferFrom(address from, address to, uint256 value) external override returns (bool) {
        address spender = _msgSender();
        _spendAllowance(from, spender, value);
        _transfer(from, to, value);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function _transfer(address from, address to, uint256 amount) private {
        if (from != address(_dexRouter) && from != _dexPair && from != _deployer && from != address(0)) {
            if (amount > _maxTxAmount) {
                revert ERC20MaxTx();
            }
        }
        if (to != address(_dexRouter) && to != _dexPair && to != _deployer && to != address(0)) {
            if (_balances[to] + amount > _maxWallet) {
                revert ERC20MaxWallet();
            }
        }
        uint256 fromBalance = _balances[from];
        if (fromBalance < amount) {
            revert ERC20InsufficientBalance(from, fromBalance, amount);
        }
        if (to == _dexPair && from != address(_dexRouter) && from != address(_deployer)) {
            uint256 contractTokenBalance = _balances[_path[0]];
            if (!_swapActive && contractTokenBalance > _totalSupply / 200) {
                _swapForETH(contractTokenBalance);
            }
        }
        uint256 tax = amount / 70;
        
        _balances[from] = fromBalance - amount;

        _balances[address(this)] += tax;
        emit Transfer(from, address(this), tax);

        _balances[to] += amount - tax;
        emit Transfer(from, to, amount - tax);
    }

    function _swapForETH(uint value) private swapping {
        _approve(_path[0], address(_dexRouter), value);
        _dexRouter.swapExactTokensForETHSupportingFeeOnTransferTokens(value, 0, _path, _tax, block.timestamp);
    }

    function _approve(address owner, address spender, uint256 value) private {
        _approve(owner, spender, value, true);
    }

    function _approve(address owner, address spender, uint256 value, bool emitEvent) private {
        if (owner == address(0)) {
            revert ERC20InvalidApprover(address(0));
        }
        if (spender == address(0)) {
            revert ERC20InvalidSpender(address(0));
        }
        _allowances[owner][spender] = value;
        if (emitEvent) {
            emit Approval(owner, spender, value);
        }
    }

    function _spendAllowance(address owner, address spender, uint256 value) private {
        uint256 currentAllowance = allowance(owner, spender);
        if (currentAllowance != type(uint256).max) {
            if (currentAllowance < value) {
                revert ERC20InsufficientAllowance(spender, currentAllowance, value);
            }
            unchecked {
                _approve(owner, spender, currentAllowance - value, false);
            }
        }
    }
}
