    "contracts/AutuniteNFT.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol";
import "@openzeppelin/contracts/token/common/ERC2981.sol";
import "./utils/Ownable.sol";

/**
 * @title AutuniteNFT
 * @dev ERC721 token contract with additional functionality.
 */
contract AutuniteNFT is ERC721, ERC721Enumerable, ERC2981, Ownable {
    string private _baseURI_;
    address public minter;
    bool public singleNFT;

    /**
     * @dev Modifier to restrict a function's access to the minter address.
     */
    modifier onlyMinter() {
        require(msg.sender == minter, "Only the minter can call this function");
        _;
    }

    /**
     * @dev Constructor function for the AutuniteNFT contract.
     * @param _uri_ The base URI for token metadata.
     * @param _name_ The name of the token.
     * @param _symbol_ The symbol of the token.
     * @param _owner_ The initial owner of the contract.
     * @param _defaultRoyalty_ The default royalty fee for the owner.
     * @param _singleNFT_ Flag indicating whether only one NFT is allowed per address.
     */
    constructor(
        string memory _uri_,
        string memory _name_,
        string memory _symbol_,
        address _owner_,
        uint96 _defaultRoyalty_,
        bool _singleNFT_
    ) ERC721(_name_, _symbol_) Ownable(_owner_) {
        _baseURI_ = _uri_;
        minter = msg.sender;
        singleNFT = _singleNFT_;
        _setDefaultRoyalty(_owner_, _defaultRoyalty_);
    }

    /**
     * @dev Function to get all tokens owned by a specific address.
     * @param userAddr The address of the user.
     * @return tokens An array of token IDs owned by the address.
     */
    function userTokens(address userAddr) public view returns (uint256[] memory tokens) {
        uint256 len = balanceOf(userAddr);
        tokens = new uint256[](len);

        for (uint256 index; index < len; index++) {
            tokens[index] = tokenOfOwnerByIndex(userAddr, index);
        }
    }

    /**
     * @dev Internal function to override the base URI.
     * @return The base URI for token metadata.
     */
    function _baseURI() internal view override returns (string memory) {
        return _baseURI_;
    }

    /**
     * @dev Function to change the base URI for token metadata.
     * @param _uri_ The new base URI.
     */
    function changeBaseURI(string memory _uri_) public onlyMinter {
        _baseURI_ = _uri_;
    }

    /**
     * @dev Function to set the default royalty for a receiver address.
     * @param receiver The address of the receiver.
     * @param feeNumerator The royalty fee numerator.
     */
    function setDefaultRoyalty(address receiver, uint96 feeNumerator) public onlyMinter {
        _setDefaultRoyalty(receiver, feeNumerator);
    }

    /**
     * @dev Function to delete the default royalty for the contract owner.
     */
    function deleteDefaultRoyalty() public onlyMinter {
        _deleteDefaultRoyalty();
    }

    /**
     * @dev Function to set the royalty for a specific token.
     * @param tokenId The ID of the token.
     * @param receiver The address of the royalty receiver.
     * @param feeNumerator The royalty fee numerator.
     */
    function setTokenRoyalty(
        uint256 tokenId,
        address receiver,
        uint96 feeNumerator
    ) public onlyMinter {
        _setTokenRoyalty(tokenId, receiver, feeNumerator);
    }

    /**
     * @dev Function to reset the royalty for a specific token.
     * @param tokenId The ID of the token.
     */
    function resetTokenRoyalty(uint256 tokenId) public onlyMinter {
        _resetTokenRoyalty(tokenId);
    }

    /**
     * @dev Function to safely mint a new token.
     * @param to The address to which the token will be minted.
     * @param tokenId The ID of the new token.
     */
    function safeMint(address to, uint256 tokenId) public onlyMinter {
        _safeMint(to, tokenId);
    }

    /**
     * @dev Function to toggle the singleNFT flag.
     */
    function changeSingleNFT() public onlyMinter {
        if (singleNFT) {
            singleNFT = false;
        } else {
            singleNFT = true;
        }
    }

    /**
     * @dev Function to change the minter address.
     * @param newMinter The new minter address.
     */
    function changeMinter(address newMinter) public onlyOwner {
        minter = newMinter;
    }

    /**
     * @dev Internal function to be called before a token transfer.
     * @param from The address from which the token is transferred.
     * @param to The address to which the token is transferred.
     * @param tokenId The ID of the token.
     * @param batchSize The number of tokens being transferred in a batch.
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId,
        uint256 batchSize
    ) internal override(ERC721, ERC721Enumerable) {
        if (singleNFT) {
            require(balanceOf(to) == 0, "Each address can only own one AUT");
        }
        super._beforeTokenTransfer(from, to, tokenId, batchSize);
    }

    /**
     * @dev Function to check if a contract supports a given interface.
     * @param interfaceId The interface ID.
     * @return A boolean indicating whether the interface is supported.
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        override(ERC721, ERC721Enumerable, ERC2981)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}
"
    },
    "contracts/Minter.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "./utils/Administration.sol";

/**
 * @title AutuniteMinter
 * @dev Contract for minting and payment functionality of Autunite NFTs.
 */
contract AutuniteMinter is Administration {
    using CountSplitter for uint256;

    mapping(uint256 => TokenPayment[]) _tokenPayments;
    mapping(uint256 => TokenPayment[]) _tokenPayOuts;

    struct TokenPayment {
        uint256 timestamp;
        uint256 value;
    }

    event Pay(address indexed from, uint256 indexed tokenId, uint256 indexed amount);

    /**
     * @dev Modifier to restrict a function's access to the mainWallet address.
     */
    modifier onlyMainWallet {
        require(msg.sender == mainWallet, "Minter: Only the mainWallet can call this function");
        _;
    }

    /**
     * @dev Constructor function for the AutuniteMinter contract.
     * @param _baseURI_ The base URI for token metadata.
     * @param _name_ The name of the token.
     * @param _symbol_ The symbol of the token.
     * @param _minterOwner_ The owner of the minter contract.
     * @param _nftOwner_ The owner of the NFT contract.
     * @param _mainWallet_ The address of the main wallet.
     * @param _BUSD_ The address of the BUSD token contract.
     * @param _minValue_ The minimum value required for minting.
     * @param _defaultRoyalty_ The default royalty fee for the NFT contract owner.
     * @param _singleNFT_ Flag indicating whether only one NFT is allowed per address.
     */
    constructor(
        string memory _baseURI_,
        string memory _name_,
        string memory _symbol_,
        address _minterOwner_,
        address _nftOwner_,
        address _mainWallet_,
        address _BUSD_,
        address _aggregator_,
        uint256 _minValue_,
        uint96 _defaultRoyalty_,
        bool _singleNFT_
    ) Ownable(_minterOwner_) PriceFeed(_aggregator_) {
        AUT = new AutuniteNFT(_baseURI_, _name_, _symbol_, _nftOwner_, _defaultRoyalty_, _singleNFT_);
        BUSD = IERC20(_BUSD_);
        minValue_ = _minValue_;
        minPayUp_ = _minValue_;
        mainWallet = _mainWallet_;
    }

    /**
     * @dev Function to get the details of a token's updates.
     * @param tokenId The ID of the token.
     * @return An array of TokenPayment structs representing the token's updates.
     */
    function tokenPayments(uint256 tokenId) public view returns (TokenPayment[] memory) {
        return _tokenPayments[tokenId];
    }

    /**
     * @dev Function to get the details of a token's updates.
     * @param tokenId The ID of the token.
     * @return An array of TokenPayOut structs representing the token's updates.
     */
    function tokenPayOuts(uint256 tokenId) public view returns (TokenPayment[] memory) {
        return _tokenPayOuts[tokenId];
    }

    /**
     * @dev Function to get the initial value of a token.
     * @param tokenId The ID of the token.
     * @return A string representing the initial value of the token.
     */
    function tokenInitialValue(uint256 tokenId) public view returns (string memory) {
        uint256 value;
        for (uint256 i; i < _tokenPayments[tokenId].length; i++) {
            value += _tokenPayments[tokenId][i].value;
        }
        return string.concat((value / 10**18).split(3), " $");
    }

    /**
     * @dev Retrieves the total payout value in USD for a specific token ID.
     * @param tokenId The ID of the token to retrieve the total payout value for.
     * @return payoutValue The total payout value in USD for the specified token ID.
     */
    function tokenTotalPayOut(uint256 tokenId) public view returns (string memory) {
        uint256 value;
        for (uint256 i; i < _tokenPayOuts[tokenId].length; i++) {
            value += _tokenPayOuts[tokenId][i].value;
        }
        return string.concat((value / 10**18).split(3), " $");
    }

    /**
     * @dev Function to mint a new token and pay the specified amount.
     * @param tokenId The ID of the new token to be minted.
     * @param amountBUSD The payment amount in BUSD tokens (if paying in BUSD).
     */
    function MintAndPay(uint256 tokenId, uint256 amountBUSD) public payable {
        address userAddr = msg.sender;
        uint256 amountBNB = msg.value;
        uint256 amount;

        if (amountBNB != 0) {
            require(amountBUSD == 0, "Minter: One of BUSD or BNB amount should be 0");
            amount = amountBNB * BNB_USD() / 10 ** 8;
            require(amount >= minValue_, "Minter: Insufficient value");
            payable(mainWallet).transfer(amountBNB);
        } else {
            amount = amountBUSD;
            require(amount >= minValue_, "Minter: Insufficient value");
            BUSD.transferFrom(userAddr, mainWallet, amount);
        }

        totalEntered += amount;
        AUT.safeMint(userAddr, tokenId);
        _tokenPayments[tokenId].push(TokenPayment(block.timestamp, amount));
        emit Pay(userAddr, tokenId, amount);
    }

    /**
     * @dev Function to pay the specified amount for an existing token.
     * @param tokenId The ID of the token for which the payment is made.
     * @param amountBUSD The payment amount in BUSD tokens (if paying in BUSD).
     */
    function PayUp(uint256 tokenId, uint256 amountBUSD) public payable {
        address userAddr = msg.sender;
        require(userAddr == AUT.ownerOf(tokenId), "Minter: Only the owner of the token can pay up");
        uint256 amountBNB = msg.value;
        uint256 amount;

        if (amountBNB != 0) {
            require(amountBUSD == 0, "Minter: One of BUSD or BNB amount should be 0");
            amount = amountBNB * BNB_USD() / 10 ** 8;
            require(amount >= minValue_, "Minter: Insufficient value");
            payable(mainWallet).transfer(amountBNB);
        } else {
            amount = amountBUSD;
            require(amount >= minValue_, "Minter: Insufficient value");
            BUSD.transferFrom(userAddr, mainWallet, amount);
        }

        totalEntered += amount;
        _tokenPayments[tokenId].push(TokenPayment(block.timestamp, amount));
        emit Pay(userAddr, tokenId, amount);
    }

    /**
     * @dev Function to mint a new token on behalf of the contract owner.
     * @param userAddr The address for which the token will be minted.
     * @param tokenId The ID of the new token.
     */
    function ownerMint(address userAddr, uint256 tokenId) public onlyOwner {
        AUT.safeMint(userAddr, tokenId);
        _tokenPayments[tokenId].push(TokenPayment(block.timestamp, 0));
    }

    /**
     * @dev Distributes specified amounts of tokens to the owners of given token IDs.
     * @param tokenId An array of token IDs to distribute.
     * @param amount An array of corresponding amounts to distribute for each token ID.
     * Requirements:
     *  - The `tokenId` and `amount` arrays must have the same length.
     *  - The function can only be called by the main wallet.
     */
    function distribute(
        uint256[] calldata tokenId,
        uint256[] calldata amount
    ) public onlyMainWallet {
        require(tokenId.length == amount.length, "Minter : Lists must be same in length");
        for(uint256 i; i < amount.length; i++) {
            totalPaidOut += amount[i];
            BUSD.transferFrom(mainWallet, AUT.ownerOf(tokenId[i]), amount[i]);
            _tokenPayOuts[tokenId[i]].push(TokenPayment(block.timestamp, amount[i]));
        }
    }
}
"
    },
    "contracts/utils/Administration.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./Ownable.sol";
import "../AutuniteNFT.sol";
import "./CountSplitter.sol";
import "./PriceFeed.sol";

/**
 * @title Administration
 * @dev Abstract contract for managing administrative functions and data of Autunite NFTs.
 */
abstract contract Administration is Ownable, PriceFeed {
    using CountSplitter for uint256;

    IERC20 public BUSD;
    AutuniteNFT public AUT;
    address public mainWallet;
    uint256 totalEntered;
    uint256 totalPaidOut;
    uint256 minValue_;
    uint256 minPayUp_;

    /**
     * @dev Function to get the total amount entered in BUSD tokens.
     * @return A string representing the total amount entered in BUSD tokens.
     */
    function TotalPaidIn() public view returns (string memory) {
        return string.concat((totalEntered / 10**18).split(3), " $");
    }

    /**
     * @dev Function to get the total amount paid out in BUSD tokens.
     * @return A string representing the total amount paid out in BUSD tokens.
     */
    function TotalPaidOut() public view returns (string memory) {
        return string.concat((totalPaidOut / 10**18).split(3), " $");
    }

    /**
     * @dev Function to get the minimum value required for minting in BUSD tokens.
     * @return A string representing the minimum value required for minting in BUSD tokens.
     */
    function minValue() public view returns (string memory) {
        return string.concat((minValue_ / 10**18).split(3), " $");
    }

    /**
     * @dev Function to get the minimum value required for paying up in BUSD tokens.
     * @return A string representing the minimum value required for paying up in BUSD tokens.
     */
    function minPayUp() public view returns (string memory) {
        return string.concat((minPayUp_ / 10**18).split(3), " $");
    }

    /**
     * @dev Function to change the base URI of the NFTs.
     * @param baseURI The new base URI.
     */
    function changeBaseURI(string memory baseURI) public onlyOwner {
        AUT.changeBaseURI(baseURI);
    }

    /**
     * @dev Function to change the singleNFT flag of the NFTs.
     */
    function changeSingleNFT() public onlyOwner {
        AUT.changeSingleNFT();
    }

    /**
     * @dev Function to set the default royalty for the NFT contract owner.
     * @param receiver The address of the royalty receiver.
     * @param feeNumerator The royalty fee numerator.
     */
    function setDefaultRoyalty(address receiver, uint96 feeNumerator) public onlyOwner {
        AUT.setDefaultRoyalty(receiver, feeNumerator);
    }

    /**
     * @dev Function to delete the default royalty for the NFT contract owner.
     */
    function deleteDefaultRoyalty() public onlyOwner {
        AUT.deleteDefaultRoyalty();
    }

    /**
     * @dev Function to set the royalty for a specific token.
     * @param tokenId The ID of the token.
     * @param receiver The address of the royalty receiver.
     * @param feeNumerator The royalty fee numerator.
     */
    function setTokenRoyalty(uint256 tokenId, address receiver, uint96 feeNumerator) public onlyOwner {
        AUT.setTokenRoyalty(tokenId, receiver, feeNumerator);
    }

    /**
     * @dev Function to reset the royalty for a specific token.
     * @param tokenId The ID of the token.
     */
    function resetTokenRoyalty(uint256 tokenId) public onlyOwner {
        AUT.resetTokenRoyalty(tokenId);
    }

    /**
     * @dev Function to change the minimum value required for minting.
     * @param _minValue The new minimum value required for minting.
     */
    function changeMinValue(uint256 _minValue) public onlyOwner {
        minValue_ = _minValue;
    }

    /**
     * @dev Function to change the minimum value required for paying up.
     * @param _minPayUp The new minimum value required for paying up.
     */
    function changeMinPayUp(uint256 _minPayUp) public onlyOwner {
        minPayUp_ = _minPayUp;
    }

    /**
     * @dev Function to change the main wallet address.
     * @param _mainWallet The new main wallet address.
     */
    function changeMainWallet(address _mainWallet) public onlyOwner {
        mainWallet = _mainWallet;
    }
}
"
